vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

// Scene definition using SDFs
float sceneSDF(vec3 p) {
    // Animated sphere
    float sphereRadius = 1.0;
    vec3 spherePos = vec3(sin(u_time) * 2.0, cos(u_time) * 0.5, 0.0);
    float sphere = length(p - spherePos) - sphereRadius;

    // Ground plane
    float ground = p.y + 1.5;

    // Combine objects
    return min(sphere, ground);
}

// Ray marching function
float raymarch(vec3 ro, vec3 rd, float maxDist) {
    float t = 0.0;

    for(int i = 0; i < 64; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);

        // If we're very close to the surface or too far, stop
        if(d < 0.001 || t > maxDist) break;

        // Move along the ray by the distance to the closest object
        t += d;
    }

    return t;
}

// Calculate normal by sampling nearby points
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution;

    // Center and adjust for aspect ratio
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Camera setup
    vec3 ro = vec3(0.0, 0.0, -5.0); // Ray origin (camera position)
    vec3 rd = normalize(vec3(uv, 1.0)); // Ray direction

    // Light position
    vec3 lightPos = vec3(5.0, 5.0, -5.0);

    // Perform ray marching
    float t = raymarch(ro, rd, 20.0);

    // Default sky color
    vec3 color = vec3(0.5, 0.7, 1.0) - rd.y * 0.2;

    // If we hit something
    if(t < 20.0) {
        // Calculate the position where the ray hit
        vec3 p = ro + rd * t;

        // Get the normal at the surface
        vec3 normal = getNormal(p);

        // Calculate lighting
        vec3 lightDir = normalize(lightPos - p);
        float diff = max(dot(normal, lightDir), 0.0); // Diffuse lighting

        // Add ambient, diffuse, and specular lighting
        vec3 ambient = vec3(0.1);
        vec3 diffuse = vec3(diff);

        // Simple material color based on normal
        vec3 material = vec3(normal * 0.5 + 0.5);

        // Ground material vs sphere material
        if(p.y < -1.49) {
            // Checkerboard pattern for ground
            float check = mod(floor(p.x) + floor(p.z), 2.0);
            material = mix(vec3(0.2), vec3(0.8), check);
        }

        // Combine lighting and material
        color = material * (ambient + diffuse);

        // Add fog based on distance
        color = mix(color, vec3(0.5, 0.7, 1.0), 1.0 - exp(-0.05 * t));
    }

    // Output the final color
    gl_FragColor = vec4(color, 1.0);
}

vert:
attribute vec4 aVertexPosition;
void main() {
    gl_Position = aVertexPosition;
}

frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

// Constants
#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265359

// Rotation matrix around the Y axis
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Rotation matrix around the X axis
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Signed Distance Functions
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

float sdOctahedron(vec3 p, float s) {
    p = abs(p);
    return (p.x + p.y + p.z - s) * 0.57735027;
}

// Operation: Smooth union
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Operation: Repetition
vec3 opRep(vec3 p, vec3 c) {
    return mod(p + 0.5 * c, c) - 0.5 * c;
}

// Scene definition using SDFs
float sceneSDF(vec3 p) {
    // Animated central sphere
    float time = u_time * 0.5;
    vec3 spherePos = vec3(sin(time) * 2.0, cos(time * 0.7) * 0.5 + 0.5, 0.0);
    float mainSphere = sdSphere(p - spherePos, 1.0);

    // Animated torus
    vec3 torusPos = vec3(-2.0, sin(time * 0.5) * 0.5, 0.0);
    vec3 torusP = p - torusPos;
    torusP = rotateX(time) * rotateY(time * 0.7) * torusP;
    float torus = sdTorus(torusP, vec2(1.0, 0.3));

    // Animated octahedron
    vec3 octaPos = vec3(2.0, cos(time * 0.6) * 0.5, 0.0);
    vec3 octaP = p - octaPos;
    octaP = rotateY(time * 1.1) * octaP;
    float octahedron = sdOctahedron(octaP, 0.8);

    // Small orbiting spheres
    float orbitRadius = 2.5;
    float orbitHeight = 0.0;
    vec3 orbitCenter = vec3(0.0, orbitHeight, 0.0);

    // Orbiter 1
    vec3 orbiterPos1 = orbitCenter + vec3(cos(time * 2.0) * orbitRadius, sin(time) * 0.5, sin(time * 2.0) * orbitRadius);
    float orbiter1 = sdSphere(p - orbiterPos1, 0.3);

    // Orbiter 2
    vec3 orbiterPos2 = orbitCenter + vec3(cos(time * 2.0 + PI) * orbitRadius, sin(time + PI) * 0.5, sin(time * 2.0 + PI) * orbitRadius);
    float orbiter2 = sdSphere(p - orbiterPos2, 0.3);

    // Grid of small boxes
    vec3 gridPos = opRep(p + vec3(0.0, 1.5, 0.0), vec3(2.0, 0.0, 2.0));
    float boxSize = 0.1 + 0.05 * sin(length(gridPos.xz) * 3.0 - time * 2.0);
    float boxes = sdBox(gridPos, vec3(boxSize));

    // Ground plane with wave
    float ground = p.y + 1.5 + 0.1 * sin(p.x * 1.5 + time) * sin(p.z * 1.5 + time);

    // Combine objects with smooth min for some objects
    float scene = min(smin(mainSphere, torus, 0.5), octahedron);
    scene = min(scene, orbiter1);
    scene = min(scene, orbiter2);
    scene = min(scene, boxes);
    scene = min(scene, ground);

    return scene;
}

// Ray marching function
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);

        // If we're very close to the surface or too far, stop
        if(d < SURF_DIST || t > MAX_DIST) break;

        // Move along the ray by the distance to the closest object
        t += d;
    }

    return t;
}

// Calculate normal by sampling nearby points
vec3 getNormal(vec3 p) {
    float e = 0.001;
    vec3 n = vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    );
    return normalize(n);
}

// Calculate soft shadows
float getShadow(vec3 p, vec3 lightDir, float maxDist, float k) {
    float result = 1.0;
    float t = SURF_DIST * 2.0;

    for(int i = 0; i < 32; i++) {
        if(t >= maxDist) break;

        float h = sceneSDF(p + lightDir * t);
        if(h < SURF_DIST) return 0.0;

        result = min(result, k * h / t);
        t += h;
    }

    return result;
}

// Calculate ambient occlusion
float getAO(vec3 p, vec3 n) {
    float occ = 0.0;
    float weight = 1.0;

    for(int i = 0; i < 5; i++) {
        float len = 0.01 + 0.02 * float(i * i);
        float dist = sceneSDF(p + n * len);
        occ += (len - dist) * weight;
        weight *= 0.85;
    }

    return clamp(1.0 - occ * 1.0, 0.0, 1.0);
}

// Materials
vec3 getMaterial(vec3 p, vec3 normal) {
    // Base material is determined by position and normal
    vec3 material;

    // Ground material - checkerboard with ripple
    if(p.y < -1.4) {
        float ripple = 0.05 * sin(length(p.xz) * 2.0 - u_time);
        float check = mod(floor(p.x * 0.5) + floor(p.z * 0.5), 2.0);
        vec3 color1 = vec3(0.1, 0.15, 0.4); // Dark blue
        vec3 color2 = vec3(0.2, 0.3, 0.6); // Blue
        material = mix(color1, color2, check) + ripple;
        return material;
    }

    // Central sphere - colorful gradient
    if(length(p - vec3(sin(u_time * 0.5) * 2.0, cos(u_time * 0.7 * 0.5) * 0.5 + 0.5, 0.0)) < 1.1) {
        material = vec3(0.8, 0.1, 0.2) + 0.2 * sin(p.y * 5.0 + u_time); // Red with pattern
        return material;
    }

    // Torus - golden
    if(length(p - vec3(-2.0, sin(u_time * 0.5 * 0.5) * 0.5, 0.0)) < 1.5) {
        material = vec3(0.8, 0.7, 0.1); // Gold
        return material;
    }

    // Octahedron - emerald
    if(length(p - vec3(2.0, cos(u_time * 0.6 * 0.5) * 0.5, 0.0)) < 1.2) {
        material = vec3(0.1, 0.8, 0.2); // Green
        return material;
    }

    // Small orbiters - blue
    material = vec3(0.1, 0.3, 0.9); // Blue

    // Grid boxes - neon glow
    if(mod(floor(p.x + 1.5) + floor(p.z + 1.5), 2.0) < 0.1) {
        material = vec3(0.9, 0.3, 0.9); // Pink
    }

    return material;
}

// Main function
void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution.xy;

    // Center and adjust for aspect ratio
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Camera setup with slight movement
    float camTime = u_time * 0.3;
    vec3 ro = vec3(0.0, 1.0 + sin(camTime * 0.5) * 0.5, -6.0); // Ray origin (camera position)
    vec3 lookAt = vec3(0.0, 0.0, 0.0);

    // Camera frame
    vec3 ww = normalize(lookAt - ro);
    vec3 uu = normalize(cross(ww, vec3(0.0, 1.0, 0.0)));
    vec3 vv = normalize(cross(uu, ww));

    // Ray direction with a slight lens effect
    float fov = 1.0;
    vec3 rd = normalize(uv.x * uu + uv.y * vv + fov * ww);

    // Light positions
    vec3 mainLight = normalize(vec3(0.8, 0.8, 0.4));
    vec3 fillLight = normalize(vec3(-0.5, 0.3, -0.5));

    // Perform ray marching
    float t = raymarch(ro, rd);

    // Default sky color with gradient
    vec3 skyColor = mix(
        vec3(0.1, 0.12, 0.18), // Dark blue at the top
        vec3(0.3, 0.35, 0.5),  // Lighter blue at the horizon
        pow(1.0 - max(rd.y, 0.0), 2.0)
    );

    // Add some subtle stars to the sky
    if(rd.y > 0.0) {
        vec3 starPos = normalize(rd) * 100.0;
        float stars = smoothstep(0.95, 1.0, sin(starPos.x * 100.0) * sin(starPos.y * 100.0) * sin(starPos.z * 100.0));
        skyColor += stars * 0.2;
    }

    // Base color is sky
    vec3 color = skyColor;

    // If we hit something
    if(t < MAX_DIST) {
        // Calculate the position where the ray hit
        vec3 p = ro + rd * t;

        // Get the normal at the surface
        vec3 normal = getNormal(p);

        // Material properties
        vec3 material = getMaterial(p, normal);

        // Lighting calculation
        vec3 lightDir = mainLight;
        float diff = max(dot(normal, lightDir), 0.0);

        // Specular highlights
        vec3 reflectDir = reflect(-lightDir, normal);
        float spec = pow(max(dot(rd, -reflectDir), 0.0), 32.0);
        vec3 specColor = vec3(1.0) * spec * 0.5;

        // Fill light contribution
        float fillDiff = max(dot(normal, fillLight), 0.0) * 0.3;

        // Shadow calculation
        float shadow = getShadow(p, lightDir, 10.0, 8.0);

        // Ambient occlusion
        float ao = getAO(p, normal);

        // Fresnel effect for rim lighting
        float fresnel = pow(1.0 - abs(dot(normal, -rd)), 3.0) * 0.5;

        // Reflections (simplified approximation)
        vec3 reflectRay = reflect(rd, normal);
        vec3 reflection = mix(
            skyColor,
            vec3(0.1, 0.12, 0.15), // Dark reflection color
            pow(1.0 - max(reflectRay.y, 0.0), 2.0)
        );

        // Combine all lighting components
        vec3 ambient = vec3(0.03, 0.04, 0.05) * ao;
        vec3 diffuse = vec3(1.0, 0.97, 0.85) * diff * shadow;
        vec3 fill = vec3(0.15, 0.15, 0.25) * fillDiff;

        // Final surface color
        color = material * (ambient + diffuse + fill) + specColor + reflection * fresnel;

        // Add subtle glow to some objects
        float glow = 0.0;

        // Glow for orbiters
        float orbitRadius = 2.5;
        vec3 orbitCenter = vec3(0.0, 0.0, 0.0);
        float time = u_time * 0.5;

        vec3 orbiterPos1 = orbitCenter + vec3(cos(time * 2.0) * orbitRadius, sin(time) * 0.5, sin(time * 2.0) * orbitRadius);
        float orbiterDist1 = length(p - orbiterPos1);

        vec3 orbiterPos2 = orbitCenter + vec3(cos(time * 2.0 + PI) * orbitRadius, sin(time + PI) * 0.5, sin(time * 2.0 + PI) * orbitRadius);
        float orbiterDist2 = length(p - orbiterPos2);

        if(orbiterDist1 < 0.5 || orbiterDist2 < 0.5) {
            glow += 0.2;
        }

        color += vec3(0.1, 0.3, 0.9) * glow;

        // Add fog based on distance
        color = mix(color, skyColor, 1.0 - exp(-0.02 * t * t));
    }

    // Tone mapping
    color = color / (color + vec3(1.0));

    // Subtle vignette effect
    vec2 q = uv * 0.5 + 0.5;
    color *= 0.7 + 0.3 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.2);

    // Gamma correction
    color = pow(color, vec3(0.4545));

    // Output the final color
    gl_FragColor = vec4(color, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision mediump float;
uniform vec2  u_resolution;
uniform float u_time;

// === SDF Primitives ===
float sdPlane(vec3 p) {
    return p.y + 1.0;
}
float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}
float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h*h*h*k*(1.0/6.0);
}

// Single‐output scene SDF
float sceneSDF(vec3 p) {
    // infinite tunnel
    p.z = mod(p.z + 4.0, 8.0) - 4.0;
    float pulse = 0.3 + 0.1 * sin(u_time * 3.0);
    float tor = sdTorus(p - vec3(0.0, 0.0, -2.0), vec2(1.0, pulse));
    float pl  = sdPlane(p);
    return smin(tor, pl, 0.4);
}

// Estimate normal via central differences
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// Ray‐marcher
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < 90; i++){
        float d = sceneSDF(ro + rd * t);
        if(d < 0.001 || t > 60.0) break;
        t += d;
    }
    return t;
}

// HSV → RGB for color cycling
vec3 hsv(float h, float s, float v){
    vec3 c = abs(fract(vec3(h)+vec3(0.0,2.0/3.0,1.0/3.0))*6.0 - 3.0) -1.0;
    return v * mix(vec3(1.0), clamp(c,0.0,1.0), s);
}

// Volumetric light shafts
float godRays(vec2 uv){
    float density = 0.3;
    float decay   = 0.95;
    float weight  = 0.4;
    vec2 lp = vec2(0.0);
    vec2 delta = (lp - uv) * density / 30.0;
    float illum = 0.0;
    vec2 pos2 = uv;
    for(int i=0; i<30; i++){
        pos2 += delta;
        illum += weight * exp(-0.5 * length(pos2));
        weight *= decay;
    }
    return illum;
}

// Checkerboard floor
vec3 checkerColor(vec3 p){
    float c = mod(floor(p.x*2.0) + floor(p.z*2.0), 2.0);
    return mix(vec3(0.05), vec3(0.0,0.8,0.9), c);
}

void main(){
    // NDC coords
    vec2 uv = (gl_FragCoord.xy / u_resolution)*2.0 - 1.0;
    uv.x *= u_resolution.x/u_resolution.y;

    // Camera
    float ang = u_time * 0.15;
    vec3 ro = vec3(sin(ang)*5.0, 1.2 + 0.3*sin(u_time*1.5), cos(ang)*5.0);
    vec3 fwd = normalize(vec3(0.0) - ro);
    vec3 right = normalize(cross(vec3(0,1,0), fwd));
    vec3 up = cross(fwd, right);
    vec3 rd = normalize(uv.x*right + uv.y*up + 1.7*fwd);

    // Ray‐march and get hit distance
    float t = raymarch(ro, rd);

    // Precompute position for shading and grid
    vec3 pos = ro + rd * t;

    // Base background glow
    vec3 col = vec3(0.0);
    col += 0.2 * exp(-length(uv)*4.0);

    if(t < 60.0){
        // Surface normal
        vec3 nor = getNormal(pos);

        // Determine which primitive we hit
        float planeD = sdPlane(pos);
        float pulse  = 0.3 + 0.1 * sin(u_time * 3.0);
        float torusD = sdTorus(pos - vec3(0.0,0.0,-2.0), vec2(1.0, pulse));
        bool hitTorus = (torusD < planeD);

        // Dynamic base color
        vec3 base = hitTorus
            ? hsv(mod(u_time*0.1,1.0), 0.8, 1.0)
            : checkerColor(pos);

        // Diffuse lighting
        vec3 L = normalize(vec3(5.0,5.0,-3.0) - pos);
        float diff = max(dot(nor, L), 0.0);

        // Bloom‐style glow
        float glow = pow(max(dot(nor, -rd), 0.0), 6.0);
        base += glow * 1.2;

        col += base * (0.2 + diff);

        // Fog
        float fog = 1.0 - exp(-0.05 * t);
        col = mix(col, vec3(0.0,0.0,0.02), fog);

        // Animated grid overlay on tunnel walls
        vec2 gridUV = pos.xz * 0.5 + vec2(u_time*0.3);
        float grid = abs(sin(gridUV.x*10.0)) * abs(sin(gridUV.y*10.0));
        col += vec3(0.2) * pow(grid, 5.0);
    }

    // Add volumetric shafts
    col += 0.5 * godRays(uv * 0.8);

    // Tone‐map & gamma
    col = col / (col + vec3(1.0));
    col = pow(col, vec3(4.9));

    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

// SDF primitives
float sphereSDF(vec3 p, vec3 pos, float r) {
    return length(p - pos) - r;
}
float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}
float planeSDF(vec3 p, vec4 n) { // n = (normal.xyz, height)
    return dot(p, n.xyz) + n.w;
}

// Combine with smooth union
float opSmoothUnion( float d1, float d2, float k ){
    float h = clamp(0.5 + 0.5*(d2-d1)/k, 0.0, 1.0);
    return mix(d2, d1, h) - k*h*(1.0-h);
}

float sceneSDF(vec3 p) {
    // Animated sphere
    vec3 sp = vec3(sin(u_time)*2.0, 1.0 + sin(u_time*0.5)*0.5, cos(u_time)*2.0);
    float d_sphere = sphereSDF(p, sp, 1.0);

    // Pulsing box
    float boxSize = 0.5 + 0.3*sin(u_time*1.3);
    float d_box = boxSDF(p - vec3(-2.0, 0.5, 0.), vec3(boxSize));

    // Ground
    float d_plane = planeSDF(p, vec4(0.0,1.0,0.0,1.5));

    // Smoothly merge sphere and box
    float d1 = opSmoothUnion(d_sphere, d_box, 0.5);
    return min(d1, d_plane);
}

vec3 getNormal(vec3 p) {
    float e = 0.0005;
    return normalize(vec3(
        sceneSDF(p+vec3(e,0,0)) - sceneSDF(p-vec3(e,0,0)),
        sceneSDF(p+vec3(0,e,0)) - sceneSDF(p-vec3(0,e,0)),
        sceneSDF(p+vec3(0,0,e)) - sceneSDF(p-vec3(0,0,e))
    ));
}

// Soft shadow
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for(int i=0; i<32; i++) {
        float h = sceneSDF(ro + rd*t);
        res = min(res, 10.0*h/t);
        t += clamp(h, 0.02, 0.1);
        if(res < 0.001) break;
    }
    return clamp(res, 0.0, 1.0);
}

float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<100; i++) {
        float d = sceneSDF(ro + rd*t);
        if(d < 0.001) break;
        t += d;
        if(t > 100.0) break;
    }
    return t;
}

void main() {
    // uv coords
    vec2 uv = (gl_FragCoord.xy*2.0 - u_resolution) / u_resolution.y;

    // camera orbit
    float ca = u_time * 0.2;
    vec3 ro = vec3(5.0*sin(ca), 2.5, 5.0*cos(ca));
    vec3 lookAt = vec3(0.0, 0.5, 0.0);
    vec3 cw = normalize(lookAt - ro);
    vec3 cu = normalize(cross(vec3(0.0,1.0,0.0), cw));
    vec3 cv = cross(cw, cu);
    vec3 rd = normalize(uv.x*cu + uv.y*cv + 1.5*cw);

    // march
    float t = raymarch(ro, rd);
    vec3 col = vec3(0.5,0.7,1.0) - rd.y*0.2; // sky

    if(t < 100.0) {
        vec3 p = ro + rd*t;
        vec3 n = getNormal(p);

        // lighting
        vec3 lightPos = vec3(5.0, 8.0, -3.0);
        vec3 L = normalize(lightPos - p);
        float diff = max(dot(n, L), 0.0);
        float sh = softShadow(p + n*0.001, L);
        float spec = pow(max(dot(reflect(-L, n), -rd), 0.0), 32.0);

        // material color via normal
        vec3 base = n*0.5 + 0.5;
        // pulse hue
        float hue = mod(u_time*0.05 + length(p.xz)*0.1, 1.0);
        base = mix(base, vec3(hue,1.0-hue,0.5+0.5*sin(u_time)), 0.3);

        vec3 light = (0.1 + diff*sh) * base + spec*vec3(1.0);
        col = mix(vec3(0.5,0.7,1.0), light, exp(-0.02*t));
    }

    gl_FragColor = vec4(col,1.0);
}

#ifdef GL_ES
precision highp float;
#endif

uniform float u_time;
uniform vec2 u_resolution;

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

// Signed distance functions
float sphere(vec3 p, vec3 center, float r) {
    return length(p - center) - r;
}

float floorSDF(vec3 p) {
    return p.y + 1.0;
}

// Combine scene objects
float mapScene(vec3 p) {
    float d1 = sphere(p, vec3(0.0, sin(u_time)*0.5, -3.0), 1.0);
    float d2 = sphere(p, vec3(2.0, cos(u_time*0.7)*0.5, -4.5), 0.75);
    float d3 = floorSDF(p);
    return min(min(d1, d2), d3);
}

// Estimate normal at point p
vec3 getNormal(vec3 p) {
    float d = mapScene(p);
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        mapScene(p + e.xyy) - d,
        mapScene(p + e.yxy) - d,
        mapScene(p + e.yyx) - d
    ));
}

// Raymarching
float rayMarch(vec3 ro, vec3 rd) {
    float dist = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dist;
        float dS = mapScene(p);
        if (dS < SURF_DIST) break;
        dist += dS;
        if (dist > MAX_DIST) break;
    }
    return dist;
}

// Lighting
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for (int i = 0; i < 32; i++) {
        float h = mapScene(ro + rd * t);
        res = min(res, 16.0 * h / t);
        t += clamp(h, 0.01, 0.2);
        if (h < 0.001 || t > 5.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // Camera setup
    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.5));

    float dist = rayMarch(ro, rd);
    vec3 p = ro + rd * dist;

    // Background
    vec3 col = vec3(0.02, 0.02, 0.05) + 0.02 * uv.y;

    if (dist < MAX_DIST) {
        vec3 n = getNormal(p);
        vec3 lightDir = normalize(vec3(1.0, 1.2, 0.5));
        float diff = max(dot(n, lightDir), 0.0);
        float shadow = softShadow(p + n * SURF_DIST * 2.0, lightDir);

        // Reflection
        vec3 r = reflect(rd, n);
        float fresnel = pow(1.0 - max(dot(n, -rd), 0.0), 3.0);

        vec3 baseColor = mix(
            vec3(0.3, 0.6, 0.9),
            vec3(1.0, 0.6, 0.2) + 0.2 * sin(u_time + p * 3.0),
            fresnel
        );

        col = baseColor * diff * shadow;
    }

    gl_FragColor = vec4(col, 1.0);
}

