precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 100
#define MAX_DIST 50.0
#define SURF_DIST 0.001

// Signed Distance Functions
float sphereSDF(vec3 p, vec3 center, float radius) {
    return length(p - center) - radius;
}

float planeSDF(vec3 p, vec3 n, float h) {
    return dot(p, n) + h;
}

float sceneSDF(vec3 p) {
    vec3 spherePos = vec3(sin(u_time) * 2.0, cos(u_time * 0.5) * 0.5, 0.0);
    float sphere = sphereSDF(p, spherePos, 1.0);
    float ground = planeSDF(p, vec3(0.0, 1.0, 0.0), 1.5);
    return min(sphere, ground);
}

// Raymarching
float raymarch(vec3 ro, vec3 rd) {
    float dist = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dist;
        float d = sceneSDF(p);
        if(d < SURF_DIST || dist > MAX_DIST) break;
        dist += d;
    }
    return dist;
}

vec3 getNormal(vec3 p) {
    float h = 0.0005;
    vec2 k = vec2(1.0, -1.0);
    return normalize(
        k.xyy * sceneSDF(p + k.xyy * h) +
        k.yyx * sceneSDF(p + k.yyx * h) +
        k.yxy * sceneSDF(p + k.yxy * h) +
        k.xxx * sceneSDF(p + k.xxx * h)
    );
}

// Soft shadows
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for(int i = 0; i < 32; i++) {
        float h = sceneSDF(ro + rd * t);
        if(h < 0.001) return 0.0;
        res = min(res, 16.0 * h / t);
        t += clamp(h, 0.01, 0.2);
    }
    return clamp(res, 0.0, 1.0);
}

// Ambient Occlusion
float ambientOcclusion(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 1; i <= 5; i++) {
        float h = 0.01 * float(i);
        float d = sceneSDF(p + n * h);
        occ += (h - d) * sca;
        sca *= 0.7;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}

// Main
void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    vec3 ro = vec3(0.0, 0.0, -5.0);
    vec3 rd = normalize(vec3(uv, 1.5));

    float t = raymarch(ro, rd);

    vec3 bg = mix(vec3(0.8, 0.9, 1.0), vec3(0.2, 0.4, 0.8), rd.y * 0.5 + 0.5);

    if(t >= MAX_DIST) {
        gl_FragColor = vec4(bg, 1.0);
        return;
    }

    vec3 p = ro + rd * t;
    vec3 n = getNormal(p);
    vec3 lightPos = vec3(5.0 * sin(u_time), 5.0, 5.0 * cos(u_time));
    vec3 lightDir = normalize(lightPos - p);

    float diff = max(dot(n, lightDir), 0.0);
    float spec = pow(max(dot(reflect(-lightDir, n), -rd), 0.0), 32.0);

    float shadow = softShadow(p + n * 0.01, lightDir);
    float ao = ambientOcclusion(p, n);

    vec3 sphereColor = vec3(0.1, 0.6, 1.0);
    vec3 groundColor = vec3(0.2);

    vec3 baseColor = p.y < -1.49
        ? groundColor
        : sphereColor;

    vec3 color = vec3(0.0);
    color += 0.05 * ao;
    color += baseColor * diff * shadow;
    color += vec3(1.0) * spec * shadow;

    color = mix(color, bg, smoothstep(0.0, MAX_DIST, t * 0.1)); // Fog

    gl_FragColor = vec4(color, 1.0);
}

