precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define PI        3.14159265359
#define MAX_ITER  64

// 2×2 rotation
mat2 rot2(float a){
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

// Superformula radius in 2D
float superR(float phi, float m, float n1, float n2, float n3) {
    float a = 1.0;
    float b = 1.0;
    float t1 = pow(abs(cos(m * phi) / a), n2);
    float t2 = pow(abs(sin(m * phi) / b), n3);
    return pow(t1 + t2, -1.0 / n1);
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;
    float t = u_time * 1.8;                       // ← faster clock
    float slices = 6.0 + sin(u_time * 0.2) * 2.0;
    float m_sf    = 6.0 + sin(t * 1.3) * 2.0;
    float n1_sf   = 0.3 + 0.2 * cos(t * 0.7);
    float n2_sf   = 1.7 + 0.5 * sin(t * 0.9);
    float n3_sf   = 1.7 + 0.4 * cos(t * 1.1);

    // polar coords
    float angle  = atan(uv.y, uv.x);
    float radius = length(uv);

    // kaleidoscope fold
    float k = slices;
    angle = mod(angle, 2.0 * PI / k);
    angle = abs(angle - PI / k);

    // back to Cartesian
    vec2 p = vec2(cos(angle), sin(angle)) * radius;

    // superformula warp
    float R = superR(angle, m_sf, n1_sf, n2_sf, n3_sf);
    p *= R * (3.14 + 0.1 * sin(t * 0.1));  // animate warp amount

    // rotate the pattern
    p = rot2(t * 0.2) * p;

    // Julia fractal iteration
    vec2 z = p * 1.5;
    vec2 c = vec2(0.285 + 0.2 * sin(t * 0.5),
                  0.01  + 0.2 * cos(t * 0.6));
    float iter = 0.0;
    for(int i = 0; i < MAX_ITER; i++){
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (2.0 * z.x * z.y) + c.y;
        z = vec2(x, y);
        if(dot(z, z) > 4.0) break;
        iter += 1.0;
    }
    // smooth iteration count
    float mu = iter - log2(log2(dot(z, z))) + 4.0;

    // color mapping
    vec3 col = 0.5 + 0.5 * cos(
        vec3(0.0, 2.0, 4.0) + 
        mu * 0.15 + 
        vec3(0.0, 2.0, 4.0) + 
        t * 0.6
    );

    // gamma correction
    col = pow(col, vec3(0.4545));
    gl_FragColor = vec4(col, 1.0);
}

