#ifdef GL_ES
precision highp float;
#endif

uniform float uTime;
uniform vec2  uResolution;
uniform vec3  uColor;

// ——————————————————
// Helpers: hash, noise, fbm for organic motion
float hash(vec2 p) {
    return fract(sin(dot(p,vec2(127.1,311.7))) * 43758.5453);
}
float noise(vec2 p) {
    vec2 i = floor(p), f = fract(p);
    float a = hash(i), b = hash(i+vec2(1.0,0.0));
    float c = hash(i+vec2(0.0,1.0)), d = hash(i+vec2(1.0,1.0));
    vec2 u = f*f*(3.0-2.0*f);
    return mix(mix(a,b,u.x), mix(c,d,u.x), u.y);
}
float fbm(vec2 p) {
    float v = 0.0, amp = 0.5;
    mat2 m = mat2(1.6,1.2, -1.2,1.6);
    for(int i=0;i<5;i++){
        v   += amp * noise(p);
        p    = m * p;
        amp *= 0.5;
    }
    return v;
}

// 2D rotate
mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s, s,c);
}

// Soft circle
float circle(vec2 p, float r){
    return smoothstep(r, r-0.02, length(p));
}

// Soft n‑gon (by distance to polygon edges)
float polygon(vec2 p, float r, float sides){
    float ang = atan(p.y, p.x);
    float pi2 = 6.28318530718;
    float a = floor(ang / (pi2/sides)) * (pi2/sides);
    vec2 dir = vec2(cos(a), sin(a));
    float d = dot(p, dir);
    return smoothstep(r, r-0.02, d);
}

// Smooth rainbow palette
vec3 palette(float t){
    return 0.6 + 0.5 * cos(
        6.2831853 * (vec3(0.0,0.33,0.67) + t)
        + vec3(0.0,2.0,4.0)
    );
}

void main(){
    // normalize coords to [-1,1]
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution)
            / min(uResolution.x,uResolution.y);

    // evolving lens‑ripple distortion
    float r = length(uv);
    uv *= 1.0 + 3.2 * sin(10.0*r - uTime*1.0) * exp(-r*0.0);

    // add a bit more warp for depth
    uv += fbm(uv*2.0 + uTime*0.3) * 0.1;

    vec3 col = vec3(0.0);
    float t = uTime * 0.7;

    const int LAYERS = 6;
    for(int L=0; L<LAYERS; L++){
        float l = float(L);
        float depth = pow(1.9, l);

        // layer‑specific warp & rotation
        float n = noise(vec2(l*5.0, t*0.2));
        float angle = t*(0.1 + l*0.05) + n*6.2831;
        vec2 ofs = vec2(cos(angle), sin(angle)) * (0.3 + 0.2*n);

        // grid & jitter
        vec2 suv = (uv + ofs) * depth;
        float spacing = 0.6 - 0.1*sin(t*0.5 + l);
        vec2 id = floor(suv/spacing);
        vec2 gv = mod(suv, spacing) - spacing*0.5;
        vec2 jitter = (vec2(hash(id), hash(id+1.0)) - 0.5) * spacing;
        vec2 p = (gv + jitter) / spacing;

        // pick circle or polygon per cell
        float pick = fract(hash(id + sin(t*0.3))*7.0);
        float shape = mix(
            circle(p, 0.25 + 0.05*sin(t + l)),
            polygon(p, 0.35 + 0.03*cos(t*0.8 + l), 3.0 + mod(l,5.0)),
            step(0.5, pick)
        );

        // color & accumulate
        vec3 lc = palette(fract(dot(id,vec2(1.7,2.3))*0.2 + t*0.15));
        col += lc * shape / depth;
    }

    // radial glow core
    col += exp(-r*3.0) * (0.5 + 0.5*sin(t*2.0)) * 0.6;

    // contrast & saturation boost
    col = pow(col, vec3(0.85));
    col = mix(col, col*col*(3.0-2.0*col), 0.3);

    // vignette
    float vig = smoothstep(1.3, 0.6, r);
    col *= mix(1.0, 0.5, vig);

    // tint and output
    col *= uColor;
    gl_FragColor = vec4(col, 1.0);
}

