frag:
precision highp float;
uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;
uniform float uIntensity;

// Constants
const float TAU = 6.28318530718;
const int MAX_ITERATIONS = 6;
const float GOLDEN_RATIO = 1.61803398875;

// Artistic palette
vec3 palette(float t) {
    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.263, 0.416, 0.557) + vec3(sin(uTime * 0.1) * 0.1);
    return a + b * cos(TAU * (c * t + d));
}

// Alternative palette for variety
vec3 palette2(float t) {
    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.824, 0.157, 0.384) + vec3(cos(uTime * 0.15) * 0.1);
    return a + b * cos(TAU * (c * t + d));
}

// Rotate with matrix
vec2 rotate(vec2 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c) * p;
}

// Signed distance to box
float sdBox(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

// Signed distance to hexagon
float sdHexagon(vec2 p, float r) {
    const vec3 k = vec3(-0.866025404, 0.5, 0.577350269);
    p = abs(p);
    p -= 2.0 * min(dot(k.xy, p), 0.0) * k.xy;
    p -= vec2(clamp(p.x, -k.z * r, k.z * r), r);
    return length(p) * sign(p.y);
}

// Signed distance to triangle
float sdTriangle(vec2 p, float r) {
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if (p.x + k*p.y > 0.0) p = vec2(p.x-k*p.y, -k*p.x-p.y)/2.0;
    p.x -= clamp(p.x, -2.0*r, 0.0);
    return -length(p) * sign(p.y);
}

// Smooth minimum
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(b - a)/k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Better noise function (IQ-style hash)
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

// Domain warping function
vec2 warp(vec2 p) {
    float t = uTime * 0.2;
    float noise1 = sin(p.x * 3.7 + t) * cos(p.y * 2.3 - t) * 0.1;
    float noise2 = sin(p.y * 2.5 - t) * cos(p.x * 3.1 + t) * 0.1;
    return p + vec2(noise1, noise2);
}

// Complex fractal shape
float complexShape(vec2 p, float size, float t) {
    // Start with a box
    float d = sdBox(p, vec2(size));

    // Add a hexagonal influence
    float hex = sdHexagon(rotate(p, t * 0.3), size * 1.2);
    d = smin(d, hex, 0.2);

    // Add triangular elements
    float tri = sdTriangle(rotate(p, t * 0.5), size * 0.8);
    d = smin(d, tri, 0.15);

    return d;
}

// Main shader
void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * uResolution.xy) / min(uResolution.x, uResolution.y);
    float t = uTime * 0.15;
    vec3 finalColor = vec3(0.0);

    // Create a flowing pattern across the entire canvas
    vec2 baseP = uv;

    // Add subtle warping to the entire domain
    baseP = warp(baseP);

    // Apply a large-scale repeating pattern
    vec2 gridP = baseP * 2.0;
    gridP = fract(gridP) - 0.5;

    // Add macro movement to the entire pattern
    vec2 macroOffset = vec2(sin(uTime * 0.17), cos(uTime * 0.13)) * 0.15;

    // Primary fractal pattern
    vec2 p = baseP + macroOffset;
    float scale = 1.0;

    // Accumulate distance-based glow fractals
    float totalGlow = 0.0;
    vec3 accColor = vec3(0.0);

    // Main fractal iterations
    for (int i = 0; i < MAX_ITERATIONS; i++) {
        // Scale up for each iteration
        scale *= 1.1 + 0.2 * sin(t + float(i));

        // Rotate and transform the space
        p = rotate(p * scale, t * 0.1 + float(i) * GOLDEN_RATIO);

        // Calculate distance to complex shape
        float d = complexShape(p, 0.3 + 0.1 * sin(t + float(i)), t);

        // Create glowing effect based on distance
        float edgeWidth = 0.002 / float(i+1);
        float glow = smoothstep(edgeWidth, 0.0, abs(d)) + exp(-8.0 * abs(d));

        // Apply color from palette based on position and time
        vec3 col;
        if (mod(float(i), 2.0) < 1.0) {
            col = palette(d * 0.5 + t * 0.4 + float(i) * 0.8);
        } else {
            col = palette2(d * 0.7 - t * 0.3 + float(i) * 0.5);
        }

        // Weight the glow by iteration depth
        float weight = 1.0 / (1.0 + float(i) * 0.7);
        col *= glow * weight;

        // Accumulate color
        accColor += col;
        totalGlow += glow * weight;
    }

    // Add secondary pattern in the background
    vec2 bgP = baseP * 0.5;
    for (int i = 0; i < 3; i++) {
        bgP = rotate(bgP, t * 0.05);
        float bgScale = 0.2 + 0.1 * float(i);
        float bgPattern = sin(bgP.x / bgScale) * sin(bgP.y / bgScale) * 0.5 + 0.5;
        finalColor += palette(bgPattern + t * 0.1) * 0.05;
    }

    // Add the accumulated fractal colors
    finalColor += accColor;

    // Apply vignette
    float vignette = smoothstep(1.1, 0.5, length(uv));
    finalColor *= vignette;

    // Glow pulse
    float pulse = 1.0 + 0.1 * sin(t * 9.5);
    finalColor *= pulse;

    // Chromatic shift
    finalColor.r *= 1.0 + 0.07 * sin(t * 1.2);
    finalColor.b *= 1.0 + 0.07 * cos(t * 1.5);
    finalColor.g *= 1.0 + 0.07 * sin(t * 0.9);

    // Apply color grading and tone mapping
    finalColor = pow(finalColor, vec3(0.85));

    // Add film grain
    float grain = hash(uv + t) * 0.03;
    finalColor += grain;

    // Final color mix with user color and intensity
    finalColor *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);

    gl_FragColor = vec4(finalColor, 1.0);
}

vert:
// Vertex Shader
attribute vec4 aVertexPosition;
uniform float uTime;

void main() {
    // Simple pass-through vertex shader
    gl_Position = aVertexPosition; // gl_Position is used to output the vertex position
}

frag:
precision highp float;

uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;
uniform float uIntensity;

// Constants
const float TAU = 6.28318530718;

// Artistic palette
vec3 palette(float t) {
    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.263, 0.416, 0.557) + vec3(sin(uTime * 0.1) * 0.1);
    return a + b * cos(TAU * (c * t + d));
}

// Rotate with matrix
vec2 rotate(vec2 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c) * p;
}

// Signed distance to box
float sdBox(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

// Smooth minimum
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(b - a)/k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Better noise function (IQ-style hash)
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

// Main shader
void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * uResolution.xy) / min(uResolution.x, uResolution.y);
    float t = uTime * 0.15;

    vec3 finalColor = vec3(0.0);
    vec2 mouseOffset = vec2(sin(uTime * 0.3), cos(uTime * 0.27)) * 0.15;

    vec2 p = uv + mouseOffset;
    float scale = 1.0;

    // Accumulate distance-based glow fractals
    float totalGlow = 0.0;
    vec3 accColor = vec3(0.0);

    for (int i = 0; i < 4; i++) {
        scale *= 1.8;
        p = rotate(p * scale, t * 0.1 + float(i) * 0.2);

        float d = sdBox(p, vec2(0.3 + 0.1 * sin(t + float(i))));
        float glow = exp(-5.0 * abs(d));

        vec3 col = palette(d * 0.5 + t * 0.4 + float(i) * 0.8);
        col *= glow * (1.0 / (1.0 + float(i)));

        accColor += col;
        totalGlow += glow;
    }

    finalColor += accColor;

    // Vignette
    float vignette = smoothstep(1.0, 0.2, length(uv));
    finalColor *= vignette;

    // Glow pulse
    float pulse = 1.0 + 0.2 * sin(t * 3.5);
    finalColor *= pulse;

    // Chromatic shift
    finalColor.r *= 1.0 + 0.05 * sin(t * 1.2);
    finalColor.b *= 1.0 + 0.05 * cos(t * 1.5);

    // Apply color grading and tone mapping
    finalColor = pow(finalColor, vec3(0.85));

    // Add film grain
    float grain = hash(uv + t) * 0.02;
    finalColor += grain;

    // Final color mix with user color and intensity
    finalColor *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);

    gl_FragColor = vec4(finalColor, 1.0);
}

// Fragment Shader (fs_solar_vibrant.glsl)
precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

const int   MAX_STEPS  = 100;
const float MAX_DIST   = 100.0;
const float SURF_DIST  = 0.001;

vec3 gColor;                        // global hit color
float sceneSDF(vec3 p);             // forward‐declare

// ---- color helpers ----
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1., 2./3., 1./3., 3.);
    vec3 p = abs(fract(c.xxx + K.xyz)*6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}

// random hash for starfield
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898,78.233)))*43758.5453);
}

// soft shadow
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for(int i=0; i<32; i++) {
        float h = sceneSDF(ro + rd * t);
        if(h < SURF_DIST) return 0.0;
        res = min(res, 8.0 * h / t);
        t += clamp(h, 0.01, 0.2);
        if(t > MAX_DIST) break;
    }
    return clamp(res, 0.0, 1.0);
}

// estimate normal
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// ray marcher
float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<MAX_STEPS; i++){
        float d = sceneSDF(ro + rd * t);
        if(d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

// scene + dynamic planet coloring
float sceneSDF(vec3 p) {
    // sun
    float sunR = 1.5;
    float d = length(p) - sunR;
    gColor = vec3(1.0, 0.8, 0.2); // sun is warm yellow

    // helper to build a hue-shifted planet
    #define ADD_PLANET(idx, orbit, baseRadius, baseHue) \
    { \
        float ang = u_time * float(idx) * 0.2 + float(idx); \
        vec3 pos = vec3(cos(ang), 0.0, sin(ang)) * orbit; \
        float di = length(p - pos) - baseRadius; \
        if(di < d) { \
            d = di; \
            /* slowly cycle hue */ \
            float h = mod(baseHue + u_time * 0.05, 1.0); \
            gColor = hsv2rgb(vec3(h, 0.8, 1.0)); \
        } \
    }

    ADD_PLANET(1, 3.5, 0.3, 0.05);  // Mercury‐ish
    ADD_PLANET(2, 5.0, 0.4, 0.12);  // Venus‐ish
    ADD_PLANET(3, 7.0, 0.25, 0.33); // Earth‐ish
    ADD_PLANET(4, 9.0, 0.35, 0.60); // Mars‐ish
    ADD_PLANET(5, 11.0, 0.5, 0.80);// Jupiter‐ish

    return d;
}

void main() {
    // uv → world ray
    vec2 uv = (gl_FragCoord.xy - 0.5*u_resolution) / u_resolution.y;
    float camAng = u_time * 0.2;
    vec3 ro = vec3(sin(camAng)*20.0, 5.0, cos(camAng)*20.0);
    vec3 ta = vec3(0.0);
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(vec3(0,1,0), cw));
    vec3 cv = cross(cw, cu);
    vec3 rd = normalize(uv.x*cu + uv.y*cv + 1.5*cw);

    // march
    float t = rayMarch(ro, rd);
    vec3 col;

    if(t < MAX_DIST) {
        vec3 p = ro + rd * t;
        sceneSDF(p);           // sets gColor
        vec3 n = getNormal(p);

        // sun at origin
        vec3 lightDir = normalize(-p);
        float diff = max(dot(n, lightDir), 0.0);
        float sh   = softShadow(p + n * 0.01, lightDir);

        // specular
        vec3 V = normalize(ro - p);
        vec3 H = normalize(lightDir + V);
        float spec = pow(max(dot(n, H), 0.0), 64.0) * sh;

        // rim‑lighting
        float rim = pow(1.0 - max(dot(n, -rd), 0.0), 2.0);
        vec3 rimCol = vec3(1.0) - gColor; 

        // sun corona
        float glow = length(p) < 1.5 + 0.02 
            ? smoothstep(1.6, 1.4, length(p)) * 4.0 
            : 0.0;

        // combine
        vec3 ambient = 0.1 * gColor;
        vec3 diffuse = gColor * diff * sh;
        vec3 specCol = vec3(1.0) * spec;
        vec3 rimLight= rimCol * rim * 0.5;

        col = ambient + diffuse + specCol + rimLight + glow * gColor;
    }
    else {
        // dynamic starfield + aurora
        float y = rd.y * 0.5 + 0.5;
        vec3 sky = mix(vec3(0.02,0.01,0.05), vec3(0.0,0.0,0.2), y);
        float s = hash(floor(rd.xy * 400.0));
        float star = step(0.9997, s) * (0.8 + 0.2 * hash(floor(rd.xy*800.0)));
        // faint color bands like aurora
        float band = sin(rd.x*2.0 + u_time*0.5) * sin(rd.y*3.0 - u_time*0.3);
        vec3 aurora = hsv2rgb(vec3(mod(u_time*0.1 + band*0.2,1.0), 0.6, 0.5)) * 0.2;
        col = sky + star + aurora;
    }

    // vignette
    float v = smoothstep(1.0, 0.65, length(uv));
    col *= v;

    // final gamma
    col = pow(col, vec3(0.4545));
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision highp float;

uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 400
#define MAX_DIST 100.0
#define SURF_DIST 0.001

// Rotation matrix around the Y axis
mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// Smooth minimum function for blending shapes
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Signed distance functions
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

// Fractal shape
float sdFractal(vec3 p) {
    float scale = 1.0;
    float offset = 0.0;

    // Fold and twist space
    for (int i = 0; i < 8; i++) {
        p = abs(p) - vec3(0.5 + 0.2 * sin(u_time * 0.3));

        // Twist and fold
        p.xy *= rot(u_time * 0.1 + float(i) * 0.2);
        p.xz *= rot(u_time * 0.15 + sin(float(i) * 0.5));

        float s = 1.9 + 0.2 * sin(u_time * 0.4);
        p *= s;
        scale *= s;
    }

    return sdBox(p, vec3(0.8)) / scale;
}

// Scene distance function
float getDist(vec3 p) {
    // Main fractal shape
    float fractal = sdFractal(p);

    // Pulsating sphere
    float sphere = sdSphere(p, 1.5 + 0.5 * sin(u_time));

    // Blend shapes
    return smin(fractal, sphere, 0.3);
}

// Calculate normal from gradient
vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    vec3 n = vec3(
        getDist(p + e.xyy) - getDist(p - e.xyy),
        getDist(p + e.yxy) - getDist(p - e.yxy),
        getDist(p + e.yyx) - getDist(p - e.yyx)
    );
    return normalize(n);
}

// Raymarch the scene
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDist(p);
        dO += dS;
        if (dS < SURF_DIST || dO > MAX_DIST) break;
    }

    return dO;
}

// Calculate lighting
vec3 getLight(vec3 p, vec3 rd) {
    vec3 n = getNormal(p);

    // Light direction
    vec3 lightDir = normalize(vec3(sin(u_time) * 2.0, 2.0, cos(u_time) * 2.0));

    // Diffuse lighting
    float diff = max(dot(n, lightDir), 0.0);

    // Specular highlight
    vec3 refl = reflect(rd, n);
    float spec = pow(max(dot(refl, lightDir), 0.0), 32.0);

    // Colorize based on normal and time
    vec3 color = 0.5 + 0.5 * sin(u_time * 0.2 + p.y + vec3(0, 2, 4));

    // Ambient occlusion approximation
    float ao = 0.5 + 0.5 * n.y;

    // Combine components
    return color * (diff * 0.8 + 0.2) + spec * 0.5 + ao * 0.2;
}

void main() {
    // Normalized screen coordinates
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution) / min(u_resolution.x, u_resolution.y);

    // Create camera
    vec3 ro = vec3(0.0, 0.0, -3.0 + sin(u_time * 0.5)); // Ray origin (camera position)
    vec3 rd = normalize(vec3(uv, 1.0)); // Ray direction

    // Rotate camera
    rd.xz *= rot(u_time * 0.2);
    rd.xy *= rot(sin(u_time * 0.3) * 0.2);

    // Raymarch
    float d = rayMarch(ro, rd);

    // Color the scene
    vec3 col = vec3(0.0);

    if (d < MAX_DIST) {
        // Hit point
        vec3 p = ro + rd * d;

        // Get lighting and color
        col = getLight(p, rd);

        // Add some fog
        col = mix(col, vec3(0.0, 0.05, 0.1), 1.0 - exp(-0.01 * d * d));
    } else {
        // Background gradient based on ray direction
        float y = rd.y * 0.5 + 0.5;
        col = mix(vec3(0.0, 0.0, 0.2), vec3(0.0, 0.0, 0.0), y);

        // Add some stars
        vec3 rd2 = rd * 500.0;
        vec3 p = vec3(fract(rd2.x), fract(rd2.y), fract(rd2.z));
        float stars = smoothstep(0.97, 1.0, fract(sin(dot(floor(rd2), vec3(12.9898, 78.233, 45.164))) * 43758.5453));
        col += stars * 0.5;
    }

    // Vignette effect
    float vignette = 1.0 - length(uv) * 0.5;
    col *= vignette;


    // Output final color
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
#ifdef GL_ES
precision highp float;
#endif

uniform vec2 u_resolution;
uniform float u_time;

// ——————————————————————————————————————————————————————————————————————
// Helpers
// ——————————————————————————————————————————————————————————————————————
float sdMandelbulbDE(in vec3 p, float power) {
    vec3 z = p;
    float dr = 1.0;
    float r  = 0.0;
    const int MAXI = 20;
    for(int i = 0; i < MAXI; i++) {
        r = length(z);
        if(r > 4.0) break;
        // spherical coords
        float theta = acos(z.z/r);
        float phi   = atan(z.y, z.x);
        // scale & derivative
        float zr = pow(r, power - 1.0);
        dr = zr * power * dr + 1.0;
        // new position
        float sinTh = sin(theta * power);
        z = pow(r, power) * vec3(
              sinTh * cos(phi * power),
              sinTh * sin(phi * power),
              cos(theta * power)
            ) + p;
    }
    // distance estimator
    return 0.5 * log(r) * r / dr;
}

float sceneDE(vec3 p) {
    // blend two mandelbulbs with time‐varying power
    float p1 = 8.0 + 2.0*sin(u_time*0.15);
    float p2 = 4.0 + 2.0*cos(u_time*0.23);
    float d1 = sdMandelbulbDE(p, p1);
    float d2 = sdMandelbulbDE(p + vec3(1.2,0.0,0.0), p2);
    return min(d1, d2);
}

vec3 calcNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
      sceneDE(p+vec3(e,0,0)) - sceneDE(p-vec3(e,0,0)),
      sceneDE(p+vec3(0,e,0)) - sceneDE(p-vec3(0,e,0)),
      sceneDE(p+vec3(0,0,e)) - sceneDE(p-vec3(0,0,e))
    ));
}

// soft shadow
float softShad(in vec3 ro, in vec3 rd) {
    float res = 1.0, t = 0.02;
    for(int i=0; i<40; i++){
        float h = sceneDE(ro + rd*t);
        if(h<0.001) return 0.0;
        res = min(res, 8.0*h/t);
        t += clamp(h, 0.02, 0.2);
    }
    return clamp(res, 0.0, 1.0);
}

// simple ambient occlusion
float ao(in vec3 p, in vec3 n){
    float oc = 0.0, sc = 1.0;
    for(int i=1; i<=5; i++){
        float d = 0.02 * float(i);
        oc += (d - sceneDE(p + n*d)) * sc;
        sc *= 0.7;
    }
    return clamp(1.0 - oc, 0.0, 1.0);
}

vec3 hsv(in vec3 c){
    vec3 p = abs(fract(c.xxx + vec3(0.,2./3.,1./3.))*6. -3.) -1.;
    return c.z * mix(vec3(1.), clamp(p,0.,1.), c.y);
}

// ——————————————————————————————————————————————————————————————————————
// Ray‐march
// ——————————————————————————————————————————————————————————————————————
vec3 render(in vec3 ro, in vec3 rd){
    float t = 0.0;
    bool hit = false;
    for(int i=0; i<120; i++){
        float d = sceneDE(ro + rd*t);
        if(d < 0.001) { hit = true; break; }
        t += d;
        if(t > 50.0) break;
    }
    // sky: simple gradient + star‐noise
    vec3 sky = mix(vec3(0.02,0.04,0.1), vec3(0.0,0.0,0.02), rd.y*0.5 + 0.5);
    float stars = pow(max(0.,sin(rd.x*200.)*sin(rd.y*200.)*sin(rd.z*200.)),16.);
    sky += stars * 0.5;
    if(!hit) return sky;

    // surface shading
    vec3 pos = ro + rd*t;
    vec3 nor = calcNormal(pos);
    // two moving lights
    vec3 lp = vec3(5.*cos(u_time*0.6), 4. + sin(u_time*0.4)*2., 5.*sin(u_time*0.7));
    vec3 ld = normalize(lp - pos);
    float dif = max(dot(nor,ld),0.);
    float sh  = softShad(pos + nor*0.002, ld);
    float amb = ao(pos,nor)*0.5 + 0.2;
    // rim
    float rim = pow(1.0 - max(dot(nor,-rd),0.0),4.0);

    // color ramps
    float hue = mod(length(pos.xy)*0.12 - u_time*0.1, 1.);
    vec3 base = hsv(vec3(hue, 0.7, 1.0));

    vec3 col = base * (amb + dif*sh) + rim * hsv(vec3(hue+0.5,0.8,1.0));
    // HDR bloom hint
    col += pow(max(0.0, dif*sh), 8.0) * 0.5;
    // tone‐map + gamma
    col = col / (col + vec3(1.0));
    col = pow(col, vec3(0.4545));
    return col;
}

// ——————————————————————————————————————————————————————————————————————
// Main
// ——————————————————————————————————————————————————————————————————————
void main(){
    vec2 uv = (gl_FragCoord.xy*2.0 - u_resolution.xy) / min(u_resolution.x,u_resolution.y);

    // camera on a slow Lissajous path
    float cr = 6.0 + sin(u_time*0.3)*2.0;
    vec3 ro = vec3(cr*cos(u_time*0.4), 2.5 + sin(u_time*0.5)*1.0, cr*sin(u_time*0.3));
    vec3 ta = vec3(0.0,0.0,0.0);

    // build basis
    vec3 fw = normalize(ta - ro);
    vec3 rt = normalize(cross(fw, vec3(0,1,0)));
    vec3 up = cross(rt, fw);

    float fov = 1.5;
    vec3 rd = normalize(uv.x*rt + uv.y*up + fov*fw);

    vec3 color = render(ro, rd);
    gl_FragColor = vec4(color, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
