frag:
precision highp float;
uniform float u_time;
uniform vec2 u_resolution;

float menger(vec3 p) {
    float scale = 1.0;
    float d = 0.0;
    for (int i = 0; i < 10; i++) {
        p = abs(p);
        if (p.x < p.y) p.xy = p.yx;
        if (p.x < p.z) p.xz = p.zx;
        if (p.y < p.z) p.yz = p.zy;
        p = p * 3.0 - vec3(1.5);
        scale *= 3.0;
    }
    return length(p) / scale;
}

float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 60; i++) {
        vec3 p = ro + rd * t;
        float d = menger(p);
        if (d < 0.002) return t;
        t += d;
        if (t > 15.0) break;
    }
    return t;
}

vec3 getNormal(vec3 p) {
    float eps = 0.001;
    return normalize(vec3(
        menger(p + vec3(eps, 0, 0)) - menger(p - vec3(eps, 0, 0)),
        menger(p + vec3(0, eps, 0)) - menger(p - vec3(0, eps, 0)),
        menger(p + vec3(0, 0, eps)) - menger(p - vec3(0, 0, eps))
    ));
}

mat3 rotateY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, 0, -s, 0, 1, 0, s, 0, c);
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;
    vec3 ro = vec3(0.0, 0.0, -6.0);
    vec3 rd = normalize(vec3(uv, 1.5));

    mat3 ry = rotateY(u_time * 0.3);
    ro = ry * ro;
    rd = ry * rd;

    float t = raymarch(ro, rd);
    vec3 color = vec3(0.0);

    if (t < 15.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        vec3 light = normalize(vec3(-0.4, 0.8, -1.0));
        float diff = max(dot(n, light), 0.0);
        color = mix(vec3(0.1), vec3(0.8, 0.9, 1.0), diff);
    }

    gl_FragColor = vec4(color, 1.0);
}

precision highp float;

uniform vec2 u_resolution;
uniform float u_time;

// Simple and efficient hash function
float hash21(vec2 p) {
    p = fract(p * vec2(123.34, 456.78));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

// Smooth minimum function for soft transitions
float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h * h * h * k * (1.0 / 6.0);
}

// Simple rotation function
mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
