// Distance field for a sphere
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// Creates a field of spheres
float scene(vec3 p) {
    // Store the original position
    vec3 op = p;

    // Rotate the entire scene
    p.xy *= rot(u_time * 0.2);
    p.yz *= rot(u_time * 0.1);

    // Repeat the space to create a grid
    vec3 rep = vec3(4.0);
    vec3 q = mod(p + 0.5 * rep, rep) - 0.5 * rep;

    // Base SDF
    float d = sdSphere(q, 0.8 + 0.2 * sin(u_time + length(p) * 0.5));

    // Add some more spheres with different sizes and positions
    for (int i = 0; i < 3; i++) {
        float fi = float(i);
        vec3 offset = vec3(
            sin(u_time * 0.5 + fi * 2.1) * 1.5,
            cos(u_time * 0.7 + fi * 1.7) * 1.5,
            sin(u_time * 0.3 + fi * 1.3) * 1.5
        );
        float size = 0.6 + 0.4 * sin(u_time * 0.4 + fi * 1.9);

        // Create a sphere and blend it with the existing scene
        float sphere = sdSphere(q - offset, size);
        d = smin(d, sphere, 1.0);
    }

    // Add some distortion based on the original position
    d += sin(op.x * 2.0 + u_time) * sin(op.y * 2.0 + u_time) * sin(op.z * 2.0 + u_time) * 0.1;

    return d;
}

// Calculate the normal at a point
vec3 getNormal(vec3 p) {
    const float e = 0.001;
    vec2 h = vec2(e, 0);
    return normalize(vec3(
        scene(p + h.xyy) - scene(p - h.xyy),
        scene(p + h.yxy) - scene(p - h.yxy),
        scene(p + h.yyx) - scene(p - h.yyx)
    ));
}

// Raymarch through the scene
float raymarch(vec3 ro, vec3 rd) {
    float t = 7.0;
    for (int i = 0; i < 80; i++) {
        vec3 p = ro + rd * t;
        float d = scene(p);
        if (d < 0.001 || t > 20.0) break;
        t += d * 0.5; // Slow down the marching for better quality
    }
