}

// Generate a vibrant color palette based on position and normal
vec3 getColor(vec3 p, vec3 n) {
    // Base color from position
    vec3 col = 0.5 + 0.5 * cos(length(p) * 0.2 + u_time * 0.1 + vec3(0, 2, 4));

    // Add some patterns based on normal
