    vec3 rd = normalize(vec3(uv, 1.5)); // Ray direction

    // Add some camera movement/shake
    ro.xy += vec2(sin(u_time * 0.7), cos(u_time * 0.1)) * 0.3;
    rd.xy *= rot(sin(u_time * 0.3) * 0.1);

    // Raymarch the scene
    float t = raymarch(ro, rd);

    // Initialize color
    vec3 col = vec3(0.0);

    // If we hit something
