// Created by Max Warren. All rights reserved


#ifdef GL_ES
precision highp float;
#endif

uniform vec2 u_resolution;
uniform float u_time;
float mandelboxDE(vec3 p) {
    // parameters
    float scale       = 4.0 + 0.7 * sin(u_time * 0.1);
    float fixedR      = 1.0;
    float minR        = 0.5;
    const int ITER    = 20;
    vec3  z           = p;
    float dr          = 1.0;

    for(int i=0; i<ITER; i++){
        // 1) box‐fold
        z = clamp(z, -1.5, 1.0)*2.0 - z;

        // 2) sphere‐fold
        float r2 = dot(z,z);
        if(r2 < minR){
            float t = fixedR/minR;
            z *= t;
            dr *= t;
        } else if(r2 < fixedR){
            float t = fixedR/r2;
            z *= t;
            dr *= t;
        }

        // 3) scale and translate by original p
        z = scale*z + p;
        dr = dr * abs(scale) + 1.0;
    }

    return length(z) / abs(dr);
}

// ———————————————————————
// Scene DE + Normal + AO + Shadow
// ———————————————————————
float sceneDE(vec3 p) {
    // offset p slightly to avoid perfect symmetry
    p += 0.1 * vec3(
        sin(p.y*2.0 + u_time*0.3),
        sin(p.z*1.7 - u_time*0.2),
        sin(p.x*1.3 + u_time*0.4)
    );
    return mandelboxDE(p);
}

vec3 getNormal(vec3 p) {
    float e = 0.0008;
    return normalize(vec3(
      sceneDE(p + vec3(e,0,0)) - sceneDE(p - vec3(e,0,0)),
      sceneDE(p + vec3(0,e,0)) - sceneDE(p - vec3(0,e,0)),
      sceneDE(p + vec3(0,0,e)) - sceneDE(p - vec3(0,0,e))
    ));
}

float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0, t = 0.02;
    for(int i=0; i<30; i++){
        float h = sceneDE(ro + rd*t);
        if(h < 0.001) return 0.0;
        res = min(res, 8.0*h/t);
        t += clamp(h, 0.02, 0.2);
    }
    return clamp(res, 0.0, 1.0);
}

float ambientOccl(vec3 p, vec3 n) {
    float oc = 0.0, w = 1.0;
    for(int i=1; i<=4; i++){
        float d = 0.02 * float(i);
        oc += (d - sceneDE(p + n*d)) * w;
        w *= 0.6;
    }
    return clamp(1.0 - oc, 0.0, 1.0);
}

// HSV → RGB
vec3 hsv2rgb(in vec3 c) {
    vec3 p = abs(fract(c.xxx + vec3(0.0,2.0/3.0,1.0/3.0))*6.0 - 3.0) - 1.0;
    return c.z * mix(vec3(1.0), clamp(p,0.0,1.0), c.y);
}

// ———————————————————————
// Main
// ———————————————————————
void main(){
    // 1) UV → ray direction
    vec2 uv = (gl_FragCoord.xy * 2.0 - u_resolution) / min(u_resolution.x,u_resolution.y);
    vec3 ro = vec3(0.0);                    // camera at the fractal’s origin
    vec3 fw = normalize(vec3(0,0,1));      // look “forward” along +Z
    vec3 rt = normalize(cross(fw,vec3(0,1,0)));
    vec3 up = cross(rt,fw);
    vec3 rd = normalize(uv.x*rt + uv.y*up + 1.2*fw);

    // 2) Ray‑march
    float t = 1.0;
    bool hit = false;
    for(int i=0; i<120; i++){
        float d = sceneDE(ro + rd*t);
        if(d < 0.001) { hit = true; break; }
        t += d;
        if(t > 10.0) break;  // max view distance
    }

    // 3) Background
    vec3 col = vec3(0.02,0.0,0.01) + 0.1*rd.y;

    // 4) Shade fractal surface
    if(hit){
        vec3 pos = ro + rd*t;
        vec3 n   = getNormal(pos);

        // one moving light
        vec3 L = normalize(vec3( 0.5, 0.8, 1.0 ));
        float diff = max(dot(n,L), 0.0);
        float sh   = softShadow(pos + n*0.001, L);
        float ao   = ambientOccl(pos,n);

        // color cycles with height + time
        float hue = fract(0.3 + pos.y*0.2 - u_time*0.1);
        vec3 base = hsv2rgb(vec3(hue, 0.7, 1.0));

        // combine
        vec3 ambient = base * 0.2 * ao;
        vec3 diffuse = base * diff * sh;
        vec3 rim     = hsv2rgb(vec3(fract(hue+0.5),0.8,1.0))
                       * pow(1.0 - max(dot(n,-rd),0.0), 3.0) * 0.25;

        col = ambient + diffuse + rim;
    }

    // 5) Tone map + gamma
    col = col/(col+vec3(1.0));
    col = pow(col, vec3(0.4545));

    gl_FragColor = vec4(col,1.0);
}

