const int MAX_ITERATIONS = 4;
const int MAX_FOLDS = 6;

for (int i = 0; i < MAX_ITERATIONS; i++) {
    if (float(i) >= maxIterations) break;

    vec2 p = uv + mouseOffset;

    float scale = pow(2.0, float(i)) * (1.0 + 0.1 * sin(t + float(i)));
    p = rotate(p, t * (0.1 + float(i) * 0.05) + sin(t * 0.2 + float(i)) * 0.2);
    p *= scale;

    float foldCount = min(6.0, 8.0 - float(i));

