// Constants
#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265359

// Rotation matrix around Y axis
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        c, 0, s,
        0, 1, 0,
        -s, 0, c
    );
}

// Rotation matrix around X axis
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        1, 0, 0,
        0, c, -s,
        0, s, c
    );
}

// SDF for a sphere
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// SDF for a box
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

// SDF for a torus
float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

// SDF for a cylinder
float sdCylinder(vec3 p, vec3 c) {
    return length(p.xz - c.xy) - c.z;
}

// Smooth minimum function for smooth blending
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Repeat space along one axis
float opRep1(float p, float c) {
    return mod(p, c) - c * 0.5;
}

// Repeat in two dimensions
vec2 opRep2(vec2 p, vec2 c) {
    return mod(p, c) - c * 0.5;
}

// Scene definition using SDFs
float sceneSDF(vec3 p) {
    // Apply global rotation
    p = rotateY(u_time * 0.2) * p;

    // Moving origin for the main object group
    vec3 movingOrigin = vec3(0.0, sin(u_time) * 0.5, 0.0);
    vec3 q = p - movingOrigin;

    // Main sphere with animated radius pulsation
    float sphereRadius = 1.0 + sin(u_time * 2.0) * 0.2;
    float sphere = sdSphere(q, sphereRadius);

    // Outer torus rotating around the sphere
