    vec3 box1Pos = q - vec3(cos(orbitSpeed) * orbitRadius, 
                          sin(orbitSpeed * 0.7) * 0.5,
                          sin(orbitSpeed) * orbitRadius);
    box1Pos = rotateY(u_time * 3.0) * box1Pos;
    float box1 = sdBox(box1Pos, vec3(0.4));

    vec3 box2Pos = q - vec3(cos(orbitSpeed + PI) * orbitRadius, 
                          sin(orbitSpeed * 0.7 + PI) * 0.5,
                          sin(orbitSpeed + PI) * orbitRadius);
    box2Pos = rotateY(u_time * -2.5) * box2Pos;
    float box2 = sdBox(box2Pos, vec3(0.4));

    // Cylinders at a distance
    vec3 cylPos = p;
    cylPos.x = opRep1(cylPos.x, 14.0);
    cylPos.z = opRep1(cylPos.z, 14.0);
    float cylHeight = 0.4 + sin(length(vec2(p.x, p.z)) * 0.1 + u_time) * 0.5;
    float cylinders = sdCylinder(cylPos, vec3(0.0, 0.0, 0.2)) - cylHeight;
    float groundY = -1.8;
    float ripple = sin(length(p.xz) - u_time * 2.0) * 0.1;
    float ground = p.y - (groundY + ripple);

    // Combine all objects
    float mainObject = smin(sphere, torus, 0.3);
    mainObject = min(mainObject, box1);
    mainObject = min(mainObject, box2);

    return min(min(mainObject, cylinders), ground);
}

// Ray marching function
float raymarch(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = sceneSDF(p);
        dO += dS;
        if(dS < SURF_DIST || dO > MAX_DIST) break;
    }

    return dO;
}

// Calculate normal by sampling nearby points
vec3 getNormal(vec3 p) {
    float e = 0.001;
    vec3 n = vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    );
    return normalize(n);
}

// Get material properties based on position and normal
vec3 getMaterial(vec3 p, vec3 normal) {
    // Basic material color based on position
    vec3 material = vec3(0.5) + 0.5 * sin(vec3(p.x, p.y, p.z) * 0.5 + vec3(0.0, 0.33, 0.67) * u_time);

    // Ground plane material
    if(p.y < -1.75) {
        vec2 checkPos = opRep2(p.xz, vec2(1.0));
        float check = step(0.0, checkPos.x) * step(0.0, checkPos.y) * 2.0 - 1.0;
        material = mix(
            vec3(0.8, 0.1, 0.1), 
            vec3(0.1, 0.8, 0.1), 
            check > 0.0 ? 1.0 : 0.0
        );

        // Add reflective property to the ground
        float reflectivity = 0.3;
        material *= (1.0 - reflectivity);
    }

    return material;
}

// Simple soft shadow calculation
float getShadow(vec3 p, vec3 lightDir, float k) {
    float res = 1.0;
    float t = 0.01;
    for(int i = 0; i < 16; i++) {
        float h = sceneSDF(p + lightDir * t);
        if(h < 0.001) return 0.0;
        res = min(res, k * h / t);
        t += h;
        if(t > 8.0) break;
    }
    return res;
}

// Atmospheric fog effect
vec3 applyFog(vec3 color, float distance, vec3 rayDir) {
    vec3 fogColor = vec3(0.5, 0.6, 0.7);
    float fogAmount = 1.0 - exp(-distance * 0.025);

    // Make fog dependent on height
    fogAmount *= mix(1.0, 0.5, max(0.0, rayDir.y));

    return mix(color, fogColor, fogAmount);
}

// Post-processing effects
vec3 postProcess(vec3 col, vec2 uv) {
    // Apply vignette
    float vignetteStrength = 0.4;
    float vignette = smoothstep(0.5, 0.35, length(uv));
    col = mix(col, col * vignette, vignetteStrength);

    // Apply contrast and saturation
    col = pow(col, vec3(1.0 / 2.2)); // Gamma correction

    return col;
}

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution;
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y; // Aspect ratio correction

    // Camera setup with animated movement
    float camRadius = 6.0;
    float camHeight = 2.0 + sin(u_time * 0.3) * 0.5;
    float camAngle = u_time * 0.3;

    vec3 ro = vec3(cos(camAngle) * camRadius, camHeight, sin(camAngle) * camRadius); // Ray origin (camera position)
    vec3 lookAt = vec3(0.0, 0.0, 0.0); // Point to look at

    // Camera frame
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    float fov = 60.0 * PI / 180.0;
    vec3 rd = normalize(forward + uv.x * right * tan(fov/2.0) + uv.y * up * tan(fov/2.0)); // Ray direction

    // Primary light sources
    vec3 light1Pos = vec3(5.0, 5.0, -5.0);
    vec3 light1Color = vec3(1.0, 0.9, 0.7);

    vec3 light2Pos = vec3(-3.0, 4.0, 3.0);
    vec3 light2Color = vec3(0.5, 0.7, 1.0);

    // Perform ray marching
    float t = raymarch(ro, rd);

    // Default sky color with gradient
    vec3 skyColor = mix(
        vec3(0.5, 0.7, 1.0), // Horizon color
        vec3(0.2, 0.4, 0.8), // Zenith color
        pow(max(0.0, rd.y * 0.5 + 0.5), 0.5)
    );

    // Add subtle sun
    float sun = pow(max(0.0, dot(rd, normalize(vec3(0.5, 0.6, 0.3)))), 32.0);
    skyColor += vec3(1.0, 0.7, 0.3) * sun;

    // Default color is sky
    vec3 color = skyColor;

    // If we hit something
    if(t < MAX_DIST) {
        // Calculate the position where the ray hit
        vec3 p = ro + rd * t;

        // Get the normal at the surface
        vec3 normal = getNormal(p);

        // Get material properties
        vec3 material = getMaterial(p, normal);

        // Lighting calculations
        vec3 ambient = vec3(0.1);

        // Light 1
        vec3 light1Dir = normalize(light1Pos - p);
        float diff1 = max(dot(normal, light1Dir), 0.0);
        float shadow1 = getShadow(p + normal * 0.01, light1Dir, 16.0);
        vec3 diffuse1 = light1Color * diff1 * shadow1;

        // Light 2
        vec3 light2Dir = normalize(light2Pos - p);
        float diff2 = max(dot(normal, light2Dir), 0.0);
        float shadow2 = getShadow(p + normal * 0.01, light2Dir, 16.0);
        vec3 diffuse2 = light2Color * diff2 * shadow2;

        // Specular highlights
        vec3 viewDir = normalize(ro - p);
        vec3 reflectDir1 = reflect(-light1Dir, normal);
        float spec1 = pow(max(dot(viewDir, reflectDir1), 0.0), 32.0);
        vec3 specular1 = light1Color * spec1 * shadow1 * 0.5;

        vec3 reflectDir2 = reflect(-light2Dir, normal);
        float spec2 = pow(max(dot(viewDir, reflectDir2), 0.0), 32.0);
        vec3 specular2 = light2Color * spec2 * shadow2 * 0.5;

        // Add ambient occlusion
        float ao = 1.0;
        float occ = 0.0;
        float sca = 1.0;
        for(int i = 0; i < 5; i++) {
            float h = 0.01 + 0.12 * float(i) / 4.0;
            float d = sceneSDF(p + normal * h);
            occ += (h - d) * sca;
            sca *= 0.95;
        }
        ao = clamp(1.0 - 3.0 * occ, 0.0, 1.0);

        // Ground reflections (simple approximation)
        vec3 reflection = vec3(0.0);
        if(p.y < -1.75) {
            // Approximate reflection by sampling the scene from a reflected ray
            vec3 reflectedRd = reflect(rd, normal);
            float reflectDist = raymarch(p + normal * 0.01, reflectedRd);

            if(reflectDist < MAX_DIST) {
                vec3 reflectPos = p + reflectedRd * reflectDist;
                vec3 reflectNormal = getNormal(reflectPos);
                vec3 reflectMaterial = getMaterial(reflectPos, reflectNormal);

                // Very simple reflection - just the base color
                reflection = reflectMaterial * 0.3;
            } else {
                // Reflect the sky
                reflection = skyColor * 0.3;
            }
        }

        // Combine lighting and material color
        color = material * (ambient + (diffuse1 + diffuse2) * ao) + specular1 + specular2 + reflection;

        // Apply fog
        color = applyFog(color, t, rd);
    }

    // Apply post-processing
    color = postProcess(color, uv * 0.5 + 0.5);

    // Output the final color
    gl_FragColor = vec4(color, 1.0);
}

