// fragment.glsl
precision highp float;
uniform vec2 u_resolution;
uniform float u_time;

// ——————————————————
//  Mandelbulb Distance Estimator
// ——————————————————
float mandelbulbDE(vec3 p) {
    vec3 z = p;
    float dr = 1.0;
    float r = 0.0;
    const int ITER = 12;
    const float POWER = 8.0;
    for(int i = 0; i < ITER; i++) {
        r = length(z);
        if(r > 2.0) break;
        // spherical coordinates
        float theta = acos(z.z / r);
        float phi   = atan(z.y, z.x);
        // scale & derivative
        float zr = pow(r, POWER-1.0);
        dr = zr * POWER * dr + 1.0;
        // new position
        float sinth = sin(POWER * theta);
        z = zr * vec3(
            sinth * cos(POWER * phi),
            sinth * sin(POWER * phi),
            cos(POWER * theta)
        ) + p;
    }
    return 0.5 * log(r) * r / dr;
}

// ——————————————————
//  Scene SDF & Helpers
// ——————————————————
float sceneSDF(vec3 p) {
    // center fractal
    vec3 fp = p * 0.8;
    float d0 = mandelbulbDE(fp);

    // ground plane at y = -1.2
    float d1 = p.y + 1.2;

    return min(d0, d1);
}

vec3 getNormal(vec3 p) {
    float e = 0.0005;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// soft shadow
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0, t = 0.02;
    for(int i = 0; i < 32; i++) {
        float h = sceneSDF(ro + rd * t);
        res = min(res, 8.0 * h / t);
        t += clamp(h, 0.02, 0.1);
        if(res < 0.01) break;
    }
    return clamp(res, 0.0, 1.0);
}

// ambient occlusion
float ambientOcclusion(vec3 p, vec3 n) {
    float ao = 0.0, sca = 1.0;
    for(int i=0; i<5; i++){
        float h = 0.02 + 0.1 * float(i)/4.0;
        ao += (h - sceneSDF(p + n*h)) * sca;
        sca *= 0.5;
    }
    return clamp(1.0 - ao, 0.0, 1.0);
}

// ray march
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < 200; i++){
        float d = sceneSDF(ro + rd * t);
        if(d < 0.001) break;
        t += d;
        if(t > 100.0) break;
    }
    return t;
}

// hsv → rgb
vec3 hsv(float h, float s, float v) {
    vec3 c = abs(fract(vec3(h)+vec3(0,2,1)/3.0)*6.0-3.0)-1.0;
    return v * mix(vec3(1.0), clamp(c,0.0,1.0), s);
}

void main(){
    // normalized coords
    vec2 uv = (gl_FragCoord.xy*2.0 - u_resolution) / u_resolution.y;

    // animated camera on a lissajous path
    vec3 ro = vec3(
        6.0 * cos(u_time*0.3),
        2.5 + sin(u_time*0.5)*1.0,
        6.0 * sin(u_time*0.3)
    );
    vec3 ta = vec3(0.0, 0.0, 0.0);

    // build camera basis
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(vec3(0,1,0), cw));
    vec3 cv = cross(cw, cu);
    vec3 rd = normalize(uv.x*cu + uv.y*cv + 1.6*cw);

    // march
    float t = raymarch(ro, rd);
    vec3 col = vec3(0.02,0.04,0.1) + 0.2 * rd.y;             // night‑sky base

    if(t < 100.0) {
        vec3 p = ro + rd*t;
        vec3 n = getNormal(p);
        vec3 Lpos = vec3(5.0, 8.0, -3.0);
        vec3 L = normalize(Lpos - p);

        // lighting terms
        float diff = max(dot(n, L), 0.0);
        float sh   = softShadow(p + n*0.001, L);
        float spec = pow(max(dot(reflect(-L, n), -rd), 0.0), 64.0);
        float ao   = ambientOcclusion(p, n);

        // color from HSV palette
        float hue = mod(length(p.xz)*0.15 - u_time*0.05, 1.0);
        vec3 base = hsv(hue, 0.7, 1.0);

        vec3 lighting = (0.1 + diff*sh)*base + spec*vec3(1.0);
        lighting *= ao;

        // volumetric fog
        float fog = 1.0 - exp(-t * 0.02);
        col = mix(col, lighting, exp(-0.02*t));
        col = mix(col, vec3(0.02,0.04,0.1), fog);
    }

    gl_FragColor = vec4(col, 1.0);
}

