//MAXW :)
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;
void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution.xy;
    vec2 p = uv * 2.0 - 1.0;
    p.x *= u_resolution.x / u_resolution.y;
//cam 
    vec3 ro = vec3(0.0, 18.0, -9.0);
    vec3 rd = normalize(vec3(p.x, p.y * 7.5 - 0.5, 1.0));

//floor
    float t = -ro.y / rd.y;
    vec3 pos = ro + rd * t;
    float speed = 9.0;
    pos.z += u_time * speed;
    float lineWidth = 0.001;
//grid line distance
    float gx = abs(mod(pos.x, 1.0) - 0.5);
    float gz = abs(mod(pos.z, 1.0) - 0.5);
    float lineDist = min(gx, gz);
    float gridMask = 0.5 - smoothstep(lineWidth, lineWidth + 0.01, 
    lineDist);
    float fog = exp(-0.04 * t);
    gridMask *= fog;
    vec3 neon = vec3(0.0, 1.2, 1.0);
    vec3 floorBase = mix(vec3(0.05, 0.0, 0.1), neon, 
    smoothstep(0.0, 1.0, fog));
    vec3 col = mix(floorBase, neon, gridMask);
    if (t < 0.0 || t > 100.0) {
        col = mix(vec3(0.8,0.2,0.3), vec3(0.1,0.0,0.2), p.y * 0.5 + 0.5);
        vec2 sunUV = uv - vec2(0.5, 0.25);
        float sunR = 0.3;
        float d = length(sunUV);
        float glow = smoothstep(sunR, sunR - 0.02, d);
        float core = smoothstep(sunR * 0.6, sunR * 0.58, d);
        vec3 sunCol = vec3(1.0, 0.8, 0.2);
        col = mix(col, sunCol, glow);
        col = mix(col, sunCol * 1.5, core);
    }
    gl_FragColor = vec4(col, 1.0);
}

