// Proprietary – © Max Warren. This code is not open source and may not be copied or used without permission


precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;
float hash(vec2 p) {
    p = fract(p * vec2(1.34, 456.21));
    p += dot(p, p + 78.23);
    return fract(p.x * p.y);
}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);
    return mix(
        mix(hash(i), hash(i + vec2(1.0, 0.0)), f.x),
        mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), f.x),
        f.y
    );
}
float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}
float gridPattern(vec2 p) {
    vec2 grid = abs(fract(p - 0.5) - 0.5);
    float lineWidth = 0.1;
    vec2 lines = smoothstep(0.0, lineWidth, grid);
    float line = min(lines.x, lines.y);
    return 1.0 - min(line, 1.0);
}

vec3 cityColor(vec3 p, vec3 n, float height) {
     vec3 neonPink = vec3(1.0, 0.1, 0.8);
    vec3 neonBlue = vec3(0.0, 0.9, 1.0);
    vec3 neonPurple = vec3(0.6, 0.0, 0.9);
    vec3 neonYellow = vec3(1.0, 0.9, 0.0);
    float scanline = step(0.5, fract(p.y * 20.0 + u_time * 0.5));
    float windowPattern = step(0.7, noise(vec2(floor(p.x * 10.0), floor(p.y * 15.0))));
    float glitchIntensity = 0.3;
    float glitch = step(0.95, noise(vec2(floor(p.y * 50.0), floor(u_time * 5.0)))) * glitchIntensity;
    p.x += glitch;
    float window = step(0.6, hash(floor(p.xz * 2.0)));
    float heightFactor = smoothstep(0.0, 20.0, height);
    vec3 buildingColor = mix(neonBlue * 0.1, neonPurple * 0.1, heightFactor);
    buildingColor = mix(buildingColor, neonYellow, window * windowPattern * 0.4);
    buildingColor = mix(buildingColor, neonPink, gridPattern(p.xz * 10.0) * 0.5);
    float shine = pow(max(0.0, dot(n, normalize(vec3(0.5, 1.0, 0.5)))), 2.0);
    buildingColor += neonBlue * shine * 0.5;
    return buildingColor;
}
float sceneSDF(vec3 p) {
    float ground = p.y + 1.0;
    vec2 gridPos = floor(p.xz / 8.0);
    vec2 localPos = mod(p.xz, 8.0) - 4.0;
    float buildingNoise = hash(gridPos);
    float buildingHeight = buildingNoise * 15.0 + 5.0;
    float building = boxSDF(vec3(localPos.x, p.y - buildingHeight * 0.5, localPos.y), 
                          vec3(2.0 + buildingNoise * 0.8, buildingHeight * 0.5, 2.0 + buildingNoise * 0.8));
    float antenna = 100.0;
    if (buildingNoise > 0.75) {
        antenna = boxSDF(vec3(localPos.x, p.y - buildingHeight - 3.0, localPos.y), 
                        vec3(0.1, 3.0, 0.1));
    }
    float holo = 100.0;
    if (buildingNoise > 0.8) {
        vec3 holoPos = vec3(localPos.x, p.y - buildingHeight * 0.3, localPos.y);
        float holoTime = u_time * 3.0 + buildingNoise * 10.0;
        holoPos.xz += vec2(sin(holoTime * 0.3), cos(holoTime * 0.5)) * 0.5;
        holo = boxSDF(holoPos, vec3(0.7, 0.2, 0.7));
    }
    return min(min(ground, building), min(antenna, holo));
}
float raymarch(vec3 ro, vec3 rd, float maxDist) {
    float t = 0.0;
    for(int i = 0; i < 120; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if(d < 0.001 || t > maxDist) break;
        t += d * 0.5;
    }
    return t;
}
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}
vec3 applyFog(vec3 color, float distance, vec3 rayDir) {
    vec3 fogColor = vec3(0.0, 0.05, 0.1);
    float fogAmount = 1.0 - exp(-distance * 0.02);
    float beam = pow(max(0.0, dot(rayDir, normalize(vec3(0.0, -0.2, 1.0)))), 8.0);
    fogColor = mix(fogColor, vec3(0.0, 0.5, 0.8), beam * 0.5);
    return mix(color, fogColor, fogAmount);
}
void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    float cameraHeight = 20.0 + sin(u_time * 0.2) * 0.5;
    vec3 ro = vec3(sin(u_time * 0.1) * 5.0, cameraHeight, u_time * 2.0);
    vec3 lookAt = vec3(sin(u_time * 0.1 + 0.5) * 3.0, cameraHeight - 5.0, ro.z + 20.0);
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);
    float t = raymarch(ro, rd, 150.0);
    vec3 col = vec3(0.0);
    if(t < 150.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        vec2 gridPos = floor(p.xz / 8.0);
        float buildingHeight = hash(gridPos) * 15.0 + 5.0;
        col = cityColor(p, n, buildingHeight);
        float heightRatio = (p.y + 1.0) / (buildingHeight + 1.0);
        float glow = smoothstep(0.7, 1.0, heightRatio) * 0.5;
        if(abs(mod(p.y + u_time, 3.0) - 1.5) < 0.1) {
            col += vec3(0.8, 0.2, 1.0) * 0.5;
        }
        float grid = step(0.9, sin(p.y * 20.0) * 0.5 + 0.5) * step(0.9, sin(p.x * 20.0) * 0.5 + 0.5);
        col += vec3(0.0, 0.6, 1.0) * grid * 0.3;
        float rain = step(0.98, fract(p.y * 0.2 - u_time * 2.0));
        col += vec3(0.6, 0.8, 1.0) * rain * 0.2;
    } else {
        float horizon = max(0.0, 1.0 - abs(rd.y) * 5.0);
        vec2 skyUV = rd.xz / (rd.y + 0.1);
        float gridPattern = step(0.9, fract(skyUV.x * 10.0)) + step(0.9, fract(skyUV.y * 10.0));
        col = mix(
            vec3(5.01, 0.04, 5.08),
            vec3(1.0, 0.5, 1.7),
            horizon
        );
        col += vec3(0.8, 0.2, 0.5) * pow(horizon, 8.0) * 0.5;
        col += vec3(0.0, 0.4, 0.6) * gridPattern * 0.05 * horizon;
    }
    col = applyFog(col, t, rd);
    vec2 vignetteUV = gl_FragCoord.xy / u_resolution;
    float vignette = smoothstep(0.0, 1.0, 16.0 * vignetteUV.x * vignetteUV.y * (1.0 - vignetteUV.x) * (1.0 - vignetteUV.y));
    col *= mix(0.7, 1.0, vignette);
    col = mix(col, col * vec3(1.1, 0.9, 1.2), 0.5);
    col = pow(col, vec3(0.9));
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision highp float;

uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 200
#define MAX_DIST 200.0
#define SURF_DIST 0.01
#define PI 3.14159265

// Simple hash function
float hash(float n) {
    return fract(sin(n) * 43758.5453);
}

// 2D hash function
vec2 hash2(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
    return fract(sin(p) * 43758.5453);
}

// Rotation matrix
mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// Signed distance functions
float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdRoundBox(vec3 p, vec3 b, float r) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0) - r;
}

float sdCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// Function to generate buildings with more variety
float getBuildingHeight(vec2 p) {
    // More varied height distribution
    float h = hash(p.x * 13.0 + p.y * 17.0);
    // Create some taller skyscrapers among smaller buildings
    if (h > 0.85) return h * 40.0 + 15.0;
    return h * 20.0 + 5.0;
}

// Building window pattern
float getWindowPattern(vec3 p, vec2 cell) {
    // Window grid pattern with variety
    float windowSize = 0.7 + hash(cell.x * 31.0 + cell.y * 57.0) * 0.3;
    vec3 windowGrid = mod(p + 0.5, vec3(2.0, 3.0, 2.0)) - 0.5;

    // Random window lighting pattern
    float lit = step(hash(floor(p.x) * 12.34 + floor(p.z) * 23.45 + floor(p.y) * 34.56 + u_time * 0.1), 0.6);

    // Window shape
    float windowShape = step(abs(windowGrid.x), windowSize) * 
                        step(abs(windowGrid.z), windowSize) * 
                        step(abs(windowGrid.y), 1.0);

    return windowShape * lit;
}

// Function to create a more varied grid of buildings
float cityGrid(vec3 p) {
    // Define the grid size
    float gridSize = 15.0;

    // Calculate grid cell position
    vec2 cell = floor(p.xz / gridSize);
    vec2 localPos = mod(p.xz, gridSize) - gridSize * 0.5;

    // Building size within cell - more variety
    float variety = hash(cell.x * 31.0 + cell.y * 57.0);
    float buildingSize = 5.0 + variety * 4.0;

    // Different building types
    float buildingType = hash(cell.x * 43.0 + cell.y * 37.0);

    // Random building height based on grid position
    float height = getBuildingHeight(cell);

    // Building position can be offset from center
    vec2 offset = (hash2(cell) - 0.5) * (gridSize - buildingSize) * 0.5;
    vec3 buildingPos = vec3(localPos.x - offset.x, p.y, localPos.y - offset.y);

    float building; 

    // Create different building types
    if (buildingType < 0.7) {
        // Standard rectangular building
        building = sdBox(buildingPos, vec3(buildingSize * 0.5, height * 0.5, buildingSize * 0.5));
    } else if (buildingType < 0.85) {
        // Cylindrical building
        building = sdCylinder(buildingPos, height * 0.5, buildingSize * 0.4);
    } else {
        // Rounded corners building
        building = sdRoundBox(buildingPos, vec3(buildingSize * 0.5, height * 0.5, buildingSize * 0.5), 1.0);
    }

    // Add some architectural details to larger buildings
    if (height > 15.0 && building < 1.0) {
        // Add setbacks for taller buildings
        float setbackHeight = height * 0.7;
        if (p.y > setbackHeight) {
            float setbackBuilding = sdBox(vec3(buildingPos.x, buildingPos.y - setbackHeight * 0.5, buildingPos.z), 
                                        vec3(buildingSize * 0.3, (height - setbackHeight) * 0.5, buildingSize * 0.3));
            building = max(building, -setbackBuilding);
        }
    }

    return building;
}

// Function to create roads with more detail
float roads(vec3 p) {
    // Roads along x and z axis
    float roadWidthX = 7.0;
    float roadWidthZ = 7.0;

    // Grid size should match the city grid
    float gridSize = 15.0;

    // Calculate distance to roads
    float distX = abs(mod(p.x, gridSize) - gridSize * 0.5) - roadWidthX * 0.5;
    float distZ = abs(mod(p.z, gridSize) - gridSize * 0.5) - roadWidthZ * 0.5;

    // Roads are where either distX or distZ is negative
    float roadNetwork = min(distX, distZ);

    // Road height (slightly above ground)
    float roadHeight = p.y - 0.01;

    // Road texture - slight unevenness
    float bumps = sin(p.x * 5.0) * sin(p.z * 5.0) * 0.02;
    roadHeight += bumps;

    return max(roadNetwork, roadHeight);
}

// Generate street lights with more detail
float streetLights(vec3 p) {
    // Grid size should match the city grid
    float gridSize = 15.0;

    // Position street lights at intersections
    vec2 intersection = mod(p.xz + gridSize * 0.5, gridSize) - gridSize * 0.5;
    float distToIntersection = length(intersection);

    // Only place lights at intersections
    if (distToIntersection > 0.5) return MAX_DIST;

    // Pole
    float pole = sdCylinder(vec3(intersection.x, p.y - 2.0, intersection.y), 4.0, 0.2);

    // Light fixture at the top
    float lightFixture = sdSphere(vec3(intersection.x, p.y - 6.0, intersection.y), 0.5);

    // Arm connecting pole to light
    vec3 armPos = vec3(intersection.x - 0.5, p.y - 5.5, intersection.y);
    float arm = sdCylinder(vec3(armPos.xy, armPos.z), 0.6, 0.1);

    // Combine elements
    return min(min(pole, lightFixture), arm);
}

// Add some street props (fire hydrants, trash cans)
float streetProps(vec3 p) {
    float gridSize = 15.0;

    // Calculate grid cell and local position
    vec2 cell = floor(p.xz / gridSize);
    vec2 localPos = mod(p.xz, gridSize) - gridSize * 0.5;

    float prop = MAX_DIST;

    // Place props along sidewalks
    float distFromCenter = abs(localPos.x) + abs(localPos.y);
    float propProb = hash(cell.x * 49.0 + cell.y * 39.0);

    if (distFromCenter > 6.0 && distFromCenter < 7.0 && propProb > 0.7) {
        // Fire hydrant or trash can
        if (hash(cell.x * 29.0 + cell.y * 47.0) > 0.5) {
            // Fire hydrant
            vec3 hydrantPos = vec3(p.x, p.y - 0.5, p.z);
            prop = min(prop, sdCylinder(hydrantPos, 0.5, 0.3));
            prop = min(prop, sdSphere(vec3(hydrantPos.x, hydrantPos.y - 0.7, hydrantPos.z), 0.4));
        } else {
            // Trash can
            vec3 trashPos = vec3(p.x, p.y - 0.7, p.z);
            prop = min(prop, sdCylinder(trashPos, 0.7, 0.4));
        }
    }

    return prop;
}

// Scene distance function
float getDist(vec3 p) {
    // Ground plane with slight unevenness
    float ground = p.y - sin(p.x * 0.05) * sin(p.z * 0.05) * 0.1;

    // Buildings
    float buildings = cityGrid(p);

    // Roads
    float roadNetwork = roads(p);

    // Street lights
    float lights = streetLights(p);

    // Street props
    float props = streetProps(p);

    // Combine all elements
    return min(min(min(min(ground, buildings), roadNetwork), lights), props);
}

// Calculate normal from gradient
vec3 getNormal(vec3 p) {
    float d = getDist(p);
    vec2 e = vec2(0.01, 0.0);

    vec3 n = d - vec3(
        getDist(p - e.xyy),
        getDist(p - e.yxy),
        getDist(p - e.yyx)
    );

    return normalize(n);
}

// Raymarch the scene
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDist(p);
        dO += dS;
        if (dS < SURF_DIST || dO > MAX_DIST) break;
    }

    return dO;
}

// AMBIENT OCCLUSION
float calculateAO(vec3 p, vec3 n) {
    float ao = 0.0;
    float weight = 1.0;

    for (float i = 1.0; i < 6.0; i += 1.0) {
        float dist = i * 0.5;
        float sampleDist = getDist(p + n * dist);
        ao += weight * (dist - sampleDist) / dist;
        weight *= 0.5;
    }

    return 1.0 - clamp(ao, 0.0, 1.0);
}



// Generate city lights
vec3 getCityLights(vec3 p, vec3 n, vec3 rd) {
    // Window colors with variety
    vec3 windowColor = mix(
        vec3(0.9, 0.7, 0.3),  // Warm yellow
        vec3(0.7, 0.9, 1.0),  // Cool blue-white
        hash(floor(p.x) + floor(p.z) * 10.0 + floor(p.y) * 100.0)
    );

    // Get grid cell for pattern variation
    vec2 cell = floor(p.xz / 15.0);

    // Window pattern
    float windowPattern = getWindowPattern(p, cell);

    // Street light color
    vec3 streetLightColor = vec3(1.0, 0.9, 0.7);

    // Check if we're on a street light
    float lights = streetLights(p);

    // Road markings - more detailed
    vec3 roadColor = vec3(0.2);
    float roadMarkingX = step(0.95, fract((p.x) / 3.0)) * step(abs(mod(p.z, 15.0) - 15.0 * 0.5), 0.3);
    float roadMarkingZ = step(0.95, fract((p.z) / 3.0)) * step(abs(mod(p.x, 15.0) - 15.0 * 0.5), 0.3);
    float roadMarking = max(roadMarkingX, roadMarkingZ);

    // Combine all light sources
    vec3 color = vec3(0.0);

    // Window lights with bloom effect
    if (windowPattern > 0.5) {
        color += windowColor * 2.0;

        // Add glow/bloom effect
        float glow = 0.2 / (0.1 + length(rd - reflect(-normalize(vec3(0.0, -1.0, 0.0)), n)));
        color += windowColor * glow;
    }

    // Street lights with bloom
    if (lights < SURF_DIST) {
        color += streetLightColor * 3.0;

        // Add glow
        float lightGlow = 0.5 / (0.1 + length(rd - reflect(-normalize(vec3(0.0, -1.0, 0.0)), n)));
        color += streetLightColor * lightGlow;
    }

    // Road markings
    if (p.y < 0.1 && roadMarking > 0.5) {
        color += vec3(1.0, 1.0, 0.5);
    }

    return color;
}

// Calculate material color based on position and normal
vec3 getMaterial(vec3 p, vec3 n, vec3 rd) {
    // Base material color
    vec3 material = vec3(0.2);

    // Building variations based on height
    if (p.y > 0.1) {
        // Get building height at this position
        float height = getBuildingHeight(floor(p.xz / 15.0));

        // Taller buildings have different materials
        if (height > 25.0) {
            // Glass skyscraper
            material = mix(vec3(0.2, 0.3, 0.4), vec3(0.3, 0.4, 0.5), n.y);

            // Reflections on glass
            vec3 r = reflect(rd, n);
            float skyReflection = pow(max(0.0, r.y), 1.0);
            material += vec3(skyReflection * 0.1);
        } else {
            // Standard building - vary color slightly by position
            float colorVar = hash(floor(p.x) * 13.0 + floor(p.z) * 17.0);
            material = mix(
                vec3(0.3, 0.3, 0.35), // Dark gray
                vec3(0.4, 0.35, 0.3),  // Brownish
                colorVar
            );
        }
    }
    // Ground and roads
    else {
        // Road surface
        float roadX = abs(mod(p.x, 15.0) - 15.0 * 0.5) - 3.5;
        float roadZ = abs(mod(p.z, 15.0) - 15.0 * 0.5) - 3.5;

        if (roadX < 0.0 || roadZ < 0.0) {
            // Asphalt texture
            float noiseVal = hash(p.x * 100.0 + p.z * 100.0) * 0.05;
            material = vec3(0.1, 0.1, 0.11) + noiseVal;
        } else {
            // Sidewalk
            material = vec3(0.3, 0.3, 0.28);

            // Sidewalk pattern
            float sideWalkPattern = step(0.85, fract(p.x + p.z));
            material = mix(material, material * 0.9, sideWalkPattern);
        }
    }

    return material;
}

// Apply fog with atmospheric scattering
vec3 applyFog(vec3 color, vec3 rayDir, float distance) {
    // Base fog color
    vec3 fogColor = mix(
        vec3(0.05, 0.05, 0.1),  // Dark blue at horizon
        vec3(0.01, 0.01, 0.03), // Darker blue higher up
        rayDir.y * 0.5 + 0.5
    );

    // Time-based fog density for atmosphere changes
    float fogDensity = 0.008 + sin(u_time * 0.05) * 0.002;

    // Distance-based fog
    float fogAmount = 1.0 - exp(-distance * fogDensity);

    // Height-based fog (less dense higher up)
    fogAmount *= mix(1.0, 0.5, max(0.0, rayDir.y));

    return mix(color, fogColor, fogAmount);
}

// Calculate sky color
vec3 getSkyColor(vec3 rd) {
    // Sky gradient
    vec3 skyColor = mix(
        vec3(0.05, 0.05, 0.1),  // Dark blue at horizon
        vec3(0.01, 0.01, 0.03), // Darker blue at zenith
        rd.y * 0.5 + 0.5
    );

    // Add some stars
    if (rd.y > 0.1) {
        vec3 p = normalize(rd) * 100.0;
        float stars = smoothstep(0.95, 1.0, hash(p.x * 45.34 + p.y * 67.21 + p.z * 97.12));
        skyColor = mix(skyColor, vec3(1.0), stars * 0.5);
    }

    // Add a subtle moon
    vec3 moonDir = normalize(vec3(0.0, 0.6, -1.0));
    float moonDot = max(0.0, dot(rd, moonDir));
    float moon = smoothstep(0.99, 0.995, moonDot);

    // Moon glow
    float moonGlow = pow(moonDot, 32.0) * 0.3;

    // Add moon and glow to sky
    skyColor += vec3(1.0, 0.98, 0.9) * (moon + moonGlow);

    return skyColor;
}

void main() {
    // Normalized screen coordinates
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution) / min(u_resolution.x, u_resolution.y);

    // Add subtle vignette effect
    float vignette = 1.0 - smoothstep(0.4, 1.4, length(uv));

    // Camera setup with smoother movement
    float camRadius = 60.0;
    float camHeight = 35.0 + sin(u_time * 0.0) * 0.0;
    float camSpeed = 0.11;
    vec3 ro = vec3(camRadius * sin(u_time * camSpeed), camHeight, camRadius * cos(u_time * camSpeed));
    vec3 lookAt = vec3(0.0, 5.0, 0.0);

    // Camera frame
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);

    // Ray direction with adjustable field of view
    float fov = 0.8;
    vec3 rd = normalize(forward + fov * uv.x * right + fov * uv.y * up);

    // Add chromatic aberration
    float chromaticStrength = 0.003;
    vec3 rdR = normalize(forward + fov * (uv.x + chromaticStrength) * right + fov * uv.y * up);
    vec3 rdB = normalize(forward + fov * (uv.x - chromaticStrength) * right + fov * uv.y * up);

    // Raymarch the scene with RGB separation for chromatic aberration
    float d = rayMarch(ro, rd);
    float dR = rayMarch(ro, rdR);
    float dB = rayMarch(ro, rdB);

    // Initialize color
    vec3 col = vec3(0.0);

    // If we hit something
    if (d < MAX_DIST) {
        // Calculate hit position and normal
        vec3 p = ro + rd * d;
        vec3 n = getNormal(p);

        // Calculate ambient occlusion
        float ao = calculateAO(p, n);

        // Light direction
        vec3 lightDir = normalize(vec3(0.5, 1.0, 0.3));


        // Get base material
        vec3 material = getMaterial(p, n, rd);

        // Basic lighting
        float diffuse = max(0.0, dot(n, lightDir));
        float ambient = 0.2;

        // Combine lighting
        vec3 lighting = material * (ambient + diffuse) * ao;

        // Add city lights
        vec3 lights = getCityLights(p, n, rd);
        col = lighting + lights;

        // Apply atmospheric fog
        col = applyFog(col, rd, d);
    } else {
        // Sky color with chromatic aberration
        vec3 skyR = getSkyColor(rdR);
        vec3 skyG = getSkyColor(rd);
        vec3 skyB = getSkyColor(rdB);

        col = vec3(skyR.r, skyG.g, skyB.b);
    }

    // Apply vignette
    col *= vignette;

    // Gamma correction
    col = pow(col, vec3(0.45));

    // Subtle film grain
    float grain = hash(uv.x * u_time + uv.y * 42.0) * 0.05;
    col += grain - 0.025;

    // Output the final color
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
// Created by Max Warren :)
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;
#define PI 3.14159
float hash(vec2 p) {
    p = fract(p * vec2(123.34, 345.45));
    p += dot(p, p + 34.45);
    return fract(p.x * p.y);
}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);
    return mix(
        mix(hash(i), hash(i + vec2(1.0, 0.0)), f.x),
        mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), f.x),
        f.y
    );
}
float roundedBox(vec3 p, vec3 b, float r) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) - r;
}
float citySDF(vec3 p, out float height, out vec2 cell) {
    cell = floor(p.xz / 6.0);
    vec2 local = mod(p.xz, 6.0) - 3.0;
    float h = hash(cell) * 20.0 + 5.0;
    height = h;
    float taper = 1.0 - smoothstep(0.0, h, p.y);
    float base = roundedBox(vec3(local.x, p.y - h / 2.0, local.y), vec3(2.0, h / 2.0, 2.0) * taper, 0.3);
    return base;
}
float raymarch(vec3 ro, vec3 rd, out vec3 p, out float height, out vec2 cell) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        p = ro + t * rd;
        float d = citySDF(p, height, cell);
        if (d < 0.001 || t > 100.0) break;
        t += d * 0.5;
    }
    return t;
}
vec3 getNormal(vec3 p, float height, vec2 cell) {
    float e = 0.01;
    vec3 n;
    vec3 dx = vec3(e, 0.0, 0.0);
    vec3 dy = vec3(0.0, e, 0.0);
    vec3 dz = vec3(0.0, 0.0, e);
    vec3 dummy;
    vec2 dummy2;
    n.x = citySDF(p + dx, height, cell) - citySDF(p - dx, height, cell);
    n.y = citySDF(p + dy, height, cell) - citySDF(p - dy, height, cell);
    n.z = citySDF(p + dz, height, cell) - citySDF(p - dz, height, cell);
    return normalize(n);
}
vec3 applyFog(vec3 color, float dist) {
    float fogAmount = 1.0 - exp(-dist * 0.02);
    vec3 fogColor = vec3(0.0, 0.05, 0.1) + vec3(0.02, 0.05, 0.2) * sin(u_time * 0.5);
    return mix(color, fogColor, fogAmount);
}
vec3 getBuildingColor(vec3 p, float h, vec2 cell) {
    float flicker = noise(vec2(p.x * 5.0, p.y * 15.0 + u_time * 3.0));
    float window = step(0.8, flicker) * step(0.3, sin(p.y * 10.0 + u_time)) * 0.8;
    vec3 baseColor = mix(vec3(0.1, 0.1, 0.2), vec3(0.0, 0.5, 1.0), window);
    float pulse = sin(u_time * 5.0 + hash(cell) * 10.0) * 0.5 + 0.5;
    baseColor += pulse * 0.05 * vec3(1.0, 0.1, 0.8);
    return baseColor;
}

// Rain effect
float rain(vec2 uv) {
    return step(0.99, fract(uv.y * 50.0 - u_time * 30.0));
}
void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution.xy) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    // Camera
    vec3 ro = vec3(sin(u_time * 0.1) * 20.0, 20.0 + sin(u_time * 0.5) * 2.0, u_time * 5.0);
    vec3 lookAt = ro + vec3(0.0, -2.0, 5.0);
    vec3 f = normalize(lookAt - ro);
    vec3 r = normalize(cross(vec3(0.0, 1.0, 0.0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(uv.x * r + uv.y * u + f);
    vec3 p;
    float height;
    vec2 cell;
    float t = raymarch(ro, rd, p, height, cell);
    vec3 col = vec3(0.0);
    if (t < 100.0) {
        vec3 n = getNormal(p, height, cell);
        vec3 lightDir = normalize(vec3(0.3, 1.0, 0.2));
        float diff = max(0.0, dot(n, lightDir));
        col = getBuildingColor(p, height, cell);
        col += diff * vec3(0.1, 0.4, 0.7);
        col = applyFog(col, t);
    } else {
        col = applyFog(vec3(0.0, 0.05, 0.1), 100.0);
    }

    // scanlines
    float scan = 0.02 * sin(gl_FragCoord.y * 20.0 + u_time * 30.0);
    col += scan;
    col += rain(uv) * vec3(0.5, 0.7, 1.0) * 0.2;

    gl_FragColor = vec4(col, 1.0);
}

