frag:
//penthouse - MaxWarren

precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}
float noise2d(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);
    vec2 u = f * f * (3.0 - 2.0 * f);
    float n00 = hash(i + vec2(0.0, 0.0));
    float n10 = hash(i + vec2(1.0, 0.0));
    float n01 = hash(i + vec2(0.0, 1.0));
    float n11 = hash(i + vec2(1.0, 1.0));
    return mix(mix(n00, n10, u.x),
               mix(n01, n11, u.x),
               u.y);
}
float fbm(vec2 x) {
    float sum = 0.0;
    float amp = 0.5;
    mat2 rot = mat2( cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.5) );
    for(int i = 0; i < 5; i++){
        sum += amp * noise2d(x);
        x = rot * x * 2.0;
        amp *= 0.5;
    }
    return sum;
}
vec3 marbleColor(vec3 p) {
    float m = p.z * 2.0 + 0.5 * noise2d(p.xz * 3.0);
    float veins = smoothstep(0.4, 0.5, sin(m * 10.0));
    return mix(vec3(0.95), vec3(0.75), veins);
}
vec3 wallMaterial(vec3 p) {
    vec2 uv = p.xz * 4.0;
    float t = fbm(uv + u_time * 0.1);
    float bump = fbm(uv * 8.0) * 0.1;
    vec3 baseCol = mix(vec3(0.85,0.80,0.75), vec3(0.95,0.92,0.90), t);
    return baseCol + bump;
}
vec3 backgroundScene(vec3 rd) {
    float skyT = clamp((rd.y + 0.2) * 0.8, 0.0, 1.0);
    vec3 sky = mix(vec3(0.2, 0.1, 0.4), vec3(0.8, 0.6, 0.9), skyT);
//building height
    float t = u_time * 0.02;
    float bNoise = noise2d(vec2(rd.x * 1.5 + t, 0.0));
    float buildingTop = mix(-0.1, 0.3, bNoise); // height between ground and mid-sky

    if (rd.y < buildingTop) {
        // 3) Facade base color
        vec3 facade = vec3(0.1, 0.1, 0.15);

        // 4) Window grid: tile in X and Y
        vec2 gridUV;
        gridUV.x = fract(rd.x * 20.0);
        gridUV.y = fract((rd.y - buildingTop) * -50.0);

        // 5) Define window “on” region
        float winX = step(0.1, gridUV.x) * step(gridUV.x, 0.9);
        float winY = step(0.1, gridUV.y) * step(gridUV.y, 0.9);
        float windowMask = winX * winY;

        // 6) Window flicker / variation
        float flick = step(0.5, fract(bNoise * 10.0 + floor(rd.x*10.0)));
        vec3 windowCol = vec3(1.0, 0.9, 0.6) * flick;

        return mix(facade, windowCol, windowMask);
    }

    // else above skyline → sky
    return sky;
}

// Primitive SDFs
float sdPlane(vec3 p, vec3 n, float h) { return dot(p,n) + h; }
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float sdCylinder(vec3 p, vec2 h) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - h;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// Scene SDF
float sceneSDF(vec3 p) {
    float dFloor = sdPlane(p, vec3(0,1,0), 1.5);
    float dCeil  = sdPlane(p, vec3(0,-1,0), 3.0);
    float wallL = sdBox(p - vec3(-5.0,0.0,0.0), vec3(0.5,3.0,10.0));
    float wallR = sdBox(p - vec3( 5.0,0.0,0.0), vec3(0.5,3.0,10.0));
    float wallB = sdBox(p - vec3(0.0,0.0,-5.0), vec3(5.0,3.0,0.5));

    // front wall with arch “window”
    float wF    = sdBox(p - vec3(0.0,0.0, 5.0), vec3(5.0,3.0,0.5));
    vec3 archP  = p - vec3(0.0,-1.0,5.0);
    float arch  = max(sdBox(archP, vec3(2.0,1.5,1.0)),
                      -sdCylinder(archP, vec2(2.0,0.5)));
    float wallF = min(wF, arch);

    // columns
    float cols = 1e5;
    for(int i=-3; i<=3; i+=2) {
        vec3 cpL = p - vec3(-5.0, -1.5, float(i)*2.0);
        vec3 cpR = p - vec3( 5.0, -1.5, float(i)*2.0);
        cols = min(cols, sdCylinder(cpL, vec2(0.3,3.0)));
        cols = min(cols, sdCylinder(cpR, vec2(0.3,3.0)));
    }
    vec3 glowPos = vec3(sin(u_time)*2.0,
                        1.0 + sin(u_time*1.3)*0.5,
                        cos(u_time)*2.0);
    float dGlow = length(p - glowPos) - 0.5;

    float palace = min(min(min(wallL, wallR), min(wallB, wallF)),
                       min(dFloor, min(dCeil, cols)));
    return min(palace, dGlow);
}
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<100; i++) {
        float d = sceneSDF(ro + rd * t);
        if(d < 0.001 || t > 50.0) break;
        t += d;
    }
    return t;
}
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p+vec3(e,0,0)) - sceneSDF(p-vec3(e,0,0)),
        sceneSDF(p+vec3(0,e,0)) - sceneSDF(p-vec3(0,e,0)),
        sceneSDF(p+vec3(0,0,e)) - sceneSDF(p-vec3(0,0,e))
    ));
}
vec3 shadeNR(vec3 p, vec3 rd, vec3 n, vec3 lightPos, vec3 glowPos) {
    float diff = max(dot(n, normalize(lightPos - p)), 0.0);
    vec3 amb = vec3(0.15);
    vec3 mat;
    float glowDist = length(p - glowPos);
    if (glowDist < 0.6) {
        mat  = vec3(1.0, 0.8, 0.2);
        diff = 1.0;
        amb  = vec3(0.5);
    }
    else if (p.y < -1.49) {
        float ck = mod(floor(p.x) + floor(p.z), 2.0);
        mat = mix(vec3(0.9), vec3(0.8), ck * 0.2);
    }
    else {
        float dL = abs(sdBox(p - vec3(-5.0,0.0,0.0), vec3(0.5,3.0,10.0)));
        float dR = abs(sdBox(p - vec3( 5.0,0.0,0.0), vec3(0.5,3.0,10.0)));
        if (min(dL, dR) < 0.05) {
            mat = wallMaterial(p);
        } else {
            mat = marbleColor(p * 0.5);
        }
    }
    return mat * (amb + diff);
}
vec3 shade(vec3 ro, vec3 rd, vec3 p, vec3 n, vec3 lightPos, vec3 glowPos) {
    vec3 base = shadeNR(p, rd, n, lightPos, glowPos);
    float reflectivity = (p.y < -1.49) ? 0.8 : 0.2;
    vec3 reflCol = vec3(0.0);
    if(reflectivity > 0.01) {
        vec3 reflDir = reflect(rd, n);
        float t2 = raymarch(p + n * 0.01, reflDir);
        if(t2 < 50.0) {
            vec3 pr = (p + n * 0.01) + reflDir * t2;
            vec3 nr = getNormal(pr);
            reflCol = shadeNR(pr, reflDir, nr, lightPos, glowPos);
        }
    }
    return mix(base, reflCol, reflectivity);
}
void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution)*2.0 - 1.0;
    uv.x *= u_resolution.x/u_resolution.y;
    float ang = u_time * 0.1;
    vec3 ro = vec3(cos(ang)*12.0, 2.0, sin(ang)*12.0);
    vec3 target = vec3(0.0);
    vec3 fwd = normalize(target - ro);
    vec3 rgt = normalize(cross(vec3(0,1,0), fwd));
    vec3 upv = cross(fwd, rgt);
    vec3 rd  = normalize(fwd + uv.x*rgt + uv.y*upv*0.5);
    vec3 glowPos  = vec3(sin(u_time)*2.0,
                         1.0 + sin(u_time*1.3)*0.5,
                         cos(u_time)*2.0);
    vec3 lightPos = vec3(5.0, 8.0, -5.0);
    float t = raymarch(ro, rd);
    vec3 col;
    if (t < 50.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        col = shade(ro, rd, p, n, lightPos, glowPos);
        col = mix(col, vec3(0.8,0.9,1.0),
                  1.0 - exp(-0.03 * t));
    } else {
        col = backgroundScene(rd);
    }
    gl_FragColor = vec4(col,1.0);
}

precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f *= f * (3.0 - 2.0 * f);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float sceneSDF(vec3 p) {
    float d = p.y + 1.0; // Ground

    vec2 grid = floor(p.xz / 5.0);
    vec2 rp = mod(p.xz + 2.5, 5.0) - 2.5;

    // Building height based on noise
    float h = mix(8.0, 20.0, noise(grid * 0.3 + u_time * 0.02));
    vec3 blockPos = vec3(rp.x, p.y - h * 0.5, rp.y);
    float building = boxSDF(blockPos, vec3(1.5, h * 0.5, 1.5));

    // Street-level glowing pipes
    float trace = 1e5;
    if (abs(rp.x) < 0.05 || abs(rp.y) < 0.05) {
        float pulse = 0.1 + 0.05 * sin(u_time * 10.0 + grid.x * 4.0 + grid.y * 3.0);
        trace = boxSDF(vec3(rp.x, p.y - 0.05, rp.y), vec3(0.03 + pulse, 0.05, 2.0));
    }

    return min(d, min(building, trace));
}

vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}

vec3 shade(vec3 p, vec3 n) {
    vec3 base = vec3(0.01, 0.01, 0.02);
    vec3 lightDir = normalize(vec3(0.5, 1.0, 0.3));
    float diff = max(dot(n, lightDir), 0.0);

    // City glow from windows
    float windowGrid = step(0.8, fract(p.y * 4.0)) * step(0.8, fract(p.x * 2.0));
    float flicker = noise(p.xz * 10.0 + u_time * 2.0);
    vec3 windowLight = mix(vec3(1.0, 0.2, 0.6), vec3(0.0, 1.0, 1.0), fract(p.x + p.z)) * windowGrid * flicker;

    // Neon ground traces
    float groundGlow = smoothstep(0.3, 0.6, sin(p.z * 3.0 + u_time * 5.0));
    vec3 traceColor = vec3(0.0, 1.0, 0.6) * groundGlow;

    return base + diff * 0.1 + windowLight * 0.7 + traceColor;
}

float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if (d < 0.001 || t > 200.0) break;
        t += d * 0.6;
    }
    return t;
}

void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Flying city camera
    vec3 ro = vec3(sin(u_time * 0.15) * 30.0, 20.0 + sin(u_time * 0.5) * 2.0, u_time * 2.0);
    vec3 target = vec3(0.0, 5.0, ro.z + 20.0);
    vec3 f = normalize(target - ro);
    vec3 r = normalize(cross(vec3(0, 1, 0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(uv.x * r + uv.y * u + 1.5 * f);

    float t = raymarch(ro, rd);
    vec3 col;

    if (t < 200.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        col = shade(p, n);
    } else {
        col = mix(vec3(0.01, 0.02, 0.03), vec3(0.0, 0.0, 0.1), uv.y + 0.5); // night sky gradient
    }

    // Fog for city depth
    float fog = 1.0 - exp(-t * 0.03);
    col = mix(col, vec3(0.0, 0.02, 0.03), fog);

    gl_FragColor = vec4(pow(col, vec3(0.4545)), 1.0);
}

precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

// Basic hash & noise
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f*f*(3.0 - 2.0*f);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float sceneSDF(vec3 p) {
    float d = p.y + 0.5;

    // Grid of "chip modules"
    vec2 grid = floor(p.xz / 4.0);
    vec2 rp = mod(p.xz + 2.0, 4.0) - 2.0;

    float n = noise(grid * 0.3 + u_time * 0.03);
    float h = mix(0.5, 3.0, n); // Lower "building" height
    vec3 cp = vec3(rp.x, p.y - h * 0.5, rp.y);

    float chipBlock = boxSDF(cp, vec3(1.5, h, 1.5));

    // Add "trace lines" between blocks
    float traceLine = 1e3;
    if (abs(rp.x) < 0.1 || abs(rp.y) < 0.1) {
        traceLine = boxSDF(vec3(rp.x, p.y - 0.25, rp.y), vec3(0.05, 0.25, 2.0));
    }

    return min(d, min(chipBlock, traceLine));
}

vec3 getNormal(vec3 p) {
    float e = 0.002;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

vec3 shade(vec3 p, vec3 n, float height) {
    vec3 ambient = vec3(0.03, 0.05, 0.04);
    vec3 glow = vec3(0.0, 1.0, 0.6); // Circuit-glow
    vec3 lightDir = normalize(vec3(0.6, 1.0, 0.8));
    float diff = max(dot(n, lightDir), 0.0);

    // Simulated "trace glow" lines
    float linePattern = step(0.7, noise(floor(p.xz * 4.0)));
    glow *= linePattern;

    // Edge glow
    float edge = smoothstep(height * 0.6, height, p.y + 0.2);
    vec3 color = ambient + diff * 0.1 + glow * (0.3 + edge * 0.7);

    return color;
}

float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<80; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if(d < 0.001 || t > 100.0) break;
        t += d * 0.7;
    }
    return t;
}

void main(){
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Camera
    vec3 ro = vec3(sin(u_time * 0.05) * 8.0, 12.0, u_time * 5.0);
    vec3 look = vec3(0.0, 0.0, ro.z + 10.0);
    vec3 f = normalize(look - ro);
    vec3 r = normalize(cross(vec3(0,1,0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(uv.x * r + uv.y * u + 1.5 * f);

    float t = raymarch(ro, rd);
    vec3 col;
    if(t < 100.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        float h = mix(0.5, 3.0, noise(floor(p.xz / 4.0) * 0.3 + u_time * 0.03));
        col = shade(p, n, h);
    } else {
        col = mix(vec3(0.01, 0.02, 0.03), vec3(0.0, 0.2, 0.15), pow(max(rd.y, 0.0), 0.5));
    }

    // Slight fog
    float fog = 1.0 - exp(-t * 0.02);
    col = mix(col, vec3(0.0, 0.05, 0.02), fog);

    col = pow(col, vec3(0.4545)); // Gamma correction
    gl_FragColor = vec4(col, 1.0);
}

precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f *= f * (3.0 - 2.0 * f);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float sceneSDF(vec3 p) {
    float d = p.y + 1.0; // Ground

    vec2 grid = floor(p.xz / 5.0);
    vec2 rp = mod(p.xz + 2.5, 5.0) - 2.5;

    // Building height based on noise
    float h = mix(1.0, 14.0, noise(grid * 0.3 + u_time * 0.02));
    vec3 blockPos = vec3(rp.x, p.y - h * 0.5, rp.y);
    float building = boxSDF(blockPos, vec3(1.5, h * 0.5, 1.5));

    // Street-level glowing pipes
    float trace = 1e5;
    if (abs(rp.x) < 0.05 || abs(rp.y) < 0.05) {
        float pulse = 0.1 + 0.05 * sin(u_time * 10.0 + grid.x * 4.0 + grid.y * 3.0);
        trace = boxSDF(vec3(rp.x, p.y - 0.05, rp.y), vec3(0.03 + pulse, 0.05, 2.0));
    }

    // City edge lights (near horizon) 
    float edgeGlow = smoothstep(0.3, 0.5, p.y - 5.0);
    float cityEdgeGlow = edgeGlow * 0.8;

    return min(d, min(building, trace)) - cityEdgeGlow;
}

vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}

vec3 shade(vec3 p, vec3 n) {
    vec3 base = vec3(0.01, 0.01, 0.02);
    vec3 lightDir = normalize(vec3(0.5, 1.0, 0.3));
    float diff = max(dot(n, lightDir), 0.0);

    // City glow from windows
    float windowGrid = step(0.8, fract(p.y * 4.0)) * step(0.8, fract(p.x * 2.0));
    float flicker = noise(p.xz * 10.0 + u_time * 2.0);
    vec3 windowLight = mix(vec3(1.0, 0.2, 0.6), vec3(0.0, 1.0, 1.0), fract(p.x + p.z)) * windowGrid * flicker;

    // Neon ground traces
    float groundGlow = smoothstep(0.3, 0.6, sin(p.z * 3.0 + u_time * 5.0));
    vec3 traceColor = vec3(0.0, 1.0, 0.6) * groundGlow;

    // Soft reflections
    vec3 reflectedColor = mix(vec3(0.02, 0.01, 0.02), vec3(1.0, 1.0, 0.5), smoothstep(0.3, 0.5, p.y)); // Simulate reflective surface
    reflectedColor *= max(0.0, dot(n, normalize(vec3(0.2, 0.8, 0.5)))) * 0.5;

    return base + diff * 0.1 + windowLight * 0.7 + traceColor + reflectedColor;
}

float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
