precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f *= f * (3.0 - 2.0 * f);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

// Cyberpunk module grid
float sceneSDF(vec3 p) {
    float d = p.y + 1.0;

    vec2 grid = floor(p.xz / 4.0);
    vec2 rp = mod(p.xz + 2.0, 4.0) - 2.0;

    // Random heights and sizes per cell
    float h = 0.4 + 0.6 * hash(grid * 1.23);
    float w = 1.0 + 0.5 * hash(grid * 0.73);

    vec3 blockPos = vec3(rp.x, p.y - h * 0.5, rp.y);
    float block = boxSDF(blockPos, vec3(w, h, w));

    // Animated pulse traces
    float trace = 1e5;
    float lineX = step(abs(rp.x), 0.05);
    float lineZ = step(abs(rp.y), 0.05);
    float pulse = 0.1 + 0.1 * sin(u_time * 10.0 + grid.x * 3.0 + grid.y * 4.0);
    if (lineX > 0.0 || lineZ > 0.0) {
        trace = boxSDF(vec3(rp.x, p.y - 0.05, rp.y), vec3(0.02 + pulse, 0.05, 2.0));
    }

    return min(d, min(block, trace));
}

vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}

vec3 shade(vec3 p, vec3 n) {
    vec3 baseColor = vec3(0.02, 0.01, 0.03); // deep base
    vec3 neonBlue = vec3(0.0, 1.0, 0.9);
    vec3 neonPink = vec3(1.0, 0.2, 0.9);

    float diff = max(dot(n, normalize(vec3(1.0, 1.0, 0.5))), 0.0);

    // Emissive pulse along traces
    float pulse = sin(p.z * 5.0 + u_time * 10.0);
    float glow = smoothstep(0.2, 0.5, pulse);
    vec3 traceGlow = mix(neonBlue, neonPink, sin(u_time + p.x * 0.5) * 0.5 + 0.5);

    // Glowing panels (pseudo tech glyphs)
    float panelGlow = smoothstep(0.8, 1.0, noise(p.xz * 4.0 + u_time * 0.5));
    vec3 glyph = panelGlow * neonPink * 0.2;

    vec3 col = baseColor + diff * 0.2 + glow * traceGlow * 0.5 + glyph;
    return col;
}

float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if (d < 0.001 || t > 100.0) break;
        t += d * 0.6;
    }
    return t;
}

