    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Cyber-cinematic camera
    vec3 ro = vec3(sin(u_time * 0.2) * 10.0, 10.0 + sin(u_time) * 1.5, u_time * 3.0);
    vec3 target = vec3(0.0, 0.0, ro.z + 8.0);
    vec3 f = normalize(target - ro);
    vec3 r = normalize(cross(vec3(0, 1, 0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(uv.x * r + uv.y * u + 1.2 * f);

    float t = raymarch(ro, rd);
    vec3 col;

    if (t < 100.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        col = shade(p, n);
    } else {
        col = vec3(0.01, 0.02, 0.03); // night sky
    }

    // Fog glow
    float fog = 1.0 - exp(-t * 0.025);
    col = mix(col, vec3(0.0, 0.0, 0.0), fog);

    gl_FragColor = vec4(pow(col, vec3(0.4545)), 1.0); // gamma correction
}

