#ifdef GL_ES
precision highp float;
#endif

// uniforms you must set from your host (e.g. WebGL)...
uniform vec2 u_resolution;
uniform float u_time;

// signed distance to an axis‑aligned box of radius b
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)), 0.0);
}

// build a repeating fractal of cubes by iterating mod+scale
float mapFractal(vec3 p) {
    float d = 1e10;
    float scale = 3.0;
    // 5–6 iterations give plenty of detail
    for(int i = 0; i < 2; i++) {
        // tile space into cubes of size 'scale'
        vec3 q = mod(p, scale) - 0.5 * scale;
        // distance to cube of half‑size 1.0 in that cell
        d = min(d, sdBox(q, vec3(0.5)) / pow(scale, float(i)));
        p *= scale;
    }
    return d;
}

// central distance field
float mapScene(vec3 p) {
    return mapFractal(p);
}

// approximate normal by gradient
vec3 calcNormal(vec3 p) {
    float eps = 1e-3;
    return normalize(vec3(
        mapScene(p + vec3(eps,0,0)) - mapScene(p - vec3(eps,0,0)),
        mapScene(p + vec3(0,eps,0)) - mapScene(p - vec3(0,eps,0)),
        mapScene(p + vec3(0,0,eps)) - mapScene(p - vec3(0,0,eps))
    ));
}

// classic sphere‑tracing
float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < 100; i++) {
        float d = mapScene(ro + rd * t);
        if(d < 0.001) break;
        t += d;
        if(t > 50.0) break;
    }
    return t;
}

void main() {
    // normalized pixel coords (–1..+1)
    vec2 uv = (gl_FragCoord.xy - 0.5*u_resolution.xy) / u_resolution.y;

    // simple orbiting camera
    float ang = u_time * 0.1;
    vec3 ro = vec3(8.0 * sin(ang), 3.0, 4.0 * cos(ang));
    vec3 ta = vec3(0.0);

    // build camera basis
    vec3 fw = normalize(ta - ro);
    vec3 rt = normalize(cross(fw, vec3(0.0,1.0,0.0)));
    vec3 up = cross(rt, fw);

    // shoot ray
    vec3 rd = normalize(uv.x*rt + uv.y*up + 1.5*fw);
    float t = rayMarch(ro, rd);

    vec3 col = vec3(0.0);
    if(t < 50.0) {
        vec3 pos = ro + rd * t;
        vec3 n = calcNormal(pos);

        // simple directional light
        vec3 lightDir = normalize(vec3(1.0,1.0,0.6));
        float diff = max(dot(n, lightDir), 0.0);

        // ambient occlusion hint
        float ao = 1.0;
        for(int i=1; i<=5; i++){
            float h = 0.02 * float(i);
            ao -= (h - mapScene(pos + n*h)) * 0.2;
        }

        col = vec3(diff) * ao;
    }

    // gamma‑correct
    col = pow(col, vec3(0.4545));

    gl_FragColor = vec4(col,1.0);
}

