// Simple hash & smoother noise
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f*f*(3.0-2.0*f);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

// Box SDF for buildings
float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

// Scene definition: simple grid of buildings
float sceneSDF(vec3 p) {
    // ground
    float d = p.y + 1.0;

    // grid cell coords
    vec2 cell = floor(p.xz / 8.0);
    vec2 rp = mod(p.xz, 2.5) - 4.0;

    // height noise
    float n = noise(cell * 0.5 + u_time * 0.05);
    float h = mix(10.0, 25.0, n);
    vec3 cp = vec3(rp.x, p.y - h*0.5, rp.y);

    // building shape
    float bld = boxSDF(cp, vec3(2.0 + n*1.0, h*1.5, 2.0 + n*1.0));

    // occasional antenna
    float ant = 1e3;
    if(n > 0.6) {
        ant = boxSDF(vec3(rp.x, p.y - (h + 2.0), rp.y), vec3(0.1, 2.0, 0.1));
    }

    return min(d, min(bld, ant));
}

// Estimate normal
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// Basic shading: ambient + single neon color
vec3 shade(vec3 p, vec3 n, float height) {
    vec3 ambient = vec3(0.02);
    vec3 sun = vec3(0.3, 0.3, 0.35);
    vec3 sunDir = normalize(vec3(1.5, 0.2, 0.5));
    float diff = max(dot(n, sunDir), 0.0);

    // neon accent (blue & pink)
    float win = step(0.5, noise(floor(p.xz*6.0)));
    vec3 neonBase = mix(vec3(0.0, 0.8, 1.0), vec3(1.0, 0.1, 0.8), fract(p.y * 0.5 + u_time));

    vec3 col = ambient + sun * diff;
    col += neonBase * win * 0.7;

    // top-floor glow
    float topGlow = smoothstep(height*0.7, height, p.y + 1.0);
    col += neonBase * topGlow * 0.5;

    return col;
}

// Raymarching loop
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<80; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if(d < 0.001 || t > 120.0) break;
        t += d * 0.8;
    }
    return t;
}

void main(){
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // camera setup
    vec3 ro = vec3(sin(u_time*0.1)*10.0, 68.0 + sin(u_time*1.8)*0.1, u_time*10.2);
    vec3 look = vec3(0.0, 5.0, ro.z + 20.0);
    vec3 f = normalize(look - ro);
    vec3 r = normalize(cross(vec3(0,1,0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(uv.x*r + uv.y*u + 1.5*f);

    float t = raymarch(ro, rd);
    vec3 col;
    if(t < 120.0) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);
        float height = mix(10.0,25.0, noise(floor(p.xz/8.0)*0.5 + u_time*0.05));
        col = shade(p, n, height);
    } else {
        // simple sky gradient
        float y = pow(max(rd.y, 0.0), 0.5);
        col = mix(vec3(0.02,0.05,0.1), vec3(0.0,0.3,0.5), y);
    }

    // subtle fog
    float fog = 1.0 - exp(-t * 0.015);
    col = mix(col, vec3(0.0,0.05,0.1), fog);

    // final tone mapping
    col = pow(col, vec3(0.4545));
    gl_FragColor = vec4(col,1.0);
}

