float sphereGrid(vec3 p) {
    p = repeat(p - vec3(0.0, 1.0, 3.0), 2.5); // grid spacing
    float r = 0.8 + 0.2 * sin(u_time * 3.0 + length(p.xy));
    return length(p) - r;
}

// SDF: Ground plane
float ground(vec3 p) {
    return p.y;
}

// Combined scene
float scene(vec3 p) {
    return min(sphereGrid(p), ground(p));
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        scene(p + e.xyy) - scene(p - e.xyy),
        scene(p + e.yxy) - scene(p - e.yxy),
        scene(p + e.yyx) - scene(p - e.yyx)
    ));
}

float march(vec3 ro, vec3 rd, out vec3 p) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        p = ro + rd * t;
        float d = scene(p);
        if (d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for (int i = 0; i < 50; i++) {
        float h = scene(ro + rd * t);
        if (h < 0.001) return 0.0;
        res = min(res, 8.0 * h / t);
        t += h;
        if (t > 5.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // Camera orbit
    float angle = u_time * 0.3;
    vec3 ro = vec3(8.0 * sin(angle), 3.0, 8.0 * cos(angle));
    vec3 target = vec3(0.0, 1.0, 3.0);
    vec3 f = normalize(target - ro);
    vec3 r = normalize(cross(vec3(0.0, 1.0, 0.0), f));
