    float streetGap = length(fract(p.xz / 20.0) - 0.5) - 0.4;
    float streets = streetGap * 10.0;
    float city = min(building, streets);
    return min(city, ground);
}
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < 100; i++) {
        vec3 pos = ro + rd * t;
        float d = sceneSDF(pos);
        if(d < 0.001 || t > 100.0) break;
        t += d;
    }
    return t;
}
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for(int i = 0; i < 50; i++) {
        float h = sceneSDF(ro + rd * t);
        if(h < 0.001) return 0.0;
        res = min(res, 8.0 * h / t);
        t += clamp(h, 0.02, 0.2);
        if(t > 50.0) break;
    }
    return clamp(res, 0.0, 1.0);
}
float ambientOcclusion(vec3 p, vec3 n) {
    float ao = 0.0;
    float sca = 1.0;
    for(int i = 1; i <= 5; i++) {
        float h = float(i) * 0.1;
        float d = sceneSDF(p + n * h);
        ao += (h - d) * sca;
        sca *= 0.7;
    }
    return clamp(1.0 - ao, 0.0, 1.0);
}
void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    vec3 ro = vec3(0.0, 10.0, -20.0);
    vec3 ta = vec3(0.0, 0.0, 20.0);
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(cw, vec3(0,1,0)));
    vec3 cv = normalize(cross(cu, cw));
    vec3 rd = normalize(uv.x * cu + uv.y * cv + 1.2 * cw);
    float t = raymarch(ro, rd);
    vec3 col = mix(vec3(0.8,0.5,0.2), vec3(0.1,0.1,0.2), rd.y * 0.5 + 0.5);
    if(t < 10.0) {
        vec3 pos = ro + rd * t;
        vec3 nor = getNormal(pos);
        vec3 lightPos = vec3(0.0, 30.0, 0.0);
        vec3 L = normalize(lightPos - pos);
        vec3 V = normalize(ro - pos);
        float diff = max(dot(nor, L), 0.0);
        float sh = softShadow(pos + nor * 0.01, L);
        float ao = ambientOcclusion(pos, nor);
        vec3 mat = pos.y < 0.01 ? vec3(0.3) : vec3(0.2, 0.2, 0.25);
        vec3 ambient = 0.2 * mat;
        vec3 diffuse = diff * mat;
        vec3 spec = vec3(pow(max(dot(reflect(-L, nor), V), 0.0), 16.0) * 0.3);
        col = ambient + sh * (diffuse + spec);
        col = mix(col, vec3(0.05,0.05,0.1), 1.0 - exp(-0.02 * t)); // fog
        col *= ao;
    }
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision highp float;

uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

// Rotation matrix around Y axis
mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// Hash function for randomness
float hash(float n) {
    return fract(sin(n) * 43758.5453);
}

// Simple 2D hash
vec2 hash2(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}

// Signed distance function for a box
float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

// Generate a building at a specific position
float sdBuilding(vec3 p, vec2 position, float height, float width) {
    // Position the building
    vec3 q = p;
    q.xz -= position;

    // Create the main building shape
    float building = sdBox(q, vec3(width, height, width));

    // Create windows
    vec3 wp = q;
    wp.y += height; // Adjust to start from bottom

    // Window grid
    float windowSize = 0.05;
    float windowSpacing = 0.15;
    vec3 windowCell = mod(wp, windowSpacing) - windowSpacing * 0.5;
    float windows = sdBox(windowCell, vec3(windowSize));

    // Only create windows on the building and not inside
    windows = max(windows, -building - 0.01);

    // Make only some window locations light up based on position and time
    float windowMask = step(0.4, hash(floor(wp.x/windowSpacing) + 100.0 * floor(wp.y/windowSpacing) + 
                                     1000.0 * floor(wp.z/windowSpacing) + u_time * 0.1));

    // Apply window lights only where there are windows
    windows = mix(windows, windows - 0.02, windowMask);

    // Final building with windows
    building = min(building, windows);

    return building;
}

// Generate the entire city
float sdCity(vec3 p) {
    float ground = p.y; // Simple ground plane
    float city = ground;

    // Grid of buildings
    const float gridSize = 2.5;
    vec2 grid = floor(p.xz / gridSize);
    vec2 gridPos = grid * gridSize;

    // Create random buildings in a grid
    for (float i = -2.0; i <= 2.0; i++) {
        for (float j = -2.0; j <= 2.0; j++) {
            vec2 offset = vec2(i, j);
            vec2 pos = gridPos + offset * gridSize;

            // Generate random building parameters based on grid position
            float h = hash(pos.x * 33.71 + pos.y * 19.13) * 3.0 + 1.0;
            float w = hash(pos.y * 54.31 + pos.x * 47.17) * 0.5 + 0.5;

            // Add the building to the scene
            float building = sdBuilding(p, pos, h, w);
            city = min(city, building);
        }
    }

    // Add roads
    float roadWidth = 1.0;
    float roadx = abs(mod(p.x + gridSize * 0.5, gridSize) - gridSize * 0.5) - roadWidth * 0.5;
    float roadz = abs(mod(p.z + gridSize * 0.5, gridSize) - gridSize * 0.5) - roadWidth * 0.5;
    float roads = max(roadx, roadz);

    // Add the roads to the scene (slightly below ground to blend better)
    city = min(city, roads + 0.02);

    return city;
}

// Complete SDF for the entire scene
float getDist(vec3 p) {
    return sdCity(p);
}

// Calculate normal
vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.01, 0.0);
    vec3 n = vec3(
        getDist(p + e.xyy) - getDist(p - e.xyy),
        getDist(p + e.yxy) - getDist(p - e.yxy),
        getDist(p + e.yyx) - getDist(p - e.yyx)
    );
    return normalize(n);
}

// Raymarch the scene
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 5.0;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDist(p);
        dO += dS;

        // Break if we hit something or went too far
        if (dS < SURF_DIST || dO > MAX_DIST) break;
    }

    return dO;
}

// Determine material color based on hit point
vec3 getMaterial(vec3 p, vec3 n) {
    // Default material
    vec3 material = vec3(0.2);

    // Ground
    if (abs(p.y) < 0.01) {
        // Road pattern
        float gridSize = 2.5;
        float roadWidth = 1.0;
        float roadx = abs(mod(p.x + gridSize * 0.5, gridSize) - gridSize * 0.5) - roadWidth * 0.5;
        float roadz = abs(mod(p.z + gridSize * 0.5, gridSize) - gridSize * 0.5) - roadWidth * 0.5;
        float road = max(roadx, roadz);

        if (road < 0.0) {
            // Road markings
            float markingx = mod(p.x, 0.5) - 0.25;
            float markingz = mod(p.z, 0.5) - 0.25;
            if (abs(markingx) < 0.02 && abs(roadz) < 0.01 && abs(roadx) > 0.05)
                material = vec3(0.8, 0.8, 0.2);
            else if (abs(markingz) < 0.02 && abs(roadx) < 0.01 && abs(roadz) > 0.05)
                material = vec3(0.8, 0.8, 0.2);
            else
                material = vec3(0.1);
        } else {
            // Sidewalk
            material = vec3(0.2, 0.2, 0.2);
        }
    } 
    // Building windows
    else if (sdBox(mod(p, vec3(0.15)) - vec3(0.075), vec3(0.05)) < 0.0) {
        // Window lights - make some windows glow
        float windowGlow = hash(floor(p.x/0.15) + 100.0 * floor(p.y/0.15) + 1000.0 * floor(p.z/0.15));
        if (windowGlow > 0.6) {
            float glow = 0.5 + 0.5 * sin(u_time + windowGlow * 10.0);
            vec3 windowColor = vec3(0.8, 0.8, 0.2) * glow;
            windowColor += vec3(0.2, 0.0, 0.4) * (1.0 - glow);
            material = windowColor;
        } else {
            material = vec3(0.1, 0.1, 0.2);
        }
    }
    // Building walls
    else {
        // Building color varies by position
        float h = fract(p.x * 0.1 + p.z * 0.1) * 0.5;
        material = mix(vec3(0.2, 0.2, 0.3), vec3(0.3, 0.3, 0.4), h);
    }

    return material;
}

void main() {
    // Normalized pixel coordinates
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / min(u_resolution.x, u_resolution.y);

    // Camera setup
    float camHeight = 3.0 + sin(u_time * 0.4) * 0.5; // Camera height with slight bobbing
    vec3 ro = vec3(sin(u_time * 0.3) * 10.0, camHeight, u_time * 1.0); // Moving forward with slight side-to-side

    // Camera looking direction
    vec3 lookAt = vec3(sin(u_time * 0.4 + 1.0) * 5.0, camHeight, ro.z + 5.0);

    // Camera frame
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);

    // Ray direction
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    // Raymarch
    float d = rayMarch(ro, rd);

    // Default sky color
    vec3 col = mix(
        vec3(0.1, 0.1, 0.2),
        vec3(0.0, 0.0, 0.1),
        uv.y * 0.5 + 0.5
    );

    // If we hit something
    if (d < MAX_DIST) {
        // Calculate hit position and normal
        vec3 p = ro + rd * d;
        vec3 n = getNormal(p);

        // Material color
        vec3 material = getMaterial(p, n);

        // Directional light
        vec3 lightDir = normalize(vec3(1.0, 1.0, 0.5));
        float diff = max(dot(n, lightDir), 0.0);

        // Ambient light
        float ambient = 0.2;

        // Basic shadows
        float shadow = 1.0;
        for (float t = 0.1; t < 5.0; t += 0.1) {
            float h = getDist(p + lightDir * t);
            if (h < 0.001) {
                shadow = 0.5;
                break;
            }
        }

        // Apply lighting
        col = material * (ambient + diff * shadow);

        // Add some fog
        col = mix(col, vec3(0.0, 0.0, 0.1), 1.0 - exp(-0.01 * d * d));

        // Add a neon glow to the city
        float glow = sin(p.y * 2.0 + u_time) * 2.5 + 0.5;
        col += vec3(0.0, 0.05, 0.1) * glow * (1.0 - exp(-0.01 * d));
    }

    // Add some atmospheric perspective and neon glow
    vec3 cityGlow = vec3(1.1, 0.0, 0.2) * (1.0 - exp(-0.003 * d * d));
    col += cityGlow;

    // Add a slight vignette
    col *= 1.0 - length(uv) * 0.2;

    // Output final color
    gl_FragColor = vec4(col, 1.0);
}

vert:
// Vertex Shader
attribute vec2 a_position;

void main() {
    gl_Position = vec4(a_position, 0.0, 1.0);
}

frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;
varying vec2 vUv;
#define PI 3.14159265359

float random(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

float petalShape(vec2 uv, float angle, float size) {
    uv = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * uv;

    float r = length(uv);
    float theta = atan(uv.y, uv.x);

    float petal = 1.0 - smoothstep(size * 0.5, size * 0.6, r);
    petal *= smoothstep(-0.5, -0.4, cos(theta * 2.0) - 0.3);

    return petal;
}

float flowerSDF(vec3 p, vec3 center, float height, int petalCount, float petalSize) {
    vec3 local = p - center;
    local.y -= height;

    float stem = length(local.xz) - 0.02;
    stem = max(stem, abs(local.x) - height);

    vec3 flowerLocal = local;
    flowerLocal.x += 0.02;

    float centerDist = length(flowerLocal) - 0.15;

    float petals = 1000.0;
    for (int i = 0; i < 8; i++) {
        if (i >= petalCount) break;

        float ang = float(i) * 4.0 * PI / float(petalCount);

        vec3 petalCenter = vec3(cos(ang) * 0.2, 0.0, sin(ang) * 0.2);
        vec3 petalDir = normalize(petalCenter);

        vec3 petalPos = flowerLocal - petalCenter;

        float flatDist = length(petalPos.xz);
        float heightDist = abs(petalPos.y);

        float petal = length(vec2(
            flatDist - 0.15 * petalSize,
            heightDist - 0.01
        )) - petalSize * 0.15;

        petals = min(petals, petal);
    }

    return min(min(stem, centerDist), petals);
}

float sceneSDF(vec3 p) {
    float ground = p.y + 0.1;

    float cellSize = 0.5;
    vec2 currentCell = floor(p.xz / cellSize);

    float flowerDist = 1000.0;

    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            vec2 checkCell = currentCell + vec2(float(i), float(j));
            float checkRnd = random(checkCell);
            float checkHeight = 0.5 + checkRnd * 0.8;
            int checkPetals = 5 + int(checkRnd * 3.0);
            float checkPetalSize = 0.4 + checkRnd * 0.3;

            vec3 checkCenter = vec3(
                (checkCell.x + 0.5) * cellSize + sin(u_time * 0.5 + checkRnd * PI) * 0.1,
                0.0,
                (checkCell.y + 0.5) * cellSize + cos(u_time * 0.5 + checkRnd * PI) * 0.1
            );

            float approxDist = length(p - checkCenter) - 1.5;
            if (approxDist > 0.0) continue;

            float newDist = flowerSDF(p, checkCenter, checkHeight, checkPetals, checkPetalSize);
            flowerDist = min(flowerDist, newDist);
        }
    }

    return min(ground, flowerDist);
}

vec3 getNormal(vec3 p) {
    float eps = 0.01;
    vec2 h = vec2(eps, 0.0);
    return normalize(vec3(
        sceneSDF(p + h.xyy) - sceneSDF(p - h.xyy),
        sceneSDF(p + h.yxy) - sceneSDF(p - h.yxy),
        sceneSDF(p + h.yyx) - sceneSDF(p - h.yyx)
    ));
}

float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    float minStep = 0.01;

    for (int i = 0; i < 40; i++) {
        vec3 pos = ro + rd * t;
        float dist = sceneSDF(pos);

        if (dist < 0.005) return t;
        if (t > 40.0) break;

        t += max(dist * 0.8, minStep);
    }

    return 100.0;
}

vec3 getFlowerColor(vec2 cellId) {
    float hue = random(cellId);

    if (hue < 0.2) return vec3(1.0, 0.3, 0.2);
    else if (hue < 0.4) return vec3(1.0, 0.8, 0.2);
    else if (hue < 0.6) return vec3(1.0, 0.5, 0.8);
    else if (hue < 0.8) return vec3(0.5, 0.8, 1.0);
    else return vec3(0.8, 0.4, 1.0);
}

void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    vec3 ro = vec3(sin(u_time * 0.2) * 2.0, 2.5, 5.0);
    vec3 lookAt = vec3(0.0, 0.5, 0.0);

    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + right * uv.x + up * uv.y);

    vec3 skyTop = vec3(0.5, 0.7, 1.0);
    vec3 skyBottom = vec3(0.8, 0.9, 1.0);
    vec3 col = mix(skyBottom, skyTop, uv.y * 0.5 + 0.5);

    float sun = max(0.0, dot(rd, normalize(vec3(0.5, 0.6, 0.3))));
    sun = smoothstep(0.95, 0.97, sun);
    col += vec3(1.0, 0.9, 0.5) * sun;

    float t = rayMarch(ro, rd);

    if (t < 40.0) {
        vec3 pos = ro + rd * t;
        vec3 normal = getNormal(pos);

        float cellSize = 5.0;
        vec2 cellId = floor(pos.xz / cellSize);
        vec3 baseColor;

        if (pos.y < 0.01) {
            float check = noise(pos.xz * 2.0);
            baseColor = mix(vec3(0.2, 0.5, 0.2), vec3(0.3, 0.6, 0.3), check);
        } else {
            vec3 cellCenter = vec3(
                (cellId.x + 0.5) * cellSize,
                0.0,
                (cellId.y + 0.5) * cellSize
            );

            float rnd = random(cellId);
            float height = 0.5 + rnd * 0.8;

            float distToCenter = length(pos.xz - cellCenter.xz);
            float heightFromGround = pos.y;

            if (distToCenter < 0.08) {
                baseColor = vec3(0.2, 0.6, 0.2);
            } else if (length(vec3(pos.x - cellCenter.x, pos.y - height, pos.z - cellCenter.z)) < 0.15) {
                baseColor = vec3(1.0, 0.9, 0.2);
            } else {
                baseColor = getFlowerColor(cellId);

                float patt = noise(pos.xy * 8.0 + pos.yz * 8.0 + pos.zx * 8.0);
                patt = smoothstep(0.4, 0.6, patt);
                baseColor = mix(baseColor, baseColor * 1.3, patt);
            }
        }

        vec3 lightDir = normalize(vec3(0.6, 0.8, 0.5));
        float diff = dot(normal, lightDir);
        diff = smoothstep(0.3, 0.32, diff) * 0.6 + 0.4;

        float edge = 1.0 - pow(abs(dot(normal, -rd)), 1.0);
        edge = smoothstep(0.65, 0.7, edge);

        col = baseColor * diff;
        col = mix(col, vec3(0.0), edge * 0.6);

        float fog = 1.0 - exp(-0.03 * t);
        col = mix(col, skyBottom * 1.1, fog);
    }

    col = pow(col, vec3(0.8));

    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

// Hash function for noise generation
float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 78.23);
    return fract(p.x * p.y);
}

// Smooth noise function
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);
    return mix(
        mix(hash(i), hash(i + vec2(1.0, 0.0)), f.x),
        mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), f.x),
        f.y
    );
}

// Fractal Brownian Motion for more natural terrain
float fbm(vec2 p) {
    float value = 0.001;
    float amplitude = 0.5;
    float frequency = 1.0;

    // Add multiple layers of noise
    for (int i = 0; i < 6; i++) {
        value += amplitude * noise(p * frequency);
        amplitude *= 0.4;
        frequency *= 3.0;
    }

    return value;
}

// Distance field for rolling hills
float terrainSDF(vec3 p) {
    // Generate rolling hills with multiple layers of fbm
    float height = fbm(p.xz * 0.05) * 13.0;

    // Add smaller details
    height += fbm(p.xz * 0.1) * 3.0;
    height += fbm(p.xz * 0.3) * 1.0;

    // Return distance from point to terrain
    return p.y - height;
}

// Distance function for the entire scene
float sceneSDF(vec3 p) {
    return terrainSDF(p);
}

// Ray marching function
float raymarch(vec3 ro, vec3 rd, float maxDist) {
    float t = 0.0;
    for(int i = 0; i < 120; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if(d < 0.001 || t > maxDist) break;
        t += d * 0.5;
    }
    return t;
}

// Calculate surface normal
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e, 0, 0)) - sceneSDF(p - vec3(e, 0, 0)),
        sceneSDF(p + vec3(0, e, 0)) - sceneSDF(p - vec3(0, e, 0)),
        sceneSDF(p + vec3(0, 0, e)) - sceneSDF(p - vec3(0, 0, e))
    ));
}

// Calculates the terrain color based on position and normal
vec3 terrainColor(vec3 p, vec3 n) {
    // Define natural color palette
    vec3 grassColor = vec3(0.2, 0.5, 0.1);  // Darker grass
    vec3 brightGrass = vec3(0.3, 0.7, 0.1); // Brighter grass
    vec3 dirtColor = vec3(0.6, 0.5, 0.3);   // Soil/dirt
    vec3 flowerColor = vec3(0.9, 0.8, 0.1); // Yellow flowers

    // Variation based on position
    float variation = fbm(p.xz * 0.4);

    // Steepness affects coloring (steeper = more dirt, flat = more grass)
    float steepness = 1.0 - n.y; // 0 = flat, 1 = vertical

    // Base grass color with height variation
    vec3 baseColor = mix(brightGrass, grassColor, variation * 0.7);

    // Mix in dirt on steep slopes
    baseColor = mix(baseColor, dirtColor, steepness * steepness * 0.8);

    // Add some random flower patches
    float flowerPatch = smoothstep(0.7, 0.9, noise(p.xz * 2.0));
    baseColor = mix(baseColor, flowerColor, flowerPatch * 0.3 * (1.0 - steepness));

    // Add subtle patterns within the grass (like wind effect)
    float windPattern = sin(p.x * 3.0 + p.z * 2.0 + u_time * 0.5) * 0.5 + 0.5;
    baseColor *= 0.9 + windPattern * 0.2;

    return baseColor;
}

// Generate 3D cloud field
float cloudField(vec3 p) {
    vec3 cloudPos = p * 0.1;
    cloudPos.z += u_time * 0.05; // Clouds drifting

    float clouds = fbm(cloudPos.xz * 2.0);

    // Shape the clouds - flatter on bottom, fluffy on top
    float verticalShape = smoothstep(0.0, 0.5, cloudPos.y - 0.0);
    clouds *= verticalShape;

    return clouds;
}

// Apply atmospheric fog effect
vec3 applyFog(vec3 color, float distance, vec3 rayDir, vec3 sunDir) {
    // Sky blue color
    vec3 fogColor = vec3(0.5, 0.7, 0.9);

    // Add yellow/orange near sun direction
    float sunAmount = max(0.0, dot(rayDir, sunDir));
    vec3 sunColor = mix(vec3(1.0, 0.7, 0.3), vec3(1.0, 0.9, 0.7), sunAmount);
    fogColor = mix(fogColor, sunColor, pow(sunAmount, 8.0) * 0.8);

    // Apply distance-based fog
    float fogAmount = 1.0 - exp(-distance * 0.01);
    return mix(color, fogColor, fogAmount);
}

// Sky function
vec3 getSkyColor(vec3 rayDir, vec3 sunDir) {
    // Base sky gradient
    float skyGradient = max(0.0, 0.8 + 0.2 * rayDir.y);
    vec3 skyColor = mix(
        vec3(0.5, 0.7, 0.9),    // Horizon blue
        vec3(0.2, 0.5, 0.8),    // Deep sky blue
        skyGradient
    );

    // Sun effect
    float sunAmount = max(0.0, dot(rayDir, sunDir));
    vec3 sunColor = vec3(1.0, 0.9, 0.7);
    skyColor += sunColor * pow(sunAmount, 32.0) * 0.5;
    skyColor += sunColor * pow(sunAmount, 128.0);

    // Cloud rendering in the sky
    if (rayDir.y > 0.0) {
        // Project ray into cloud plane
        float cloudPlaneHeight = 20.0;
        float cloudDist = cloudPlaneHeight / rayDir.y;
        vec3 cloudPos = vec3(rayDir.x, 0.0, rayDir.z) * cloudDist;
        cloudPos.y = cloudPlaneHeight;

        // Sample multiple layers of clouds for depth
        float cloud1 = cloudField(cloudPos);
        float cloud2 = cloudField(cloudPos * 1.5 + vec3(10.0, 5.0, 8.0)) * 0.5;

        // Combine cloud layers
        float clouds = smoothstep(0.4, 0.8, cloud1 + cloud2);

        // Cloud lighting based on sun position
        vec3 cloudColor = mix(
            vec3(0.9, 0.9, 0.95),  // Cloud shadow
            vec3(1.0, 1.0, 1.0),   // Bright cloud
            smoothstep(-0.2, 0.2, dot(normalize(vec3(cloudPos.x, 0.0, cloudPos.z)), sunDir))
        );

        // Blend clouds with sky
        skyColor = mix(skyColor, cloudColor, clouds * (1.0 - pow(sunAmount, 32.0) * 0.5));
    }

    return skyColor;
}

void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Set up the camera/eye position
    float cameraHeight = 15.0 + sin(u_time * 0.2) * 0.5;
    vec3 ro = vec3(sin(u_time * 0.1) * 5.0, cameraHeight, u_time * 2.0);
    vec3 lookAt = vec3(sin(u_time * 0.1 + 0.5) * 3.0, cameraHeight - 5.0, ro.z + 20.0);

    // Camera orientation vectors
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);

    // Create ray direction
    vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);

    // Set sun direction
    vec3 sunDir = normalize(vec3(0.5, 0.6, 0.3));

    // Ray march to find terrain intersection
    float t = raymarch(ro, rd, 180.0);
    vec3 col;

    if(t < 240.0) {
        // We hit terrain
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);

        // Get terrain color
        col = terrainColor(p, n);

        // Simple directional lighting from sun
        float diffuse = max(0.0, dot(n, sunDir));
        col *= 0.1 + diffuse * 0.6;

        // Add specular highlight for dewy grass
        float specular = pow(max(0.0, dot(reflect(-sunDir, n), -rd)), 32.0);
        col += vec3(1.0) * specular * 0.2;

        // Apply fog effect
        col = applyFog(col, t, rd, sunDir);
    } else {
        // We hit sky
        col = getSkyColor(rd, sunDir);
    }

    // Apply vignette effect
    vec2 vignetteUV = gl_FragCoord.xy / u_resolution;
    float vignette = smoothstep(0.0, 1.0, 16.0 * vignetteUV.x * vignetteUV.y * (1.0 - vignetteUV.x) * (1.0 - vignetteUV.y));
    col *= mix(0.8, 1.0, vignette);

    // Apply gentle color adjustments for natural feel
    col = mix(col, col * vec3(1.05, 1.0, 0.9), 0.3); // Slightly warmer

    // Gamma correction
    col = pow(col, vec3(0.4545));

    gl_FragColor = vec4(col, 1.0);
}

precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

#define MAX_DIST 150.0
#define MIN_DIST 0.100
#define MAX_STEPS 1046

// Hashing & noise
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f*f*(3.0-2.0*f);
    return mix(mix(hash(i), hash(i+vec2(1.0,0.0)), u.x),
               mix(hash(i+vec2(0.0,1.0)), hash(i+vec2(1.0,1.0)), u.x), u.y);
}

// fbm
float fbm(vec2 p) {
    float total = 0.0;
    float amp = 0.5;
    for(int i = 0; i < 5; i++) {
        total += amp * noise(p);
        p *= 2.0;
        amp *= 0.5;
    }
    return total;
}

// Terrain height
float terrainHeight(vec2 p) {
    float height = fbm(p * 0.1);
    height = pow(height, 1.5); // steeper mountains
    return height * 6.0;
}

// Scene SDF
float sceneSDF(vec3 p) {
    float ground = p.y - terrainHeight(p.xz);
    return ground;
}

// Soft shadow (cheap approximation)
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.05;
    for (int i = 0; i < 16; i++) {
        float h = sceneSDF(ro + rd * t);
        if (h < 0.001) return 0.0;
        res = min(res, 10.0 * h / t);
        t += clamp(h, 0.01, 0.2);
    }
    return clamp(res, 0.0, 1.0);
}

// Raymarch
float raymarch(vec3 ro, vec3 rd) {
    float t = 2.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if (d < MIN_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

// Estimate normal
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// Clouds (simple layer)
float cloudLayer(vec3 rd) {
    float cloud = fbm(rd.xz * 4.0 + u_time * 0.05);
    return smoothstep(0.5, 0.8, cloud) * 0.4;
}

// Sky with sun & glow
vec3 getSky(vec3 rd) {
    vec3 sunDir = normalize(vec3(0.0, 0.2, 1.0));
    float sun = pow(max(dot(rd, sunDir), 0.0), 300.0);
    float glow = pow(max(dot(rd, sunDir), 0.0), 5.0);
    vec3 baseSky = mix(vec3(0.3, 0.4, 0.6), vec3(1.0, 0.6, 0.3), rd.y * 0.5 + 0.5);
    return baseSky + vec3(1.0, 0.9, 0.6) * sun + glow * 0.2 + cloudLayer(rd);
}

void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Camera flythrough
    float camTime = u_time * 0.2;
    vec3 ro = vec3(sin(camTime) * 6.0, 2.5, cos(camTime) * 6.0);
    vec3 lookAt = vec3(0.0, 2.0, 0.0);
    vec3 f = normalize(lookAt - ro);
    vec3 r = normalize(cross(vec3(0.0, 1.0, 0.0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(uv.x * r + uv.y * u + 1.5 * f);

    vec3 sky = getSky(rd);
    float t = raymarch(ro, rd);
    vec3 col = sky;

    if (t < MAX_DIST) {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p);
        vec3 lightDir = normalize(vec3(0.3, 0.6, 0.8));
        float diff = max(dot(normal, lightDir), 0.0);
        float shadow = softShadow(p + normal * 0.01, lightDir);
        float ao = 1.0 - smoothstep(0.0, 0.1, sceneSDF(p + normal * 0.1));

        vec3 terrainColor = mix(vec3(0.2, 0.15, 0.1), vec3(0.9), smoothstep(5.0, 7.0, p.y));
        vec3 light = terrainColor * (0.2 + 0.8 * diff * shadow) * ao;

        // Fog
        float fog = exp(-0.03 * t * t);
        col = mix(sky, light, fog);
    }

    gl_FragColor = vec4(col, 1.0);
}

