                return fract(sin(dot(p, vec3(12.9898, 78.233, 45.164))) * 43758.5453);
            }
            float voronoi(vec3 p) {
                vec3 i = floor(p);
                vec3 f = fract(p);
                float minDist = 1.0;
                for (int z = -1; z <= 1; z++) {
                    for (int y = -1; y <= 1; y++) {
                        for (int x = -1; x <= 1; x++) {
                            vec3 neighbor = vec3(float(x), float(y), float(z));
                            vec3 point = neighbor + random(i + neighbor) - f;
                            float dist = length(point);
