precision highp float;
uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;

// Simplex 2D noise function
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187, 0.366025403784439,
                        -0.577350269189626, 0.024390243902439);
    vec2 i  = floor(v + dot(v, C.yy));
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod(i, 289.0);
    vec3 p = permute(permute(i.y + vec3(0.0, i1.y, 1.0))
    + i.x + vec3(0.0, i1.x, 1.0));
    vec3 m = max(0.5 - vec3(dot(x0, x0), dot(x12.xy, x12.xy),
                    dot(x12.zw, x12.zw)), 0.0);
    m = m*m;
    m = m*m;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * (a0*a0 + h*h);
    vec3 g;
    g.x = a0.x * x0.x + h.x * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}

void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
    float t = uTime * 0.3;

    // Create flowing liquid effect with multiple layers of noise
    float flow = 0.0;
    for (float i = 1.0; i <= 4.0; i++) {
        float scale = pow(2.0, i);
        vec2 offset = vec2(t * (0.1 / i), t * (0.05 / i));
        flow += snoise(uv * scale + offset) / scale;
    }

    // Add ripples
    vec2 p = uv;
    for (int i = 0; i < 3; i++) {
        float speed = 0.2 + float(i) * 0.05;
        float size = 5.0 + float(i) * 3.0;
        float amplitude = 0.3 - float(i) * 0.05;
        float ripple = sin(length(p) * size - t * speed) * amplitude;
        flow += ripple;
    }

    // Create color variation based on the flow field
    vec3 color1 = vec3(0.1, 0.5, 0.8);
    vec3 color2 = vec3(0.0, 0.8, 0.6);
    vec3 color3 = vec3(0.0, 0.4, 0.8);

    float flowMapped = flow * 0.5 + 0.5; // Map to 0-1 range
    vec3 color = mix(color1, color2, flowMapped);
    color = mix(color, color3, sin(flowMapped * 3.14159 + t) * 0.5 + 0.5);

    // Enhance edges with slight highlights
    float edge = abs(flow - snoise(uv * 2.0 + vec2(t * 0.2, 0.0)));
    color += edge * edge * 0.3;

    // Apply user color
    color *= uColor;

    gl_FragColor = vec4(color, 1.0);
}

