                vec3 p = vec3(uv, 0.0);

                // Rotate over time
                p = rotateY(t * 0.1) * rotateX(t * 0.15) * p;

                // Add time to z-coordinate for animation
                p.z += t * 0.3;

                // Generate nebula density
                float density = 0.0;

                // Main nebula clouds
                density += fbm(p * 1.5) * 0.5;

                // Add dust layers
                density += fbm(p * 3.0 + fbm(p + t * 0.2) * 0.5) * 0.25;

                // Add stars
                float stars = 0.0;
                for (int i = 0; i < 3; i++) {
                    vec3 starPos = p * float(i+1) * 2.0;
                    float starNoise = noise(starPos * 10.0);
                    stars += pow(starNoise, 20.0) * 2.0;
                }

                // Define nebula colors
                vec3 purpleColor = vec3(0.5, 0.0, 0.7);
                vec3 blueColor = vec3(0.0, 0.2, 0.8);
                vec3 pinkColor = vec3(0.9, 0.4, 0.6);

                // Create colored nebula
                vec3 color = mix(purpleColor, blueColor, density);
                color = mix(color, pinkColor, fbm(p * 2.0 + t * 0.1) * 0.5);

