precision highp float;
            uniform float uTime;
            uniform vec2 uResolution;
            uniform vec3 uColor;

            // Hash function
            float hash(vec2 p) {
                p = fract(p * vec2(123.34, 456.21));
                p += dot(p, p + 45.32);
                return fract(p.x * p.y);
            }

            // Line segment distance function
            float sdSegment(vec2 p, vec2 a, vec2 b) {
                vec2 pa = p - a;
                vec2 ba = b - a;
                float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
                return length(pa - ba * h);
            }

            // Rounded rectangle distance function
            float sdRoundBox(vec2 p, vec2 b, float r) {
                vec2 q = abs(p) - b + r;
                return length(max(q, 0.0)) + min(max(q.x, q.y), 0.0) - r;
            }

            // Circuit component drawing helper
            float component(vec2 p, vec2 pos, float size, float type) {
                vec2 localP = p - pos;
                float comp = 1.0;

                // Different component types
                if (type < 0.33) {
                    // IC chip
                    comp = sdRoundBox(localP, vec2(size * 0.7, size * 0.4), size * 0.05);

                    // Pins
                    for (float i = -3.0; i <= 3.0; i += 1.0) {
                        float pinDist = sdSegment(
                            localP, 
                            vec2(i * size * 0.2, size * 0.4), 
                            vec2(i * size * 0.2, size * 0.5)
                        );
                        comp = min(comp, pinDist);

                        pinDist = sdSegment(
                            localP, 
                            vec2(i * size * 0.2, -size * 0.4), 
                            vec2(i * size * 0.2, -size * 0.5)
                        );
                        comp = min(comp, pinDist);
                    }
                } 
                else if (type < 0.66) {
                    // Capacitor
                    comp = sdRoundBox(localP, vec2(size * 0.2, size * 0.4), size * 0.05);

                    // Leads
                    comp = min(comp, sdSegment(
                        localP, 
                        vec2(0.0, size * 0.4), 
                        vec2(0.0, size * 0.6)
                    ));

                    comp = min(comp, sdSegment(
                        localP, 
                        vec2(0.0, -size * 0.4), 
                        vec2(0.0, -size * 0.6)
                    ));
                } 
                else {
                    // Resistor
                    comp = sdRoundBox(localP, vec2(size * 0.5, size * 0.2), size * 0.05);

                    // Leads
                    comp = min(comp, sdSegment(
                        localP, 
                        vec2(size * 0.5, 0.0), 
                        vec2(size * 0.7, 0.0)
                    ));

                    comp = min(comp, sdSegment(
                        localP, 
                        vec2(-size * 0.5, 0.0), 
                        vec2(-size * 0.7, 0.0)
                    ));
                }

                return comp;
            }

            void main() {
                vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
                float t = uTime * 0.3;

                // Scale and grid the space
                float gridSize = 0.2;
                vec2 grid = floor(uv / gridSize);
                vec2 gridUv = fract(uv / gridSize) * gridSize;

                // Base color - dark background
                vec3 color = vec3(0.05, 0.07, 0.1);

                // Create circuit traces
                float circuit = 1.0;

                // Horizontal traces
                for (float i = -5.0; i <= 5.0; i += 1.0) {
                    float linePos = i * 0.2;
                    float lineWidth = 0.01 + 0.005 * sin(grid.x * 0.5 + t + i);
                    float line = abs(uv.y - linePos) - lineWidth;

                    // Junctions
                    for (float j = -5.0; j <= 5.0; j += 1.0) {
                        float juncPos = j * 0.2;
                        float juncHash = hash(vec2(i + 100.0, j + 200.0));

                        // Random vertical connections
                        if (juncHash > 0.7) {
                            float vline = abs(uv.x - juncPos) - lineWidth;
                            float vstart = linePos;
                            float vend = linePos + 0.2 * (hash(vec2(i, j)) < 0.5 ? -1.0 : 1.0);

                            if (uv.y >= min(vstart, vend) - lineWidth && 
                                uv.y <= max(vstart, vend) + lineWidth) {
                                circuit = min(circuit, vline);
                            }

                            // Add components
                            if (juncHash > 0.85) {
                                float compHash = hash(vec2(j*51.3, i*27.8));
                                float compSize = 0.05 + 0.03 * hash(vec2(i*13.7, j*41.2));
                                float comp = component(uv, vec2(juncPos, (vstart + vend) * 0.5), compSize, compHash);
                                circuit = min(circuit, comp);
                            }
                        }
                    }

                    circuit = min(circuit, line);
                }

                // Vertical traces
                for (float i = -5.0; i <= 5.0; i += 1.0) {
                    float linePos = i * 0.2;
                    float lineWidth = 0.01 + 0.005 * sin(grid.y * 0.5 + t + i);
                    float line = abs(uv.x - linePos) - lineWidth;
                    circuit = min(circuit, line);
                }

                // Draw circuit
                if (circuit < 0.0) {
                    // Calculate electric pulse
                    float pulse = 0.0;
                    for (float i = 0.0; i < 5.0; i++) {
                        float speed = 0.2 + i * 0.1;
                        float offset = i * 1.5;
                        pulse += 0.5 + 0.5 * sin(grid.x * 3.0 + grid.y * 2.0 + t * speed + offset);
                    }
                    pulse *= 0.2;

                    // Circuit color
                    color = mix(
                        vec3(0.0, 0.5, 0.7),
                        vec3(0.0, 0.8, 1.0),
                        pulse
                    );

                    // Add glow based on pulse
                    color += vec3(0.0, 0.5, 1.0) * pulse * pulse;
                }

                // Add subtle grid pattern
                float gridLines = max(
                    smoothstep(0.003, 0.0, abs(fract(uv.x / 0.05) - 0.5) - 0.48),
                    smoothstep(0.003, 0.0, abs(fract(uv.y / 0.05) - 0.5) - 0.48)
                );
                color += vec3(0.1, 0.15, 0.2) * gridLines;

                // Nodes at grid intersections
                for (float i = -5.0; i <= 5.0; i += 1.0) {
                    for (float j = -5.0; j <= 5.0; j += 1.0) {
                        float nodePos = hash(vec2(i*33.1, j*51.7));
                        if (nodePos > 0.85) {
                            vec2 center = vec2(i, j) * 0.2;
                            float node = length(uv - center) - 0.01;
                            if (node < 0.0) {
                                float blink = 0.5 + 0.5 * sin(t * 2.0 + i * j);
                                color = mix(color, vec3(0.0, 1.0, 0.5) * blink, 0.8);
                            }
                        }
                    }
                }

                // Apply user color
                color *= uColor;

                gl_FragColor = vec4(color, 1.0);
            }

