precision highp float;
            uniform float uTime;
            uniform vec2 uResolution;
            uniform vec3 uColor;

            // Hash function
            float hash(vec2 p) {
                p = fract(p * vec2(123.4, 456.7));
                p += dot(p, p + 45.32);
                return fract(p.x * p.y);
            }

            // Character rendering function
            float character(vec2 p, float index) {
                p = floor(p * vec2(4.0, 5.0));

                // Simple character patterns
                if (index < 0.15) {
                    // 1
                    return step(1.0, mod(p.x, 3.0)) * step(1.0, mod(p.y, 5.0));
                } else if (index < 0.3) {
                    // 0
                    if (p.x < 1.0 || p.x > 2.0) return 0.0;
                    if (p.y < 1.0 || p.y > 3.0) return 0.0;
                    return 1.0;
                } else if (index < 0.45) {
                    // |
                    return step(1.5, p.x) * step(p.x, 2.5);
                } else if (index < 0.6) {
                    // -
                    return step(1.5, p.y) * step(p.y, 2.5);
                } else if (index < 0.75) {
                    // +
                    return max(
                        step(1.5, p.x) * step(p.x, 2.5),
                        step(1.5, p.y) * step(p.y, 2.5)
                    );
                } else if (index < 0.9) {
                    // .
                    return step(3.0, p.y) * step(p.x, 2.0);
                } else {
                    // *
                    if (p.x == 1.0 && p.y == 1.0) return 1.0;
                    if (p.x == 3.0 && p.y == 1.0) return 1.0;
                    if (p.x == 2.0 && p.y == 2.0) return 1.0;
                    if (p.x == 1.0 && p.y == 3.0) return 1.0;
                    if (p.x == 3.0 && p.y == 3.0) return 1.0;
                    return 0.0;
                }
            }

            void main() {
                vec2 uv = gl_FragCoord.xy / uResolution.xy;
                float t = uTime * 0.5;

                // Create a grid for the matrix
                float cellSize = 0.03;
                vec2 cell = floor(uv / cellSize);
                vec2 cellUV = fract(uv / cellSize);

                // Animate characters falling
                float speed = 0.6;
                float scrollOffset = floor(t * speed);

                // Randomize characters
                float charIndex = hash(cell + scrollOffset);

                // Create vertical lanes with different speeds
                float laneSpeed = 0.1 + 0.3 * hash(vec2(cell.x, 0.0));
                float laneOffset = floor(t * laneSpeed * 4.0);
                cell.y += laneOffset;

                // Render characters
                float char = character(cellUV, charIndex);

                // Calculate brightness based on position
                float brightness = 1.0 - fract((cell.y - scrollOffset) / 15.0);
                brightness = pow(brightness, 2.0) * 2.0;

                // Add a subtle horizontal wave
                float wave = sin(uv.y * 10.0 + t) * 0.1;
                float waveBrightness = 0.05 * (1.0 + sin(uv.y * 30.0 - t * 5.0));

                // Random glitch effect
                float glitchLine = step(0.98, hash(vec2(cell.y, floor(t * 10.0))));
                float glitchOffset = (hash(vec2(cell.y, floor(t * 12.0))) * 2.0 - 1.0) * 0.1;
                if (glitchLine > 0.0) {
                    brightness *= hash(vec2(cell.x, cell.y));
                    uv.x += glitchOffset;
                }

                // Background glow
                float bgGlow = 0.0;
                for (float i = 0.0; i < 5.0; i++) {
                    float yOffset = 0.1 + 0.2 * i;
                    float intensity = 0.03 / (0.01 + abs(uv.y - yOffset - t * 0.2));
                    bgGlow += intensity;
                }

                // Apply color
                vec3 charColor = vec3(0.0, 1.0, 0.4) * brightness * char;
                vec3 glowColor = vec3(0.0, 0.5, 0.2) * bgGlow;
                vec3 waveColor = vec3(0.0, 0.7, 0.3) * waveBrightness;

                vec3 color = charColor + glowColor + waveColor;

                // Add dark background
                color += vec3(0.0, 0.02, 0.05);

                // Apply user color
                color *= uColor;

                gl_FragColor = vec4(color, 1.0);
            }

