precision highp float;
            uniform float uTime;
            uniform vec2 uResolution;
            uniform vec3 uColor;

            // Hash function
            float hash(vec3 p) {
                p = fract(p * vec3(0.1031, 0.1030, 0.0973));
                p += dot(p, p.yxz + 33.33);
                return fract((p.x + p.y) * p.z);
            }

            // Noise function
            float noise(vec3 p) {
                vec3 i = floor(p);
                vec3 f = fract(p);
                f = f * f * (3.0 - 2.0 * f);

                return mix(
                    mix(mix(hash(i), hash(i + vec3(1.0, 0.0, 0.0)), f.x),
                        mix(hash(i + vec3(0.0, 1.0, 0.0)), hash(i + vec3(1.0, 1.0, 0.0)), f.x), f.y),
                    mix(mix(hash(i + vec3(0.0, 0.0, 1.0)), hash(i + vec3(1.0, 0.0, 1.0)), f.x),
                        mix(hash(i + vec3(0.0, 1.0, 1.0)), hash(i + vec3(1.0, 1.0, 1.0)), f.x), f.y), f.z
                );
            }

            // Fractional Brownian Motion
            float fbm(vec3 p) {
                float value = 0.0;
                float amplitude = 0.5;
                float frequency = 1.0;

                for (int i = 0; i < 5; i++) {
                    value += amplitude * noise(p * frequency);
                    amplitude *= 0.5;
                    frequency *= 2.0;
                }

                return value;
            }

            // Rotate around Y axis
            mat3 rotateY(float angle) {
                float c = cos(angle);
                float s = sin(angle);
                return mat3(
                    c, 0.0, s,
                    0.0, 1.0, 0.0,
                    -s, 0.0, c
                );
            }

            void main() {
                vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
                float t = uTime * 0.1;

                // Create 3D space coordinates
                vec3 p = vec3(uv, 0.0);

                // Rotate the scene
                p = rotateY(t) * p;

                // Galaxy parameters
                float galaxyRadius = 1.0;
                float galaxyThickness = 0.2;
                float spiralTightness = 4.0;
                float spiralArms = 3.0;

                // Convert to polar coordinates for spiral
                float r = length(p.xy);
                float theta = atan(p.y, p.x);

                // Spiral density function
                float spiral = sin(theta * spiralArms + r * spiralTightness - t * 2.0) * 0.5 + 0.5;
                spiral *= smoothstep(1.0, 0.0, r / galaxyRadius); // Fade out at edges

                // Get distance from galactic plane
                float diskDistance = abs(p.z);

                // Calculate density based on distance from center and plane
                float diskDensity = exp(-r * 2.0) * 0.5; // Central bulge
                diskDensity += exp(-r * 1.0 - diskDistance * 5.0) * spiral * 0.5; // Spiral arms
                diskDensity *= exp(-diskDistance / galaxyThickness); // Vertical falloff

                // Add noise to make it more interesting
                float noiseFactor = fbm(p * 3.0 + t * 0.1);
                diskDensity *= 0.8 + noiseFactor * 0.4;

                // Add stars
                float stars = 0.0;
                for (int i = 0; i < 3; i++) {
                    float starScale = 20.0 + float(i) * 30.0;
                    float starBrightness = pow(noise(p * starScale), 20.0) * 3.0;
                    stars += starBrightness * (1.0 - diskDensity * 0.5);
                }

                // Create central black hole
                float blackHole = smoothstep(0.0, 0.1, r);

                // Accretion disk around black hole
                float accretionDisk = smoothstep(0.05, 0.0, abs(p.z)) * smoothstep(0.2, 0.0, r) * smoothstep(0.0, 0.1, r);

                // Color mapping
                vec3 dustColor = vec3(0.8, 0.6, 0.2);
                vec3 starColor = vec3(0.9, 0.9, 1.0);
                vec3 coreColor = vec3(0.9, 0.5, 0.1);
                vec3 blackHoleColor = vec3(0.0, 0.0, 0.0);
                vec3 accretionColor = vec3(1.0, 0.3, 0.0);

                // Combine colors
                vec3 color = dustColor * diskDensity;
                color += starColor * stars;
                color += coreColor * exp(-r * 10.0) * 2.0;
                color = mix(color, blackHoleColor, 1.0 - blackHole);
                color += accretionColor * accretionDisk * 5.0;

                // Apply user color
                color *= uColor;

                gl_FragColor = vec4(color, 1.0);
            }

