precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define PI 3.14159265359

mat2 rot(float a){
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float hash(vec2 p){
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}

float noise(vec2 p){
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * (3.0 - 2.0 * f);
    float a = hash(i + vec2(0.0, 0.0));
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(a, b, u.x) +
           (c - a) * u.y * (1.0 - u.x) +
           (d - b) * u.x * u.y;
}

float fbm(vec2 p){
    float v = 0.0;
    float amp = 0.5;
    for(int i = 0; i < 5; i++){
        v += amp * noise(p);
        p = rot(PI * 0.5) * p * 2.0;
        amp *= 0.5;
    }
    return v;
}

void main(){
    vec2 uv = (gl_FragCoord.xy * 2.0 - u_resolution.xy) / u_resolution.y;
    float t = u_time * 0.3;

    // domain warp
    vec2 p = uv;
    for(int i = 0; i < 3; i++){
        p = rot(t + float(i) * 0.7) * p * 1.4 + vec2(fbm(p + t));
    }

    float v = fbm(p - t);
    vec3 col = 0.5 + 0.5 * cos(vec3(1.0, 1.3, 1.7) * (v + vec3(0.0, 0.5, 1.0)) + u_time * 0.7);

    gl_FragColor = vec4(col, 1.0);
}

