precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 80.
#define MAX_DIST 100.
#define SURF_DIST 0.001
#define PI 3.14159265359

mat3 rotX(float a){ float c=cos(a), s=sin(a); return mat3(1,0,0, 0,c,-s, 0,s,c); }
mat3 rotY(float a){ float c=cos(a), s=sin(a); return mat3(c,0,s, 0,1,0, -s,0,c); }

float map(vec3 p){
    // Rotating 3D kaleidoscope tunnel
    p *= 0.8;
    p = rotY(u_time * 0.25) * p;
    p = rotX(u_time * 0.13) * p;
    vec3 q = mod(p, 4.0) - 2.0;
    float d1 = length(q.xy) - (1.0 + 0.3 * sin(q.z * 3.0 + u_time * 1.2));
    float d2 = length(q.yz) - (1.0 + 0.3 * sin(q.x * 3.0 - u_time * 1.5));
    return min(d1, d2);
}

float raymarch(vec3 ro, vec3 rd){
    float t = 9.0;
    for(int i = 0; i < int(MAX_STEPS); i++){
        vec3 p = ro + rd * t;
        float d = map(p);
        if(d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

vec3 getNormal(vec3 p){
    vec2 e = vec2(0.0005, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

vec3 shade(vec3 ro, vec3 rd, float t){
    vec3 p = ro + rd * t;
    vec3 n = getNormal(p);
    vec3 light = normalize(vec3(0.5, 1.0, 0.3));
    float diff = clamp(dot(n, light), 0.0, 1.0);
    vec3 base = 0.2 + 0.5 * cos(vec3(0.0, 2.0, 4.0) + length(p) * 0.5 + u_time);
    return base * diff;
}

void main(){
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 1.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    vec3 ro = vec3(0.0, 0.0, -0.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    float t = raymarch(ro, rd);
    vec3 col = (t < MAX_DIST) 
        ? shade(ro, rd, t) 
