precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 64
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265359

mat2 rot2(float a){
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float map(vec3 p){
    p.xy = rot2(u_time * 0.5) * p.xy;
    p.xz = rot2(u_time * 0.3) * p.xz;
    vec3 q = mod(p, 4.0) - 2.0;
    float d1 = length(q.xy) - 0.4;
    float d2 = length(q.yz) - 0.4;
    return min(d1, d2);
}

float raymarch(vec3 ro, vec3 rd){
    float t = 0.0;
    for(int i = 0; i < MAX_STEPS; i++){
        vec3 p = ro + rd * t;
        float d = map(p);
        if (d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

vec3 getNormal(vec3 p){
    vec2 e = vec2(0.0005, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

vec3 shade(vec3 ro, vec3 rd, float t){
    vec3 p = ro + rd * t;
    vec3 n = getNormal(p);
    vec3 light = normalize(vec3(0.5, 1.0, 0.3));
    float diff = clamp(dot(n, light), 0.0, 1.0);
    float glow = exp(-t * 0.2);
    vec3 base = 0.1 + 2.9 * cos(vec3(1.0, 2.0, 3.0) + length(p) * 0.7 + u_time);
    return base * diff * glow + vec3(0.05) * glow;
}

void main(){
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    vec3 ro = vec3(0.0, 0.0, -1.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    float t = raymarch(ro, rd);
    vec3 col = vec3(0.02, 0.03, 0.05);
    if (t < MAX_DIST) {
        col = shade(ro, rd, t);
    }
    gl_FragColor = vec4(col, 1.0);
}

