frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS      100
#define MAX_DISTANCE   100.0
#define MIN_DISTANCE   0.0005
#define PI             3.14159265359

// ————————————————————————————————————————————————————————————
//  Mandelbulb distance estimator
float mandelbulbDE(vec3 pos) {
    vec3 z = pos;
    float dr = 1.0;
    float r  = 0.0;
    const int ITER = 12;
    for (int i = 0; i < ITER; i++) {
        r = length(z);
        if (r > 2.0) break;
        // spherical coords
        float theta = acos(z.z / r);
        float phi   = atan(z.y, z.x);
        // animate the “power” of the fractal
        float power = 8.0 + sin(u_time * 0.1) * 0.5;
        // scale & derivative
        float zr = pow(r, power);
        dr = pow(r, power - 1.0) * power * dr + 1.0;
        // rotate in fractal space
        theta *= power;
        phi   *= power;
        // back to cartesian
        z = zr * vec3(sin(theta) * cos(phi),
                      sin(phi)   * sin(theta),
                      cos(theta));
        z += pos;
    }
    return 0.5 * log(r) * r / dr;
}

// ————————————————————————————————————————————————————————————
//  Sphere‑tracing
float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = mandelbulbDE(p);
        if (d < MIN_DISTANCE || t > MAX_DISTANCE) break;
        t += d;
    }
    return t;
}

// ————————————————————————————————————————————————————————————
//  Estimate normal via gradient
vec3 getNormal(vec3 p) {
    float h = 0.001;
    return normalize(vec3(
        mandelbulbDE(p + vec3(h, 0, 0)) - mandelbulbDE(p - vec3(h, 0, 0)),
        mandelbulbDE(p + vec3(0, h, 0)) - mandelbulbDE(p - vec3(0, h, 0)),
        mandelbulbDE(p + vec3(0, 0, h)) - mandelbulbDE(p - vec3(0, 0, h))
    ));
}

// ————————————————————————————————————————————————————————————
//  2×2 rotation around Z
mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

void main() {
    // — Normalize pixel coords
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // — Camera setup
    vec3 ro = vec3(0.0, 0.0, -4.0);
    // gently orbit the camera around Y
    float camAng = u_time * 0.2;
    ro.xz = rot(camAng) * ro.xz;

    // — Ray direction
    vec3 rd = normalize(vec3(uv, 1.0));
    rd.xz = rot(camAng) * rd.xz;

    // — Ray‑march into the fractal
    float t = rayMarch(ro, rd);

    vec3 col = vec3(0.0);
    if (t < MAX_DISTANCE) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);

        // — Lighting
        vec3 light = normalize(vec3(0.5, 1.0, 0.3));
        float diff = clamp(dot(n, light), 0.0, 1.0);
        float ao   = pow(diff, 0.5);

        // — Color palette (orbit‑trap style)
        vec3 base = 0.5 + 0.5 * cos(
            vec3(1.0, 1.3, 1.7) * (length(p) * 0.2 + u_time * 0.3)
          + vec3(0.0, 2.0, 4.0)
        );

        col = base * diff * ao;
    }

    gl_FragColor = vec4(col, 1.0);
}

