precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS   80
#define MAX_DIST    50.0
#define SURF_DIST   0.001
#define PI          3.14159265359

// ————————————————————————————————————————————————————————————
//  Basic rotations
mat3 rotX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(
        1.0, 0.0, 0.0,
        0.0,   c,  -s,
        0.0,   s,   c
    );
}
mat3 rotY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(
          c, 0.0,   s,
        0.0, 1.0, 0.0,
         -s, 0.0,   c
    );
}

// ————————————————————————————————————————————————————————————
//  Superformula radial function
float superformula(float phi, float m, float n1, float n2, float n3) {
    float a = 1.0;
    float b = 1.0;
    float t1 = pow(abs(cos(m * phi * 0.25) / a), n2);
    float t2 = pow(abs(sin(m * phi * 0.25) / b), n3);
    return pow(t1 + t2, -1.0 / n1);
}

// ————————————————————————————————————————————————————————————
//  Scene SDF: a morphing superformula “cosmic sculpture”
float sceneSDF(vec3 p) {
    // Animate superformula parameters
    float t = u_time * 0.3;
    float m1 = 4.0 + sin(t * 1.1) * 2.0;
    float m2 = 6.0 + cos(t * 0.7) * 1.5;
    float n1_1 = 0.3 + 0.2 * sin(t * 0.9);
    float n1_2 = 1.7 + 0.5 * cos(t * 0.5);
    float n1_3 = 1.7 + 0.4 * sin(t * 0.6);
    float n2_1 = 0.3 + 0.2 * cos(t * 0.8);
    float n2_2 = 1.2 + 0.3 * sin(t * 0.4);
    float n2_3 = 0.7 + 0.3 * cos(t * 1.3);

    // Convert to spherical coords
    vec3 v = p;
    float rlen = length(v) + 0.0001;
    float phi   = atan(v.y, v.x);
    float theta = acos(clamp(v.z / rlen, -1.0, 1.0));

    // Compute radii
    float r1 = superformula(phi, m1, n1_1, n1_2, n1_3);
    float r2 = superformula(theta, m2, n2_1, n2_2, n2_3);

    // Surface point on ideal shape
    vec3 surf = vec3(
        r1 * cos(phi) * sin(theta),
        r1 * sin(phi) * sin(theta),
        r2 * cos(theta)
    );

    // SDF is distance to that surface minus a small thickness
    return length(p - surf) - 0.02;
}

// ————————————————————————————————————————————————————————————
//  Ray march into the scene
float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 pos = ro + rd * t;
        float d = sceneSDF(pos);
        if (d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

// ————————————————————————————————————————————————————————————
//  Estimate normal via gradient
vec3 getNormal(vec3 p) {
    float h = 0.0005;
    return normalize(vec3(
        sceneSDF(p + vec3( h, 0, 0)) - sceneSDF(p - vec3( h, 0, 0)),
        sceneSDF(p + vec3(0,  h, 0)) - sceneSDF(p - vec3(0,  h, 0)),
        sceneSDF(p + vec3(0, 0,  h)) - sceneSDF(p - vec3(0, 0,  h))
    ));
}

// ————————————————————————————————————————————————————————————
//  Main
void main() {
    // Normalize pixel coords
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // Camera orbiting slowly
    float ca = u_time * 0.2;
    vec3 ro = vec3(0.0, 0.0, 5.0);
    ro.xz = (rotY(ca) * vec3(ro.x, 0.0, ro.z)).xz;

    // Ray direction
    vec3 rd = normalize(vec3(uv, -1.5));
    rd.xz = (rotY(ca) * vec3(rd.x, 0.0, rd.z)).xz;

    // Ray march
    float t = rayMarch(ro, rd);

    // Background color
    vec3 col = vec3(0.02, 0.03, 0.05) + 0.2 * uv.y;

    // If we hit the shape
    if (t < MAX_DIST) {
        vec3 pos = ro + rd * t;
        vec3 n   = getNormal(pos);

        // Simple lighting
        vec3 light = normalize(vec3(0.5, 1.0, 0.3));
        float diff = clamp(dot(n, light), 0.0, 1.0);
        float rim  = pow(1.0 - max(dot(n, -rd), 0.0), 3.0);

        // Dynamic cosmic palette
        vec3 base = 0.5 + 0.5 * cos(
            vec3(0.0, 2.0, 4.0) + length(pos) * 0.5 + u_time * 0.6
        );

        col = base * diff + rim * vec3(1.0, 0.8, 0.6) * 0.3;
    }

    // Tone mapping & gamma
    col = col / (col + vec3(1.0));
    col = pow(col, vec3(0.4545));

    gl_FragColor = vec4(col, 1.0);
}

