precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS  100
#define MAX_DIST   50.0
#define SURF_DIST  0.002
#define PI         3.14159265359

// — Superformula radius in 2D
float superR(float phi, float m, float n1, float n2, float n3) {
    float a = 1.0, b = 1.0;
    float t1 = pow(abs(cos(m * phi) / a), n2);
    float t2 = pow(abs(sin(m * phi) / b), n3);
    return pow(t1 + t2, -1.0 / n1);
}

// — Rotate around Y
mat3 rotY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(
        c, 0.0, s,
      0.0, 1.0, 0.0,
       -s, 0.0, c
    );
}

// — Tile repetition
vec3 opRep(vec3 p, vec3 c) {
    return mod(p + 0.5 * c, c) - 0.5 * c;
}

// — SDF of extruded superformula flower
float flowerSDF(vec3 p) {
    float theta = atan(p.y, p.x);
    float t = u_time * 0.5;
    float m   = 6.0 + sin(t) * 2.0;
    float n1  = 0.3 + 0.2 * sin(t * 1.3);
    float n2  = 1.0 + 0.5 * cos(t * 0.7);
    float n3  = 1.0 + 0.5 * sin(t * 0.9);
    float R   = superR(theta, m, n1, n2, n3) * 0.5 + 0.3;
    float d2d = length(p.xy) - R;
    float thickness = 0.1 + 0.05 * sin(length(p.xy) * 5.0 - t);
    return max(d2d, abs(p.z) - thickness);
}

// — Scene SDF: grid of flowers
float sceneSDF(vec3 p) {
    vec3 q = opRep(p, vec3(2.0, 0.0, 2.0));
    return flowerSDF(q);
}

// — Ray march
float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 pos = ro + rd * t;
        float d = sceneSDF(pos);
        if (d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

// — Estimate normal
vec3 getNormal(vec3 p) {
    float h = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(h, 0, 0)) - sceneSDF(p - vec3(h, 0, 0)),
        sceneSDF(p + vec3(0, h, 0)) - sceneSDF(p - vec3(0, h, 0)),
        sceneSDF(p + vec3(0, 0, h)) - sceneSDF(p - vec3(0, 0, h))
    ));
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // Camera setup
    float tCam = u_time * 0.3;
    vec3 ro = vec3(sin(tCam) * 4.0, 1.0, cos(tCam) * 4.0);
    vec3 look = vec3(0.0);
    vec3 forward = normalize(look - ro);
    vec3 right   = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up      = cross(forward, right);
    vec3 rd      = normalize(forward + uv.x * right + uv.y * up);

    // Raymarch
    float dist = rayMarch(ro, rd);
    vec3 col = vec3(0.01, 0.02, 0.03) + 0.4 * rd.y;

    if (dist < MAX_DIST) {
        vec3 pos = ro + rd * dist;
        vec3 n   = getNormal(pos);
        vec3 light = normalize(vec3(0.5, 1.0, 0.3));
        float diff = clamp(dot(n, light), 0.0, 1.0);
        float glow = exp(-dist * 0.2);

        // cell ID for hue
        vec2 id = floor((pos.xz + vec2(100.0)) * 0.5);
        float hue = fract((id.x + id.y) * 0.37 + tCam * 0.1);
        vec3 base = 0.5 + 0.5 * cos(vec3(0.0, 2.0, 4.0) + hue * 6.283 + vec3(0.0, 1.0, 2.0));

        col = base * diff * glow + base * 0.1 * glow;
    }

    // Tone & gamma
    col = col / (col + vec3(1.0));
    col = pow(col, vec3(0.4545));

    gl_FragColor = vec4(col, 1.0);
}

