precision highp float;
#endif

uniform float uTime;
uniform vec2  uResolution;
uniform vec3  uColor;

// ——————————————————
// Hash / noise / fbm for organic warping
float hash(vec2 p) {
    return fract(sin(dot(p,vec2(127.1,311.7))) * 43758.5453);
}
float noise(vec2 p) {
    vec2 i = floor(p), f = fract(p);
    float a = hash(i), b = hash(i+vec2(1.0,0.0));
    float c = hash(i+vec2(0.0,1.0)), d = hash(i+vec2(1.0,1.0));
    vec2 u = f*f*(3.0-2.0*f);
    return mix(mix(a,b,u.x), mix(c,d,u.x), u.y);
}
float fbm(vec2 p) {
    float v = 0.0, amp = 0.5;
    mat2 m = mat2(1.6, 1.2, -1.2, 1.6);
    for(int i=0; i<4; i++){
        v   += amp * noise(p);
        p    = m * p;
        amp *= 0.5;
    }
    return v;
}

// Soft circular “particle”
float circle(vec2 p, float r){
    return smoothstep(r, r-0.01, length(p));
}

// 2D rotate
mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// Smooth rainbow palette
vec3 palette(float t){
    return 0.5 + 0.5 * cos(6.2831 * (vec3(0.0, 0.33, 0.67) + t)
                          + vec3(0.0, 2.0, 4.0));
}

void main(){
    // normalize coords to [-1,1]
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution) 
            / min(uResolution.x, uResolution.y);

    // domain‑warp the UV a bit for extra swirl
    vec2 warp = vec2(fbm(uv*3.0 + uTime),
                     fbm(uv*3.0 - uTime));
    uv += warp * 0.2;

    vec3 col = vec3(0.0);
    float t = uTime * 0.5;

    // stack a few “grid” layers in depth
    for(int L = 0; L < 5; L++){
        float layer = float(L);
        float depth = pow(1.7, layer);

        // zoom UV into this layer
        vec2 luv = uv * depth;

        // dynamic grid spacing
        float spacing = 0.5 + 0.2 * sin(t*0.6 + layer);
        vec2  id     = floor(luv / spacing);
        vec2  gv     = mod(luv, spacing) - spacing * 0.5;

        // per‑cell jitter
        vec2 jitter = (vec2(hash(id), hash(id+1.0)) - 0.5) * spacing;
        vec2 p      = (gv + jitter) / spacing;

