    float vig = smoothstep(1.2, 0.5,
                    length((gl_FragCoord.xy/uResolution) - 0.5));
    col *= mix(1.0, 0.6, vig);

    // final boost & gamma, then tint
    col = pow(col, vec3(0.8)) * uColor;

    gl_FragColor = vec4(col, 1.0);
