uniform float u_time;
uniform vec2 u_resolution;

// ————————————————————
// 2D Hash and Noise from Inigo Quilez
// ————————————————————
float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

float noise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    // Smoothstep
    vec2 u = f * f * (3.0 - 2.0 * f);
    float a = hash(i + vec2(0.0,0.0));
    float b = hash(i + vec2(1.0,0.0));
    float c = hash(i + vec2(0.0,1.0));
    float d = hash(i + vec2(1.0,1.0));
    return mix(mix(a,b,u.x), mix(c,d,u.x), u.y);
}

// Fractal Brownian Motion
float fbm(vec2 p) {
    float v = 0.0;
    float amp = 0.5;
    for(int i = 0; i < 5; i++){
        v += amp * noise(p);
        p *= 2.0;
        amp *= 0.5;
    }
    return v;
}

// Smooth palette by Inigo Quilez
vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b * cos(6.28318 * (c*t + d));
}

void main() {
    // Normalize pixel coords (–1.0 to +1.0)
    vec2 uv = (gl_FragCoord.xy / u_resolution.xy) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Time and scale
    float t = u_time * 0.2;

    // Domain warp
    vec2 p = uv * 2.5;
    vec2 q = vec2(fbm(p + t), fbm(p - t));
    vec2 r = vec2(fbm(p + 4.0*q + vec2(1.7,9.2)),
                  fbm(p + 4.0*q + vec2(8.3,2.8)));

    float f = fbm(p + 4.0*r);

    // Color palette parameters
    vec3 col = palette(
      f + 0.3*t,
      vec3(0.5),                // a: base
      vec3(0.5),                // b: amplitude
      vec3(1.0, 1.0, 1.0),      // c: frequency
      vec3(0.00, 0.33, 0.67)    // d: phase shifts
    );

    gl_FragColor = vec4(col, 1.0);
}

