precision highp float;
            uniform float uTime;
            uniform vec2 uResolution;
            uniform vec3 uColor;

            // Perlin noise functions for smooth aurora waves
            vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
            vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
            vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

            float snoise(vec2 v) {
                const vec4 C = vec4(0.211324865405187, 0.366025403784439,
                                    -0.577350269189626, 0.024390243902439);
                vec2 i  = floor(v + dot(v, C.yy));
                vec2 x0 = v -   i + dot(i, C.xx);
                vec2 i1;
                i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
                vec4 x12 = x0.xyxy + C.xxzz;
                x12.xy -= i1;
                i = mod289(i);
                vec3 p = permute(permute(i.y + vec3(0.0, i1.y, 1.0))
                      + i.x + vec3(0.0, i1.x, 1.0));
                vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
                m = m*m;
                m = m*m;
                vec3 x = 2.0 * fract(p * C.www) - 1.0;
                vec3 h = abs(x) - 0.5;
                vec3 ox = floor(x + 0.5);
                vec3 a0 = x - ox;
                m *= 1.79284291400159 - 0.85373472095314 * (a0*a0 + h*h);
                vec3 g;
                g.x  = a0.x  * x0.x  + h.x  * x0.y;
                g.yz = a0.yz * x12.xz + h.yz * x12.yw;
                return 130.0 * dot(m, g);
            }

            // Fractal Brownian Motion
            float fbm(vec2 p) {
                float f = 0.0;
                float w = 0.5;
                for (int i = 0; i < 6; i++) {
                    f += w * snoise(p);
                    p *= 2.0;
                    w *= 0.5;
                }
                return f;
            }

            // Color mapping for aurora
            vec3 aurora_color(float t) {
                vec3 a = vec3(0.0, 0.5, 0.1);
                vec3 b = vec3(0.0, 0.2, 0.0);
                vec3 c = vec3(0.1, 0.5, 0.3);
                vec3 d = vec3(0.0, 0.1, 0.2);

                return a + b * cos(6.28318 * (c * t + d));
            }

            void main() {
                vec2 uv = gl_FragCoord.xy / uResolution.xy;
                vec2 pos = vec2(uv.x, uv.y * 0.8 + 0.2); // Position aurora in upper part
                float t = uTime * 0.15;

                // Create starry night sky
                vec3 skyColor = vec3(0.0, 0.02, 0.05);
                float stars = 0.0;
                vec2 grid = fract(uv * 50.0) - 0.5;
                stars = smoothstep(0.94, 0.945, 1.0 - length(grid)) * 
                        (0.5 + 0.5 * sin(uTime + fract(sin(dot(floor(uv * 50.0), vec2(12.9898, 78.233))) * 43758.5453)));
                skyColor += vec3(stars);

                // Generate aurora waves
                float y_pos = pos.y * 2.0 - 1.8; // Position for wave cutoffs
                float curtain_pos = pos.x * 2.0 - 1.0 + 0.1 * sin(t + pos.y * 5.0);

                // Create multiple aurora layers
                vec3 finalColor = skyColor;

                for (int i = 0; i < 3; i++) {
                    float layer_offset = float(i) * 0.3;
                    float wave_height = 0.15 + float(i) * 0.05;
                    float speed_factor = 1.0 - float(i) * 0.2;

                    // Get aurora wave position
                    float wave = fbm(vec2(pos.x * 5.0, t * speed_factor + layer_offset)) * wave_height;
                    float mask = smoothstep(0.0, 0.3, wave - y_pos);

                    // Create vertical curtain effect
                    float curtain = exp(-30.0 * abs(curtain_pos - float(i) * 0.4));
                    mask *= curtain;

                    // Add vertical rays
                    float rays = exp(-5.0 * abs(fract(pos.x * 10.0 + float(i) * 0.1 + t * 0.5) - 0.5));
                    mask *= mix(0.5, 1.0, rays);

                    // Color variation based on position and time
                    float color_pos = pos.y + wave * 0.1 + t * 0.05 + float(i) * 0.2;
                    vec3 auroraColor = aurora_color(color_pos);

                    // Add glow
                    float glow = exp(-2.0 * max(0.0, y_pos - wave + 0.1));
                    auroraColor *= vec3(0.8, 1.0, 0.9) * glow * 0.5;

                    // Add color to final result
                    finalColor = mix(finalColor, auroraColor, mask);
                }

                // Add subtle northern light to the sky
                float sky_glow = exp(-5.0 * pos.y);
                finalColor += vec3(0.0, 0.05, 0.1) * sky_glow;

                // Apply user color tint
                finalColor *= uColor;

                gl_FragColor = vec4(finalColor, 1.0);
            }

