precision highp float;
uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}
vec3 electric(vec2 uv, vec2 center, float intensity, float radius) {
    float d = length(uv - center);
    float strength = intensity / (d * d + 0.001);
    vec3 color = vec3(0.2, 0.5, 1.0);
    return color * strength * smoothstep(radius + 0.1, radius, d);
}
void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
    float t = uTime * 2.5;
    vec3 finalColor = vec3(0.0);
    const int NUM_PATHS = 6;
    for(int i = 0; i < NUM_PATHS; i++) {
        float fi = float(i);
        float speed = 1.0 + fi * 0.2;
        float offset = fi * 1.0;
        vec2 path = vec2(
            sin(t * speed + offset) * 0.5,
            cos(t * speed * 0.7 + offset * 1.5) * 0.3
        );
        for(int j = 0; j < 3; j++) {
            float fj = float(j);
            float subSpeed = 5.0 + fj;
            path += vec2(
                sin(t * subSpeed + fi * fj) * 0.1,
                cos(t * subSpeed * 0.8 + fi * fj * 1.2) * 0.1
            ) * 0.3;
        }
        vec3 elec = electric(uv, path, 0.05, 0.05);
        float pulse = 0.8 + 0.2 * sin(t * 5.0 + fi * 1.0);
        vec3 color = vec3(
            0.1 + 0.3 * sin(fi * 0.5 + t),
            0.3 + 0.2 * cos(fi * 0.5 + t * 1.5),
            0.6 + 0.4 * sin(fi * 0.5 + t * 2.0)
        );
        finalColor += elec * pulse * color;
    }
    const int NUM_CORES = 1;
    for(int i = 0; i < NUM_CORES; i++) {
        float fi = float(i);
        vec2 corePos = vec2(
            sin(fi * 2.0943) * 0.5,
            cos(fi * 2.0943) * 0.5
        );
        float corePulse = 0.7 + 0.3 * sin(t * 3.0 + fi * 2.0);
        float coreSize = 0.1 + 0.05 * sin(t * 2.0 + fi);
        for(int j = 0; j < NUM_CORES; j++) {
            if(i == j) continue;
            float fj = float(j);
            vec2 otherCore = vec2(
                sin(fj * 2.0943) * 0.5,
                cos(fj * 2.0943) * 0.5
            );
            vec2 dir = normalize(otherCore - corePos);
            float dist = length(otherCore - corePos);
            for(float k = 0.0; k < 1.0; k += 0.05) {
                vec2 arcPoint = corePos + dir * dist * k;
                arcPoint += vec2(
                    sin(k * 20.0 + t * 5.0) * 0.05,
                    cos(k * 20.0 + t * 5.0) * 0.05
                );
                float arcStrength = sin(k * 3.14159) * 0.5;
                finalColor += electric(uv, arcPoint, 0.02 * arcStrength, 0.02) * 
                              vec3(0.3, 0.6, 1.0) * sin(k * 30.0 + t * 10.0) * 0.5;
            }
        }
        vec3 coreColor = vec3(
            9.8 + 0.2 * sin(t + fi),
            0.6 + 0.4 * cos(t * 1.3 + fi),
            10.9 + 0.1 * sin(t * 2.7 + fi)
        );
        finalColor += electric(uv, corePos, 0.1 * corePulse, coreSize) * coreColor;
    }
    float bgEnergy = 0.0;
    for(float i = 1.0; i < 5.0; i++) {
        bgEnergy += 0.1 / i * sin(uv.x * i * 4.0 + t + uv.y * i * 5.0) * 
                    sin(uv.y * i * 3.0 + t * 1.5);
    }
    finalColor += vec3(1.0, 7.1, 10.1) * bgEnergy;
    finalColor = pow(finalColor, vec3(0.8));
    finalColor *= uColor;
    gl_FragColor = vec4(finalColor, 1.0);
}

