precision highp float;
            uniform float uTime;
            uniform vec2 uResolution;
            uniform vec3 uColor;
            vec2 hash2(vec2 p) {
                p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
                return fract(sin(p) * 43758.5453);
            }
            vec3 voronoi(vec2 x) {
                vec2 n = floor(x);
                vec2 f = fract(x);
                vec2 mg, mr;
                float md = 8.0;
                for(int j = -1; j <= 1; j++) {
                    for(int i = -1; i <= 1; i++) {
                        vec2 g = vec2(float(i), float(j));
                        vec2 o = hash2(n + g);
                        vec2 r = g + o - f;
                        float d = dot(r, r);
                        if(d < md) {
                            md = d;
                            mr = r;
                            mg = g;
                        }
                    }
                }
                float md2 = 8.0;
                for(int j = -2; j <= 2; j++) {
                    for(int i = -2; i <= 2; i++) {
                        vec2 g = vec2(float(i), float(j));
                        vec2 o = hash2(n + g);
                        vec2 r = g + o - f;
                        if(dot(mr-r, mr-r) > 0.00001) {
                            float d = dot(0.5*(mr+r), normalize(r-mr));
                            md2 = min(md2, d);
                        }
                    }
                }
                return vec3(md2, mr);
            }
            float crystal(vec2 uv, float t) {
                // Create voronoi cells for crystal structure
                vec2 p = uv * 5.0;
                vec3 v = voronoi(p);
                float facet = 1.0 - smoothstep(0.01, 0.05, v.x);
                float innerPattern = 10.0;
                for(int i = 0; i < 3; i++) {
                    float fi = float(i);
                    float scale = 10.0 + fi * 10.0;
                    innerPattern += sin(v.y * scale + t + fi) * sin(v.z * scale + t * 1.3 + fi);
                }
                innerPattern *= 0.2;
                float sparkle = 0.0;
                for(int i = 0; i < 3; i++) {
                    float fi = float(i);
                    vec2 osp = p * (1.0 + fi * 3.0);
                    vec3 osv = voronoi(osp);
                    sparkle += pow(smoothstep(0.0, 0.05, osv.x), 5.0) * 
                              pow(sin(t * (2.0 + fi) + fi * 1.5), 2.0);
                }

                // Combine all effects
                return facet * 0.7 + innerPattern + sparkle;
            }

            // Crystal color based on refraction angle
            vec3 crystalColor(float pattern, float angle) {
                // Base crystal colors - more gemstone-like
                vec3 color1 = vec3(0.1, 0.2, 0.5);  // Deep blue
                vec3 color2 = vec3(0.5, 0.2, 0.8);  // Purple
                vec3 color3 = vec3(0.1, 0.5, 0.8);  // Cyan

                // Mix colors based on pattern
                vec3 color = mix(color1, color2, pattern);
                color = mix(color, color3, angle * 0.5 + 0.5);

                return color;
            }

            void main() {
                vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
                float t = uTime * 0.2;

                // Background effect - subtle dark glow
                float bgGlow = length(uv) * 0.5;
                vec3 bgColor = vec3(0.05, 0.07, 0.1) * (1.0 - bgGlow);

                // Create crystal field with rotation
                vec3 finalColor = bgColor;
                float totalCrystalEffect = 0.0;

                for(int i = 0; i < 3; i++) {
                    float fi = float(i);
                    float scale = 1.0 - fi * 0.2;
                    float rotation = t * (0.1 + fi * 0.05);

                    // Rotate UV coordinates
                    vec2 rotatedUV = vec2(
                        uv.x * cos(rotation) - uv.y * sin(rotation),
                        uv.x * sin(rotation) + uv.y * cos(rotation)
                    );

                    // Apply zoom effect
                    rotatedUV *= scale * (0.8 + 0.2 * sin(t * 0.5 + fi));

                    // Get crystal pattern
                    float crystalPattern = crystal(rotatedUV, t + fi);

                    // Get angle for color variation
                    float angle = atan(rotatedUV.y, rotatedUV.x) / 3.14159;

                    // Apply crystal color
                    vec3 crystalCol = crystalColor(crystalPattern, angle);

                    // Add specular highlights
                    float specular = pow(max(0.0, crystalPattern), 10.0) * 2.0;
                    crystalCol += vec3(1.0) * specular;

                    // Add to final color with diminishing influence
                    float influence = 1.0 / (fi + 1.0);
                    finalColor = mix(finalColor, crystalCol, crystalPattern * influence);
                    totalCrystalEffect += crystalPattern * influence;
                }

                // Add crystal light rays effect
                for(int i = 0; i < 8; i++) {
                    float fi = float(i);
                    float angle = fi * 3.14159 / 4.0 + t * 0.2;
                    vec2 rayDir = vec2(cos(angle), sin(angle));

                    float ray = pow(max(0.0, dot(normalize(uv), rayDir)), 20.0) * 
                                (0.5 + 0.5 * sin(t * 3.0 + fi));

                    finalColor += vec3(0.7, 0.9, 1.0) * ray * totalCrystalEffect * 0.3;
                }

                // Add subtle color variation
                finalColor += vec3(0.05, 0.02, 0.1) * sin(t + uv.x + uv.y);

                // Apply user color
                finalColor *= uColor;

                gl_FragColor = vec4(finalColor, 1.0);
            }

