#define PI 3.14159
        vec3 field(vec3 p) {
            p += sin(p.zxy * PI * uTime * 0.1);
            float d = length(p);
            return cos(p*d*0.5 + uTime) * sin(p.yzx*d*0.3);
        }
        void main() {
            vec2 uv = (gl_FragCoord.xy*2.0 - uResolution.xy)/uResolution.y;
            vec3 ro = vec3(0,0,-3);
