            rd.xy = mat2(cos(uTime*0.3), -sin(uTime*0.3),
                         sin(uTime*0.3), cos(uTime*0.3)) * rd.xy;

            vec3 col = vec3(0);
            float depth = PI * 2.0;
            for(int i=0; i<ITER; i++) {
                vec3 p = ro + rd*depth;
                vec3 f = field(p * 0.5);
                float intensity = 1.0 / (1.0 + dot(f,f));
                col += intensity * mix(vec3(0.2,0.3,1.0), vec3(1.0,0.3,0.2), intensity);
                depth += 0.1 + 0.05*intensity;
            }

            col = pow(col * 0.03, vec3(1.5));
            col *= uColor * 2.0;
            gl_FragColor = vec4(col,1.0);
        }

