mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// 2D noise (simple hash)
float hash(vec2 p) {
    p = fract(p * vec2(127.1, 311.7));
    return fract(p.x * p.y * 43758.5453);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);
    return mix(
        mix(hash(i), hash(i + vec2(1.0, 0.0)), f.x),
        mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), f.x),
        f.y
    );
}

// Smooth min
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// SDF for circle
float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution) / min(uResolution.x, uResolution.y);
    float t = uTime * 0.2;

    // Dynamic camera wobble
    uv += 0.1 * vec2(sin(t * 0.7), cos(t * 0.5));

    vec3 color = vec3(0.0);
    float glow = 0.0;

    // Fractal loops
    const int ITER = 5;
    float scale = 1.0;
    for (int i = 0; i < ITER; i++) {
        vec2 p = uv * scale;
        p = rot(t * 0.3 + float(i)) * p;
        p = abs(p) - vec2(0.5);
        float d = sdCircle(p, 0.3);
        float f = 1.0 / (abs(d) * float(i + 1));
        vec3 pal = vec3(
            0.5 + 0.5 * sin(6.2831 * (d + t + float(i) * 0.4)),
            0.5 + 0.5 * cos(6.2831 * (d + t * 1.1 + float(i)) ),
            0.5 + 0.5 * sin(6.2831 * (d * 0.8 - t * 0.5 + float(i) * 1.3))
        );
        color += pal * f;
        glow += f;
        scale *= 1.8;
    }

    // Add noise overlay
    float n = noise(uv * 3.0 + t);
    color += (n - 0.5) * 0.1;

    // Tone mapping and color control
    color = color / (color + vec3(1.0));
    color = pow(color, vec3(0.8));
    color *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);

    gl_FragColor = vec4(color, 1.0);
}

