frag:
// Fragment Shader - Cosmic Flow
precision highp float;

// Uniforms
uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;
uniform float uIntensity;

// Constants
const float PI = 3.14159265359;
const float TAU = 6.28318530718;

// Optimized hash function for pseudo-randomness
float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 78.23);
    return fract(p.x * p.y);
}

// Smooth noise function
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Cubic Hermite interpolation
    f = f * f * (3.0 - 2.0 * f);

    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

// FBM (Fractional Brownian Motion) for layered noise
float fbm(vec2 p) {
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 1.0;

    // Sum several noise layers
    for (int i = 0; i < 5; i++) {
        value += amplitude * noise(p * frequency);
        amplitude *= 0.5;
        frequency *= 2.0;
    }

    return value;
}

// Dynamic color palette
vec3 palette(float t) {
    // Base parameters
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 0.7, 0.4);
    vec3 d = vec3(0.00, 0.15, 0.20) + vec3(sin(uTime * 0.2) * 0.1, cos(uTime * 0.17) * 0.1, sin(uTime * 0.23) * 0.1);

    // Create color using cosine waves
    return a + b * cos(TAU * (c * t + d));
}

// Rotation matrix
mat2 rotate2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

// Smooth minimum function for shape blending
float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h * h * h * k * (1.0/6.0);
}

// Signed distance function for a 2D circle
float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

// Variation on circles with warp
float flowCircle(vec2 p, float time, float scale) {
    // Add flowing movement
    p += vec2(
        sin(p.y * 1.5 + time) * 0.2,
        cos(p.x * 1.5 + time) * 0.2
    );

    // Base circle
    float d = sdCircle(p, 0.5 * scale);

    // Add noise ripples
    float ripple = fbm(p * 3.0 + time * 0.1) * 0.1;
    d += ripple * scale;

    return d;
}

// Cosmic flow field
vec2 flowField(vec2 p, float time) {
    // Create flowing vector field
    float angle = fbm(p * 2.0 + time * 0.1) * TAU;
    return vec2(cos(angle), sin(angle)) * 0.02;
}

// Vortex function
float vortex(vec2 p, float time) {
    float d = length(p);
    float angle = atan(p.y, p.x) + d * 2.0 + time * 0.2;
    vec2 offset = vec2(cos(angle), sin(angle)) * 0.3;
    return fbm((p + offset) * 3.0) * (1.0 - d * 0.5);
}

void main() {
    // Normalized coordinates with aspect ratio correction 
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);

    // Initialize color
    vec3 finalColor = vec3(0.0);

    // Slow down time for smoother animation
    float time = uTime * 0.2;

    // Create motion with camera offset
    vec2 camOffset = vec2(sin(time * 0.3) * 0.2, cos(time * 0.27) * 0.15);
    vec2 p = uv + camOffset;

    // Apply several iterations of flow deformation
    for (int i = 0; i < 5; i++) {
        p += flowField(p, time + float(i) * 0.5);
    }

    // Create multiple layers of flowing circles
    const int LAYERS = 4;
    for (int i = 0; i < LAYERS; i++) {
        float fi = float(i);
        float t = time + fi * PI / float(LAYERS);

        // Scale and position based on layer
        float scale = 1.0 + fi * 0.5;
        vec2 pos = p;

        // Rotate each layer differently
        pos = rotate2D(t * 0.1 + fi * 0.2) * pos;

        // Add some movement
        pos += vec2(sin(t * 0.31), cos(t * 0.27)) * 0.3;

        // Calculate distance field
        float d = flowCircle(pos, t, scale);

        // Create glow based on distance
        float glow = 0.02 / (0.02 + abs(d) * (1.0 + 0.5 * sin(t)));
        glow = pow(glow, 1.2);

        // Get color from palette
        vec3 col = palette(d * 0.2 + t * 0.1 + fi * 0.25);

        // Layer opacity decreases with depth
        float opacity = 1.0 / (fi + 1.5);

        // Add to final color
        finalColor += col * glow * opacity;
    }

    // Add vortex effects
    float vortexEffect = vortex(uv, time) * 0.5;
    finalColor += palette(vortexEffect + time * 0.3) * vortexEffect;

    // Add energy center
    float energy = 0.03 / (0.05 + length(uv - camOffset) * 2.0);
    energy *= 1.0 + 0.5 * sin(time * 3.0);
    finalColor += palette(time * 0.5) * energy * 2.0;

    // Add subtle background noise
    float bgNoise = fbm(uv * 5.0 + time * 0.1) * 0.03;
    finalColor += vec3(bgNoise) * palette(time * 0.07);

    // Apply vignette effect
    float vignette = 1.0 - dot(uv * 0.5, uv * 0.5);
    vignette = pow(vignette, 1.5);
    finalColor *= vignette;

    // Apply user color with intensity
    finalColor *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);

    // Tone mapping
    finalColor = finalColor / (1.0 + finalColor);

    // Gamma correction
    finalColor = pow(finalColor, vec3(0.8, 0.85, 0.9));

    // Add subtle film grain
    float grain = hash(uv + vec2(time * 0.01)) * 0.02 - 0.01;
    finalColor += grain;

    // Output final color
    gl_FragColor = vec4(finalColor, 1.0);
}

