precision highp float;
uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;
uniform float uIntensity;
const float PI = 3.14159265359;
const float TAU = 6.28318530718;
const float PHI = 1.61803398875;
vec4 permute(vec4 x) {
    return mod(((x*34.0)+1.0)*x, 9.0);
}
float snoise(vec3 v) {
    const vec2 C = vec2(1.0/6.0, 1.0/3.0);
    const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);
    vec3 i  = floor(v + dot(v, C.yyy));
    vec3 x0 = v - i + dot(i, C.xxx);
    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = 1.0 - g;
    vec3 i1 = min(g.xyz, l.zxy);
    vec3 i2 = max(g.xyz, l.zxy);
    vec3 x1 = x0 - i1 + C.xxx;
    vec3 x2 = x0 - i2 + C.yyy;
    vec3 x3 = x0 - D.yyy;
    i = mod(i, 289.0);
    vec4 p = permute(permute(permute(
             i.z + vec4(0.0, i1.z, i2.z, 1.0))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0))
           + i.x + vec4(0.0, i1.x, i2.x, 1.0));
    float n_ = 0.142857142857;
    vec3 ns = n_ * D.wyz - D.xzx;
    vec4 j = p - 49.0 * floor(p * ns.z * ns.z);
    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_);

    vec4 x = x_ *ns.x + ns.yyyy;
    vec4 y = y_ *ns.x + ns.yyyy;
    vec4 h = 1.0 - abs(x) - abs(y);
    vec4 b0 = vec4(x.xy, y.xy);
    vec4 b1 = vec4(x.zw, y.zw);
    vec4 s0 = floor(b0)*2.0 + 1.0;
    vec4 s1 = floor(b1)*2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));
    vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy;
    vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww;
    vec3 p0 = vec3(a0.xy, h.x);
    vec3 p1 = vec3(a0.zw, h.y);
    vec3 p2 = vec3(a1.xy, h.z);
    vec3 p3 = vec3(a1.zw, h.w);
    vec4 norm = 1.79284291400159 - 0.85373472095314 * vec4(dot(p0,p0), dot(p1,p1), dot(p2,p2), dot(p3,p3));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;
    vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
    m = m * m;
    return 42.0 * dot(m*m, vec4(dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3)));
}
vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 0.7, 0.4);
    vec3 d = vec3(0.00, 0.15, 0.20) + vec3(cos(uTime * 0.3) * 0.2, sin(uTime * 0.17) * 0.3, cos(uTime * 0.23) * 0.1);
    float s = sin(uTime * 0.1);
    float c1 = cos(uTime * 0.13);
    vec3 phase = d + vec3(t * 0.5 + s * 0.2, t * 0.8 + c1 * 0.1, t * 0.3);
    vec3 color = a + b * cos(TAU * (c * t + phase));
    color += vec3(0.1) * sin(TAU * t * 3.7 + uTime * 0.5);
    color += vec3(0.05) * sin(TAU * t * 5.3 - uTime * 0.7);
    return color;
}
mat2 rotate4D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}
float DE(vec4 p) {
    const int iterations = 8;
    float dr = 1.0;
    float scale = 1.6 + 0.1 * sin(uTime * 0.2);
    for (int i = 0; i < iterations; i++) {
        p = abs(p) - vec4(1.0, 0.5, 0.7, 0.3) * (1.0 + 0.1 * sin(uTime * 0.3 + float(i) * 0.5));
        p.xy = rotate4D(0.3 + 0.1 * sin(uTime * 0.2 + float(i) * 0.3)) * p.xy;
        p.xz = rotate4D(0.1 + 0.05 * cos(uTime * 0.3 + float(i) * 0.2)) * p.xz;
        p.yz = rotate4D(0.5 + 0.1 * sin(uTime * 0.15 + float(i) * 0.5)) * p.yz;
        p.xw = rotate4D(0.2 * sin(uTime * 0.35 + float(i) * 0.7)) * p.xw;
        p.yw = rotate4D(0.3 * cos(uTime * 0.25 + float(i) * 0.3)) * p.yw;
        p.zw = rotate4D(0.1 * sin(uTime * 0.15 + float(i) * 0.5)) * p.zw;
        p *= scale;
        dr *= scale;
    }
    float torus = length(vec2(length(p.xy) - 0.8, length(p.zw) - 0.4)) - 0.15;
    float sphere = length(p) - 1.0;
    float cube = max(max(abs(p.x), abs(p.y)), max(abs(p.z), abs(p.w))) - 0.8;
    float d = mix(mix(torus, sphere, 0.5 + 0.5 * sin(uTime * 0.2)), cube, 0.5 + 0.5 * sin(uTime * 0.37));
    return d / dr;
}
float tendril(vec2 uv, float t) {
    vec2 p = uv;
    float z = 0.5 + 0.5 * sin(t * 0.3);
    vec3 noisePos = vec3(p * 2.0, t * 0.2);
    vec3 noisePos2 = vec3(p * 1.5, t * 0.1 + 10.0);
    float n1 = snoise(noisePos);
    float n2 = snoise(noisePos2 * 2.0);
    float n3 = snoise(noisePos * 4.0 + vec3(0.0, 0.0, t * 0.3));
    float noise = n1 * 0.5 + n2 * 0.3 + n3 * 0.2;
    float len = length(p);
    float tendrils = smoothstep(0.3, 0.0, len - 0.1 * noise);
    tendrils *= 1.0 + 0.2 * sin(t * 3.0 + len * 10.0);
    return tendrils * (1.0 - len * 0.7);
}
float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h * h * h * k * (1.0/6.0);
}
void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
    float t = uTime * 0.15;
    vec2 camOffset = vec2(sin(t * 0.3) * 0.2, cos(t * 0.27) * 0.15);
    uv += camOffset;
    vec3 finalColor = vec3(0.0);
    float maxIterations = 4.0;
    if (min(uResolution.x, uResolution.y) < 800.0) maxIterations = 3.0;
    const int MAX_ITERATIONS = 5;
    for (int i = 0; i < MAX_ITERATIONS; i++) {
        if (float(i) >= maxIterations) break;
        float localTime = t + float(i) * 0.2;
        vec4 p = vec4(uv, sin(localTime * 0.3) * 0.5, cos(localTime * 0.27) * 0.5);
        float scale = pow(1.5, float(i)) * (1.0 + 0.1 * sin(t * 0.5 + float(i)));
        p *= scale;
        p.xy = rotate4D(localTime * 0.1) * p.xy;
        p.xz = rotate4D(localTime * 0.13) * p.xz;
        p.yz = rotate4D(localTime * 0.17) * p.yz;
        float d = DE(p);
        vec3 col = palette(d * 0.1 + localTime * 0.2 + float(i) * 0.3);
        vec2 tendrilUV = uv * (1.0 + 0.2 * sin(t * 0.1 + float(i)));
        float tend = tendril(tendrilUV, t + float(i) * PI);
        float glow = 0.01 / (0.0001 + abs(d) * (4.0 + sin(localTime) * 0.5));
        glow = pow(glow, 1.2);
        float intensity = max(glow, tend * 0.4);
        float alpha = 1.0 / (float(i) + 1.3);
        finalColor += col * intensity * alpha;
    }
    float overlayTendril = tendril(uv * 1.5, t * 1.2) * 0.6;
    finalColor += palette(overlayTendril + t * 0.3) * overlayTendril;
    float eP = 0.03 / (0.03 + length(uv - camOffset) * 3.0);
    eP *= 1.0 + 0.5 * sin(t * 3.0);
    finalColor += palette(t * 0.5) * eP * 3.0;
    vec3 bgNoise = vec3(snoise(vec3(uv * 3.0, t * 0.1)) * 0.03);
    finalColor += bgNoise * palette(t * 0.1);
    finalColor = mix(finalColor, 
                    finalColor * (1.0 + 0.1 * sin(t * 2.0)),
                    0.7 + 0.3 * sin(t * 0.3));
    finalColor = pow(finalColor, vec3(0.75, 0.8, 0.85));
    finalColor = finalColor / (1.0 + finalColor);
    finalColor *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);
    float grain = fract(sin(dot(uv + vec2(t * 0.1), vec2(12.9898, 78.233))) * 43758.5453);
    finalColor += (grain - 0.5) * 0.015;
    gl_FragColor = vec4(finalColor, 1.0);
}

