frag:
#ifdef GL_ES
precision mediump float;
#endif

uniform float u_time;
uniform vec2 u_resolution;

#define MAX_DIST 100.0
#define MIN_DIST 0.001
#define MAX_STEPS 100

// Repeat space using modular tiling
vec3 repeat(vec3 p, vec3 c) {
    return mod(p + 0.5 * c, c) - 0.5 * c;
}

// Signed Distance Function
float sceneSDF(vec3 p) {
    vec3 q = repeat(p, vec3(4.0, 2.0, 4.0));

    // Pillars
    float pillar = length(q.xz) - 0.3;
    float base = max(pillar, abs(q.y) - 0.0);

    // Floor
    float floor = p.y + 1.5;

    return min(base, floor);
}

// Normal calculation
vec3 getNormal(vec3 p) {
    float eps = 0.001;
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    return normalize(
        e.xyy * sceneSDF(p + e.xyy * eps) +
        e.yyx * sceneSDF(p + e.yyx * eps) +
        e.yxy * sceneSDF(p + e.yxy * eps) +
        e.xxx * sceneSDF(p + e.xxx * eps)
    );
}

// Raymarching loop
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if (d < MIN_DIST || t > MAX_DIST) break;
        t += d * 0.6;
    }
    return t;
}

// Lighting
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 0.02;
    for (int i = 0; i < 32; i++) {
        float h = sceneSDF(ro + rd * t);
        res = min(res, 8.0 * h / t);
        t += clamp(h, 0.01, 0.1);
    }
    return clamp(res, 0.0, 1.0);
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // Camera setup
    vec3 ro = vec3(0.0, 0.0, -u_time * 2.0);
    vec3 lookAt = ro + vec3(0.0, 0.0, 1.0);
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);

    float t = raymarch(ro, rd);
    vec3 col = vec3(0.0);

    if (t < MAX_DIST) {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p);
        vec3 lightPos = vec3(6.0 * sin(u_time), 5.0, -6.0 * cos(u_time));
        vec3 lightDir = normalize(lightPos - p);

        float diff = max(dot(normal, lightDir), 0.0);
        float shadow = softShadow(p + normal * 0.01, lightDir);

        vec3 lighting = vec3(0.1, 0.4, 0.8) * diff * shadow;
        col = lighting;

        // Add ambient and bounce
        col += vec3(0.05, 0.03, 0.06);
    }

    // Vignette
    float vignette = smoothstep(1.0, 0.2, length(uv));
    col *= vignette;

    gl_FragColor = vec4(col, 1.0);
}

