frag:
precision mediump float;

uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 80
#define MAX_DIST 30.0
#define SURF_DIST 0.001

// Warped crystal cave SDF
float sceneSDF(vec3 p) {
    // Fractal-like repetition
    for (int i = 0; i < 5; i++) {
        p = abs(p) - 0.5; // Crystal symmetry
        p.xy *= mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.5));
        p.xz *= mat2(cos(u_time * 0.1), sin(u_time * 0.1), -sin(u_time * 0.1), cos(u_time * 0.1));
    }

    float dist = length(p) - 1.0;

    // Add sine noise to create jagged crystalline look
    dist += 0.2 * sin(10.0 * p.x) * sin(10.0 * p.y) * sin(10.0 * p.z);
    return dist;
}

// Raymarching function
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);
        if (d < SURF_DIST || t > MAX_DIST) break;
        t += d;
    }
    return t;
}

// Approximate normal
vec3 getNormal(vec3 p) {
    float e = 0.001;
    vec2 h = vec2(e, 0);
    return normalize(vec3(
        sceneSDF(p + h.xyy) - sceneSDF(p - h.xyy),
        sceneSDF(p + h.yxy) - sceneSDF(p - h.yxy),
        sceneSDF(p + h.yyx) - sceneSDF(p - h.yyx)
    ));
}

// Iridescent color palette
vec3 iridescence(vec3 normal, vec3 viewDir, float time) {
    float angle = dot(normal, viewDir);
    float hue = 0.5 + 0.5 * cos(6.0 * angle + time);
    return 0.6 + 0.4 * cos(6.2831 * (vec3(0.5, 0.4, 0.2) + hue));
}

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution;
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    vec3 ro = vec3(0.0, 0.0, -5.0);
    vec3 rd = normalize(vec3(uv, 1.5));

    float t = raymarch(ro, rd);
    vec3 color = vec3(0.0);

    if (t < MAX_DIST) {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p);
        vec3 lightDir = normalize(vec3(2.0, 5.0, -3.0));

        float diff = max(dot(normal, lightDir), 0.0);
        vec3 viewDir = normalize(-rd);

        vec3 baseColor = iridescence(normal, viewDir, u_time);

        // Add glow/fog
        float fog = exp(-0.05 * t);
        color = baseColor * diff * fog + baseColor * 0.1;
    } else {
        // Background
        color = vec3(0.02, 0.02, 0.05);
    }

    gl_FragColor = vec4(color, 1.0);
}

