frag:
#version 300 es
//Stock Animation by Max Warren

precision highp float;
uniform float uTime;
uniform vec2 uResolution;
out vec4 fragColor;
float noise(vec2 p) {
    vec2 ip = floor(p);
    vec2 fp = fract(p);
    fp = fp * fp * (3.0 - 2.0 * fp);
    float n = mix(
        mix(sin(dot(ip, vec2(12.9898, 78.233))),
            sin(dot(ip + vec2(1.0, 0.0), vec2(12.9898, 78.233))), fp.x),
        mix(sin(dot(ip + vec2(0.0, 1.0), vec2(12.9898, 78.233))),
            sin(dot(ip + vec2(1.0, 1.0), vec2(12.9898, 78.233))), fp.x),
        fp.y);
    return 0.5 + 0.5 * n;
}
float grid(vec2 uv, float scale) {
    uv *= scale;
    vec2 grid = abs(fract(uv - 0.5) - 0.5) / fwidth(uv);
    return 1.0 - min(min(grid.x, grid.y), 1.0);
}
void main() {
    vec2 uv = gl_FragCoord.xy / uResolution.xy;
    uv.y = 1.0 - uv.y;
    vec2 warpedUV = vec2(uv.x, uv.y * (1.0 + uv.y * 1.5));
    float n1 = noise(vec2(warpedUV.x * 5.0 + uTime * 0.2, warpedUV.y * 10.0));
    float n2 = noise(vec2(warpedUV.x * 10.0 - uTime * 0.3, warpedUV.y * 20.0));
    float n3 = noise(vec2(warpedUV.x * 20.0 + uTime * 0.1, warpedUV.y * 40.0));
    float finalNoise = n1 * 0.5 + n2 * 0.3 + n3 * 0.2;
    float stockCurve = sin(warpedUV.x * 10.0 + uTime) * 0.2 +
                       sin(warpedUV.x * 5.0 - uTime * 0.5) * 0.3 +
                       finalNoise * 0.2;
    float lineY = 0.5 + stockCurve * (1.0 - uv.y);
    float dist = abs(uv.y - lineY);
    float glow = 0.01 / (dist + 0.01);
    vec3 baseColor = stockCurve > 0.0 ? vec3(0.2, 1.0, 0.6) : vec3(1.0, 0.3, 0.3);
    vec3 glowColor = baseColor * glow;
    float floor = grid(uv, 40.0) * 0.05;
    vec3 grcolor = vec3(0.0, 0.3, 0.5) * floor;
    float lighting = mix(0.3, 1.0, pow(1.0 - uv.y, 1.5));
    vec3 finalColor = (glowColor + grcolor) * lighting;
    fragColor = vec4(finalColor, 1.0);
}

