frag:
#ifdef GL_ES
precision mediump float;
#endif

uniform float u_time;
uniform vec2 u_resolution;
mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

// Fractal SDF – warping space into recursive madness
float map(vec3 p) {
    // Space warp
    p.xy *= rot(u_time * 0.0);
    p.yz *= rot(u_time * 0.0);

    float scale = 0.2;
    float d = 0.0;

    for (int i = 0; i < 5; i++) {
        p = abs(p) - 1.0;
        p = p * 2.0;
        p.xy *= rot(3.14 * 0.25);
        scale *= 2.0;
    }

    d = length(p) / scale - 0.5;
    return d;
}

// Normal from gradient
vec3 getNormal(vec3 p) {
    float eps = 0.001;
    vec2 h = vec2(eps, 0);
    return normalize(vec3(
        map(p + h.xyy) - map(p - h.xyy),
        map(p + h.yxy) - map(p - h.yxy),
        map(p + h.yyx) - map(p - h.yyx)
    ));
}

// Soft shadow
float softShadow(vec3 ro, vec3 rd) {
    float shadow = 1.0;
    float t = 0.02;
    for (int i = 0; i < 32; i++) {
        float h = map(ro + rd * t);
        if (h < 0.001) return 0.0;
        shadow = min(shadow, 10.0 * h / t);
        t += clamp(h, 0.01, 0.1);
    }
    return clamp(shadow, 0.0, 1.0);
}

// Raymarching
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 128; i++) {
        vec3 p = ro + t * rd;
        float d = map(p);
        if (d < 0.001) break;
        if (t > 100.0) break;
        t += d * 0.7;
    }
    return t;
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;

    // Camera
    vec3 ro = vec3(0.0, 0.0, -5.0 + sin(u_time * 0.3) * 2.0);
    vec3 target = vec3(0.0);
    vec3 forward = normalize(target - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);

    float t = raymarch(ro, rd);
    vec3 p = ro + rd * t;

    vec3 col = vec3(0.0);
    if (t < 100.0) {
        vec3 n = getNormal(p);
        vec3 lightPos = vec3(3.0 * sin(u_time), 4.0, 3.0 * cos(u_time));
        vec3 l = normalize(lightPos - p);
        float diff = max(dot(n, l), 0.0);
        float shadow = softShadow(p + n * 0.01, l);

        col = mix(vec3(0.2, 0.3, 0.4), vec3(0.8, 0.5, 1.0), diff) * shadow;

        // Add glow based on depth
        float glow = 1.0 / (0.01 + t * t * 0.04);
        col += glow * vec3(0.3, 0.5, 1.0);
    }

    // Add fog / atmosphere
    col = mix(col, vec3(0.0, 0.0, 0.0), smoothstep(20.0, 100.0, t));

    // Vignette
    col *= smoothstep(1.0, 0.2, length(uv));

    gl_FragColor = vec4(col, 1.0);
}

